import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Autosuggest from 'react-autosuggest';
import partners from './components/autosuggest/partners.json';
import speakers from './components/autosuggest/speakers.json';
import './FeedbackPage.css';

const FeedbackPage = () => {
  const navigate = useNavigate();
  const [partner, setPartner] = useState('');
  const [partnerSuggestions, setPartnerSuggestions] = useState([]);
  const [speakersList, setSpeakersList] = useState([{ fullName: '', id: 1 }]);
  const [positiveQualities, setPositiveQualities] = useState([]);
  const [negativeQualities, setNegativeQualities] = useState([]);
  const [usefulness, setUsefulness] = useState('');
  const [brightThoughts, setBrightThoughts] = useState('');
  const [additionalItems, setAdditionalItems] = useState([]);
  const [impression, setImpression] = useState('');
  const [currentQuestion, setCurrentQuestion] = useState(0); // Для отслеживания текущего вопроса
  const [isFinished, setIsFinished] = useState(false);

  // Функция для фильтрации предложений по введенному значению
  const getPartnerSuggestions = (value) => {
    const trimmedValue = value.trim().toLowerCase();
    if (trimmedValue.length < 2) {
      return [];
    }
    return partners.filter((partner) =>
      partner.toLowerCase().includes(trimmedValue)
    );
  };

  // Обработчик для поиска предложений
  const onSuggestionsFetchRequested = ({ value }) => {
    setPartnerSuggestions(getPartnerSuggestions(value));
  };

  // Очистка предложений
  const onSuggestionsClearRequested = () => {
    setPartnerSuggestions([]);
  };

  // Обработчик изменения значения ввода
  const onPartnerChange = (event, { newValue }) => {
    setPartner(newValue);
  };

  // Рендеринг предложений для автозаполнения
  const renderSuggestion = (suggestion) => {
    return <div>{suggestion}</div>;
  };

  const partnerInputProps = {
    placeholder: 'Введите название партнера',
    value: partner,
    onChange: onPartnerChange,
  };

  // Функция для добавления новых спикеров
  const handleAddSpeaker = () => {
    setSpeakersList([...speakersList, { fullName: '', id: speakersList.length + 1 }]);
  };

  // Функция для изменения фамилии спикера
  const handleSpeakerChange = (e, index) => {
    const updatedSpeakers = [...speakersList];
    updatedSpeakers[index].fullName = e.target.value;
    setSpeakersList(updatedSpeakers);
  };

  const questions = [
    {
      type: 'input',
      label: 'Название партнера',
      renderInput: () => (
        <Autosuggest
          suggestions={partnerSuggestions}
          onSuggestionsFetchRequested={onSuggestionsFetchRequested}
          onSuggestionsClearRequested={onSuggestionsClearRequested}
          getSuggestionValue={(suggestion) => suggestion}
          renderSuggestion={renderSuggestion}
          inputProps={partnerInputProps}
        />
      ),
    },
    {
      type: 'speakers',
      label: 'Фамилия и имя спикера',
      renderSpeakers: () => (
        <div>
          {speakersList.map((speaker, index) => (
            <div key={index}>
              <label>Фамилия и имя спикера {index + 1}</label>
              <Autosuggest
                suggestions={speakers}
                onSuggestionsFetchRequested={({ value }) => {
                  speaker.fullName = value;
                }}
                onSuggestionsClearRequested={() => setSpeakersList(speakersList)}
                getSuggestionValue={(suggestion) => suggestion}
                renderSuggestion={(suggestion) => <div>{suggestion}</div>}
                inputProps={{
                  value: speaker.fullName,
                  onChange: (e) => handleSpeakerChange(e, index),
                  placeholder: 'Введите фамилию и имя',
                }}
              />
              {index === speakersList.length - 1 && (
                <button onClick={handleAddSpeaker}>Добавить еще спикера</button>
              )}
            </div>
          ))}
        </div>
      ),
    },
    {
      type: 'qualities',
      label: 'Оценка качеств спикера',
      renderQualities: () => (
        <div>
          {['Экспертный', 'Энергичный', 'Мотивирующий', 'Харизматичный', 'Доступно доносит информацию'].map((quality, index) => (
            <div key={index}>
              <button
                onClick={() => setPositiveQualities([...positiveQualities, quality])}
                className={positiveQualities.includes(quality) ? 'green' : ''}
              >
                {quality}
              </button>
              <button
                onClick={() => setNegativeQualities([...negativeQualities, quality])}
                className={negativeQualities.includes(quality) ? 'red' : ''}
              >
                {quality}
              </button>
            </div>
          ))}
        </div>
      ),
    },
    {
      type: 'buttons',
      label: 'Фактическая ценность информации',
      options: ['Полезно в работе (буду применять на практике)', 'Бесполезно в работе (с моими клиентами это не работает)'],
      selected: usefulness,
      onChange: setUsefulness,
      renderButtons: () => (
        <div>
          {['Полезно в работе (буду применять на практике)', 'Бесполезно в работе (с моими клиентами это не работает)'].map((option) => (
            <button
              onClick={() => setUsefulness(option)}
              className={usefulness === option ? 'green' : ''}
            >
              {option}
            </button>
          ))}
        </div>
      ),
    },
    {
      type: 'textarea',
      label: 'Самые яркие мысли с мероприятия',
      value: brightThoughts,
      onChange: (e) => setBrightThoughts(e.target.value),
      renderTextarea: () => (
        <div>
          <label>{'Самые яркие мысли с мероприятия'}</label>
          <textarea
            value={brightThoughts}
            onChange={(e) => setBrightThoughts(e.target.value)}
            required
            minLength={5}
          />
        </div>
      ),
    },
    {
      type: 'buttons',
      label: 'Что стоило бы добавить в тренинг?',
      options: ['Практики', 'Клиентских историй', 'Статистических данных', 'Фишек продаж', 'Перерывов', 'Разбор продуктовой линейки', 'Детальный разбор параметров продукта', 'Ящик Prosecco'],
      selected: additionalItems,
      onChange: setAdditionalItems,
      renderButtons: () => (
        <div>
          {['Практики', 'Клиентских историй', 'Статистических данных', 'Фишек продаж', 'Перерывов', 'Разбор продуктовой линейки', 'Детальный разбор параметров продукта', 'Ящик Prosecco'].map((option) => (
            <button
              key={option}
              onClick={() => setAdditionalItems([...additionalItems, option])}
              className={additionalItems.includes(option) ? 'selected' : ''}
            >
              {option}
            </button>
          ))}
        </div>
      ),
    },
    {
      type: 'smile',
      label: 'Общее впечатление от тренинга',
      options: ['Грустный', 'Нейтральный', 'Счастливый'],
      selected: impression,
      onChange: setImpression,
      renderSmiles: () => (
        <div>
          {['Грустный', 'Нейтральный', 'Счастливый'].map((option) => (
            <button
              key={option}
              onClick={() => setImpression(option)}
            >
              {option}
            </button>
          ))}
        </div>
      ),
    },
  ];

  // Функция для перехода к следующему вопросу
  const handleNext = () => {
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      handleFinish();
    }
  };

  // Функция для завершения опроса и отправки данных
  const handleFinish = async () => {
    const data = {
      partner,
      dateTime: new Date().toISOString(),
      speakers: speakersList.map((speaker) => speaker.fullName).join(', '),
      positiveQualities,
      negativeQualities,
      usefulness,
      brightThoughts,
      additionalItems,
      impression,
    };

    try {
      const response = await fetch("https://yourserver/api/save", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      });

      if (response.ok) {
        setIsFinished(true);
        alert('Спасибо за участие в опросе!');
      } else {
        alert('Ошибка при сохранении данных');
      }
    } catch (error) {
      console.error('Ошибка:', error);
      alert('Ошибка соединения с сервером');
    }
  };

  const currentQuestionData = questions[currentQuestion];

  return (
    <div className="feedback-container">
      <h1>Оценка мероприятия</h1>

      {/* Вопросы */}
      {currentQuestionData.type === 'input' && currentQuestionData.renderInput()}
      {currentQuestionData.type === 'speakers' && currentQuestionData.renderSpeakers()}
      {currentQuestionData.type === 'qualities' && currentQuestionData.renderQualities()}
      {currentQuestionData.type === 'buttons' && currentQuestionData.renderButtons()}
      {currentQuestionData.type === 'textarea' && currentQuestionData.renderTextarea()}
      {currentQuestionData.type === 'smile' && currentQuestionData.renderSmiles()}

      {/* Кнопка "Далее" или "Завершить опрос" */}
      <button onClick={handleNext} disabled={isFinished}>
        {currentQuestion === questions.length - 1 ? 'Завершить опрос' : 'Далее'}
      </button>
    </div>
  );
};

export default FeedbackPage;
