import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import backgroundImage from './components/background.png';  // Импорт изображения
import './ConfirmCodePage.css';

const ConfirmCodePage = () => {
  const [code, setCode] = useState('');
  const [status, setStatus] = useState('');
  const navigate = useNavigate();
  const location = useLocation();
  const email = location.state?.email || '';

  const handleCodeChange = (e) => {
    setCode(e.target.value);
  };

  const handleVerifyCode = async () => {
    setStatus('Проверяем код...');

    try {
      const response = await fetch('http://127.0.0.1:5000/verify_code', {
        method: 'POST',
        body: JSON.stringify({ email, code }),
        headers: { 'Content-Type': 'application/json' },
      });

      const result = await response.json();

      if (result.success) {
        setStatus('✅ Регистрация успешна! Добро пожаловать!');
        setTimeout(() => {
          navigate('/polls'); // Переход на страницу с опросами после успешной регистрации
        }, 2000);
      } else {
        setStatus('❌ Неверный код, попробуйте снова.');
      }
    } catch (error) {
      setStatus('Ошибка проверки кода. Попробуйте позже.');
      console.error(error);
    }
  };

  return (
    <div className="confirm-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
    <div className="logo"/>
      <h2>Введите код, отправленный на указанную почту</h2>
      <input
        type="text"
        value={code}
        onChange={handleCodeChange}
        placeholder="Введите код"
        className="input-field"
      />
      <button className="btn red-btn" onClick={handleVerifyCode}>Проверить код</button>
      <p>{status}</p>
    </div>
  );
};

export default ConfirmCodePage;
