// src/SNPPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import mainmenuIcon    from './components/icons/mainmenu.png';
import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';

import './PollsPage.css';  // шапка, кнопки, анимации
import './SNPPage.css';    // ваши стили

const SNPPage = () => {
  const navigate = useNavigate();

  // === состояния ===
  const [step, setStep]             = useState(1);
  const [email, setEmail]           = useState('');
  const [emailError, setEmailError] = useState('');
  const [birthDate, setBirthDate]   = useState(new Date());
  const [gender, setGender]         = useState(null); // 'male' или 'female'
  const [calcType, setCalcType]     = useState(null); // 'premium' или 'sum'
  const [amount, setAmount]         = useState('');
  const [amountError, setAmountError] = useState('');

  // формат суммы 123456 → "123.456"
  const formatSum = raw => {
    let d = raw.replace(/\D/g, '');
    if (!d) return '';
    let o = '';
    while (d.length > 3) {
      o = '.' + d.slice(-3) + o;
      d = d.slice(0, -3);
    }
    return d + o;
  };

  // подсчёт возраста
  const getAge = date => {
    if (!(date instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - date.getFullYear();
    if (
      today.getMonth() < date.getMonth() ||
      (today.getMonth() === date.getMonth() && today.getDate() < date.getDate())
    ) age--;
    return age;
  };

  // проверка суммы
  const isAmountValid = () => {
    const n = parseInt(amount.replace(/\./g, ''), 10) || 0;
    return (calcType === 'premium' && n >= 60000)
        || (calcType === 'sum'     && n >= 450000);
  };

  // можно ли перейти дальше?
  const canGoNext = () =>
    birthDate instanceof Date &&
    getAge(birthDate) >= 18 &&
    Boolean(gender) &&
    Boolean(calcType) &&
    Boolean(amount) &&
    isAmountValid();

  // Обработчик первого шага — валидация почты
  const handleEmailNext = () => {
    if (!email.toLowerCase().endsWith('@vtb.ru')) {
      setEmailError('Используйте корпоративную почту домена @vtb.ru');
      return;
    }
    setEmailError('');
    setStep(2);
  };

  // Обработчик ввода суммы
  const onAmountChange = e => {
    const f = formatSum(e.target.value);
    setAmount(f);
    const n = parseInt(f.replace(/\./g, ''), 10) || 0;
    if (calcType === 'premium' && n < 60000) {
      setAmountError('Минимум 60 000 ₽');
    } else if (calcType === 'sum' && n < 450000) {
      setAmountError('Минимум 450 000 ₽');
    } else {
      setAmountError('');
    }
  };

  // Обработчик «Далее»
  const handleNext = () => {
    // Ваш POST-запрос здесь...
    console.log('Запускаем расчёт', { birthDate, gender, calcType, amount });
  };

  const goHome = () => navigate('/MainMenu');

  return (
    <div
      className="snp-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* HEADER */}
      <div className="polls-header">
        <button className="back-btn" onClick={goHome}>
          <img src={mainmenuIcon} alt="Домой"/>
        </button>
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип"/>
        </div>
        {/* Кнопка «Далее» справа от логотипа только на шаге 2 */}
        {step === 2 && (
          <button
            className="forward-btn"
            onClick={handleNext}
            disabled={!canGoNext()}
          >
            <span className="arrow-icon">→</span>
          </button>
        )}
      </div>

      {/* === Шаг 1: ввод корпоративной почты === */}
      {step === 1 && (
        <>
          <div className="email-step">
            <label className="email-label">Корпоративная почта ВТБ:</label>
            <input
              type="email"
              placeholder="example@vtb.ru"
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
          </div>
          {emailError && <div className="input-error">{emailError}</div>}
          <div className="polls-buttons">
            <button
              className="polls-btn animate-btn btn1"
              onClick={handleEmailNext}
            >
              Продолжить
            </button>
          </div>
        </>
      )}

      {/* === Шаг 2: всё остальное === */}
      {step === 2 && (
        <>
          <h1 className="polls-title animate-title">
            Расчёт по программе «Стратегия на пять. Гарант»
          </h1>

          {/* Дата рождения */}
          <div className="date-section animate-btn btn1">
            <label className="section-label">Дата рождения:</label>
            <DateWheelPicker
              value={birthDate}
              onChange={val => {
                if (val instanceof Date) setBirthDate(val);
                else if (val && val.day) {
                  setBirthDate(new Date(val.year, val.month - 1, val.day));
                }
              }}
            />
          </div>

          {/* Пол */}
          <div className="gender-section animate-btn btn2">
            <button
              className={`insurance-btn ${gender==='male'?'selected':''}`}
              onClick={()=>setGender('male')}
            >
              <div>Мужской</div>
              <div className="emoji">👨</div>
            </button>
            <button
              className={`insurance-btn ${gender==='female'?'selected':''}`}
              onClick={()=>setGender('female')}
            >
              <div>Женский</div>
              <div className="emoji">👩</div>
            </button>
          </div>

          {/* Тип расчёта */}
          <div className="insurance-options animate-btn btn3">
            <button
              className={`insurance-btn ${calcType==='premium'?'selected':''}`}
              onClick={()=>{
                setCalcType('premium');
                setAmount('');
                setAmountError('');
              }}
            >
              <div>Страхового</div>
              <div>взноса</div>
            </button>
            <button
              className={`insurance-btn ${calcType==='sum'?'selected':''}`}
              onClick={()=>{
                setCalcType('sum');
                setAmount('');
                setAmountError('');
              }}
            >
              <div>Страховой</div>
              <div>суммы</div>
            </button>
          </div>

          {/* Сумма */}
          {calcType && (
            <div className="sum-section animate-btn btn4">
              <label className="section-label">Сумма (₽):</label>
              <input
                type="text"
                placeholder={
                  calcType==='premium'
                    ? 'Минимум 60 000'
                    : 'Минимум 450 000'
                }
                value={amount}
                onChange={onAmountChange}
              />
              {amountError && <div className="input-error">{amountError}</div>}
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default SNPPage;
