// src/SNPPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import mainmenuIcon    from './components/icons/mainmenu.png';
import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';

import './PollsPage.css';  // —à–∞–ø–∫–∞, –∫–Ω–æ–ø–∫–∏, –∞–Ω–∏–º–∞—Ü–∏–∏
import './SNPPage.css';    // –≤–∞—à–∏ —Å—Ç–∏–ª–∏

const SNPPage = () => {
  const navigate = useNavigate();

  // === —Å–æ—Å—Ç–æ—è–Ω–∏—è ===
  const [step, setStep]             = useState(1);
  const [email, setEmail]           = useState('');
  const [emailError, setEmailError] = useState('');
  const [birthDate, setBirthDate]   = useState(new Date());
  const [gender, setGender]         = useState(null); // 'male' –∏–ª–∏ 'female'
  const [calcType, setCalcType]     = useState(null); // 'premium' –∏–ª–∏ 'sum'
  const [amount, setAmount]         = useState('');
  const [amountError, setAmountError] = useState('');

  // —Ñ–æ—Ä–º–∞—Ç —Å—É–º–º—ã 123456 ‚Üí "123.456"
  const formatSum = raw => {
    let d = raw.replace(/\D/g, '');
    if (!d) return '';
    let o = '';
    while (d.length > 3) {
      o = '.' + d.slice(-3) + o;
      d = d.slice(0, -3);
    }
    return d + o;
  };

  // –ø–æ–¥—Å—á—ë—Ç –≤–æ–∑—Ä–∞—Å—Ç–∞
  const getAge = date => {
    if (!(date instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - date.getFullYear();
    if (
      today.getMonth() < date.getMonth() ||
      (today.getMonth() === date.getMonth() && today.getDate() < date.getDate())
    ) age--;
    return age;
  };

  // –ø—Ä–æ–≤–µ—Ä–∫–∞ —Å—É–º–º—ã
  const isAmountValid = () => {
    const n = parseInt(amount.replace(/\./g, ''), 10) || 0;
    return (calcType === 'premium' && n >= 60000)
        || (calcType === 'sum'     && n >= 450000);
  };

  // –º–æ–∂–Ω–æ –ª–∏ –ø–µ—Ä–µ–π—Ç–∏ –¥–∞–ª—å—à–µ?
  const canGoNext = () =>
    birthDate instanceof Date &&
    getAge(birthDate) >= 18 &&
    Boolean(gender) &&
    Boolean(calcType) &&
    Boolean(amount) &&
    isAmountValid();

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –ø–µ—Ä–≤–æ–≥–æ —à–∞–≥–∞ ‚Äî –≤–∞–ª–∏–¥–∞—Ü–∏—è –ø–æ—á—Ç—ã
  const handleEmailNext = () => {
    if (!email.toLowerCase().endsWith('@vtb.ru')) {
      setEmailError('–ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ –∫–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω—É—é –ø–æ—á—Ç—É –¥–æ–º–µ–Ω–∞ @vtb.ru');
      return;
    }
    setEmailError('');
    setStep(2);
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –≤–≤–æ–¥–∞ —Å—É–º–º—ã
  const onAmountChange = e => {
    const f = formatSum(e.target.value);
    setAmount(f);
    const n = parseInt(f.replace(/\./g, ''), 10) || 0;
    if (calcType === 'premium' && n < 60000) {
      setAmountError('–ú–∏–Ω–∏–º—É–º 60 000 ‚ÇΩ');
    } else if (calcType === 'sum' && n < 450000) {
      setAmountError('–ú–∏–Ω–∏–º—É–º 450 000 ‚ÇΩ');
    } else {
      setAmountError('');
    }
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ ¬´–î–∞–ª–µ–µ¬ª
  const handleNext = () => {
    // –í–∞—à POST-–∑–∞–ø—Ä–æ—Å –∑–¥–µ—Å—å...
    console.log('–ó–∞–ø—É—Å–∫–∞–µ–º —Ä–∞—Å—á—ë—Ç', { birthDate, gender, calcType, amount });
  };

  const goHome = () => navigate('/MainMenu');

  return (
    <div
      className="snp-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* HEADER */}
      <div className="polls-header">
        <button className="back-btn" onClick={goHome}>
          <img src={mainmenuIcon} alt="–î–æ–º–æ–π"/>
        </button>
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="–õ–æ–≥–æ—Ç–∏–ø"/>
        </div>
        {/* –ö–Ω–æ–ø–∫–∞ ¬´–î–∞–ª–µ–µ¬ª —Å–ø—Ä–∞–≤–∞ –æ—Ç –ª–æ–≥–æ—Ç–∏–ø–∞ —Ç–æ–ª—å–∫–æ –Ω–∞ —à–∞–≥–µ 2 */}
        {step === 2 && (
          <button
            className="forward-btn"
            onClick={handleNext}
            disabled={!canGoNext()}
          >
            <span className="arrow-icon">‚Üí</span>
          </button>
        )}
      </div>

      {/* === –®–∞–≥ 1: –≤–≤–æ–¥ –∫–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω–æ–π –ø–æ—á—Ç—ã === */}
      {step === 1 && (
        <>
          <div className="email-step">
            <label className="email-label">–ö–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω–∞—è –ø–æ—á—Ç–∞ –í–¢–ë:</label>
            <input
              type="email"
              placeholder="example@vtb.ru"
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
          </div>
          {emailError && <div className="input-error">{emailError}</div>}
          <div className="polls-buttons">
            <button
              className="polls-btn animate-btn btn1"
              onClick={handleEmailNext}
            >
              –ü—Ä–æ–¥–æ–ª–∂–∏—Ç—å
            </button>
          </div>
        </>
      )}

      {/* === –®–∞–≥ 2: –≤—Å—ë –æ—Å—Ç–∞–ª—å–Ω–æ–µ === */}
      {step === 2 && (
        <>
          <h1 className="polls-title animate-title">
            –†–∞—Å—á—ë—Ç –ø–æ –ø—Ä–æ–≥—Ä–∞–º–º–µ ¬´–°—Ç—Ä–∞—Ç–µ–≥–∏—è –Ω–∞ –ø—è—Ç—å. –ì–∞—Ä–∞–Ω—Ç¬ª
          </h1>

          {/* –î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è */}
          <div className="date-section animate-btn btn1">
            <label className="section-label">–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è:</label>
            <DateWheelPicker
              value={birthDate}
              onChange={val => {
                if (val instanceof Date) setBirthDate(val);
                else if (val && val.day) {
                  setBirthDate(new Date(val.year, val.month - 1, val.day));
                }
              }}
            />
          </div>

          {/* –ü–æ–ª */}
          <div className="gender-section animate-btn btn2">
            <button
              className={`insurance-btn ${gender==='male'?'selected':''}`}
              onClick={()=>setGender('male')}
            >
              <div>–ú—É–∂—Å–∫–æ–π</div>
              <div className="emoji">üë®</div>
            </button>
            <button
              className={`insurance-btn ${gender==='female'?'selected':''}`}
