// CareFuturePage.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import mainmenuIcon    from './components/icons/mainmenu.png';
import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';
import './CareFuturePage.css';

export default function CareFuturePage() {
  const navigate = useNavigate();

  // === —Å–æ—Å—Ç–æ—è–Ω–∏—è ===
  const [step,           setStep]           = useState('email'); // email ‚Üí form ‚Üí processing ‚Üí result ‚Üí manager
  const [email,          setEmail]          = useState('');
  const [emailError,     setEmailError]     = useState('');
  const [birthDateParts, setBirthDateParts] = useState({ day:null, month:null, year:null });
  const [birthDate,      setBirthDate]      = useState(null);
  const [gender,         setGender]         = useState(null);
  const [programTerm,    setProgramTerm]    = useState(5);       // —Å—Ä–æ–∫ –ø—Ä–æ–≥—Ä–∞–º–º—ã –≤ –≥–æ–¥–∞—Ö
  const [termError,      setTermError]      = useState('');
  const [calcType,       setCalcType]       = useState(null);
  const [amount,         setAmount]         = useState('');
  const [amountError,    setAmountError]    = useState('');
  const [isProcessing,   setIsProcessing]   = useState(false);
  const [resultData,     setResultData]     = useState(null);
  const [mgrSurname,     setMgrSurname]     = useState('');
  const [mgrName,        setMgrName]        = useState('');
  const [mgrCity,        setMgrCity]        = useState('');
  const [mgrError,       setMgrError]       = useState('');
  const [slideIndex,     setSlideIndex]     = useState(0);

  // === –∫–æ–Ω—Å—Ç–∞–Ω—Ç—ã ===
  const periodicity = '–µ–∂–µ–≥–æ–¥–Ω–æ';
  const minAmount   = 100000; // –Ω–æ–≤–∞—è –º–∏–Ω–∏–º–∞–ª–∫–∞

  // === —ç—Ñ—Ñ–µ–∫—Ç: —Å–æ–±–∏—Ä–∞–µ–º Date –∏–∑ birthDateParts ===
  useEffect(() => {
    const { day, month, year } = birthDateParts;
    if (day && month && year) {
      const d = Number(day), m = Number(month), y = Number(year);
      const dt = new Date(y, m - 1, d);
      if (!isNaN(dt.getTime())
          && dt.getDate()     === d
          && dt.getMonth()+1  === m
          && dt.getFullYear() === y) {
        setBirthDate(dt);
      } else {
        setBirthDate(null);
      }
    } else {
      setBirthDate(null);
    }
  }, [birthDateParts]);

  // === —É—Ç–∏–ª–∏—Ç—ã ===
  const formatSum = raw => {
    let digits = raw.replace(/\D/g,'');
    if (!digits) return '';
    let out = '';
    while (digits.length > 3) {
      out = '.' + digits.slice(-3) + out;
      digits = digits.slice(0, -3);
    }
    return digits + out;
  };

  const getAge = date => {
    if (!(date instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - date.getFullYear();
    const m = today.getMonth() - date.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < date.getDate())) age--;
    return age;
  };

  // === –≤–∞–ª–∏–¥–∞—Ü–∏–∏ ===
  const handleEmailNext = () => {
    if (!email.endsWith('@vtb.ru')) {
      setEmailError('–î–æ–º–µ–Ω –≤–∞—à–µ–π –ø–æ—á—Ç—ã –Ω–µ —è–≤–ª—è–µ—Ç—Å—è –∫–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω—ã–º. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â—ë —Ä–∞–∑.');
    } else {
      setEmailError('');
      setStep('form');
    }
  };

  const handleAmountChange = e => {
    const fmt = formatSum(e.target.value);
    setAmount(fmt);
    const num = parseInt(fmt.replace(/\./g,''),10) || 0;
    setAmountError(num < minAmount
      ? `–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –æ—Ç ${minAmount.toLocaleString('ru-RU')} ‚ÇΩ`
      : '');
  };

  // –ø—Ä–æ–≤–µ—Ä—è–µ–º —É—Å–ª–æ–≤–∏—è –ø–µ—Ä–µ–¥ –∫–Ω–æ–ø–∫–æ–π "–î–∞–ª–µ–µ"
  const canGoNext = () => {
    if (!(birthDate instanceof Date) || !gender || !calcType || !amount || amountError) return false;
    const age = getAge(birthDate);
    if (age < 18) return false;           // –º–∏–Ω–∏–º—É–º 18 –ø–æ–ª–Ω—ã—Ö –ª–µ—Ç
    const endAge = age + programTerm;
    if (endAge > 70) return false;        // –Ω–µ —Å—Ç–∞—Ä—à–µ 70 –ø–æ–ª–Ω—ã—Ö –Ω–∞ –∫–æ–Ω–µ—Ü
    return true;
  };

  // –≤–∞–ª–∏–¥–∞—Ü–∏—è —Å—Ä–æ–∫–∞ –ø—Ä–æ–≥—Ä–∞–º–º—ã
  useEffect(() => {
    if (birthDate) {
      const age = getAge(birthDate);
      const endAge = age + programTerm;
      if (age >= 18 && endAge > 70) {
        if (programTerm === 5) {
          setTermError('–ö —Å–æ–∂–∞–ª–µ–Ω–∏—é, –¥–∞–Ω–Ω–∞—è –ø—Ä–æ–≥—Ä–∞–º–º–∞ –í–∞–º –Ω–µ –ø–æ–¥–æ–π–¥—ë—Ç');
        } else {
          setTermError('–í–æ–∑—Ä–∞—Å—Ç –Ω–∞ –¥–∞—Ç—É –æ–∫–æ–Ω—á–∞–Ω–∏—è –Ω–µ –¥–æ–ª–∂–µ–Ω –ø—Ä–µ–≤—ã—à–∞—Ç—å 70 –ª–µ—Ç. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ —É–º–µ–Ω—å—à–∏—Ç—å —Å—Ä–æ–∫.');
        }
      } else {
        setTermError('');
      }
    }
  }, [programTerm, birthDate]);

  // === –ø–µ—Ä–µ—Ö–æ–¥ –∫ —Ä–∞—Å—á—ë—Ç—É ===
  const handleNext = async () => {
    if (!canGoNext()) return;
    const dateRu = birthDate.toLocaleDateString('ru-RU',{day:'2-digit',month:'2-digit',year:'numeric'});
    const payload = {
      birthdate: dateRu,
      age:        getAge(birthDate),
      gender,
      term:       programTerm,
      payment:    calcType === 'premium' ? '–°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞' : '–°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã',
      sum:        parseInt(amount.replace(/\./g,''),10)
    };
    setIsProcessing(true);
    try {
      const res = await fetch('/api/carefuture/save', {
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body:JSON.stringify(payload)
      });
      const data = await res.json();
      if (res.ok) {
        setResultData(data);
        setSlideIndex(0);
        setStep('result');
      }
    } catch(err) {
      console.error(err);
    }
    setIsProcessing(false);
  };

  // === —Å–ª–∞–π–¥—ã —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞ ===
  const slides = resultData ? [
    <div key={0} className="detail-slide">
      <div className="slide-header">–í –¢–ï–ß–ï–ù–ò–ï {programTerm} –õ–ï–¢</div>
      <div className="slide-title">–í—ã –≤–Ω–æ—Å–∏—Ç–µ —Å—Ä–µ–¥—Å—Ç–≤–∞</div>
      <div className="slide-amount">{formatSum(String(resultData.contribution))} ‚ÇΩ</div>
      <div className="slide-period">{periodicity}</div>
      <div className="slide-subtitle">–°—É–º–º–∞ –≤–∞—à–∏—Ö –≤–∑–Ω–æ—Å–æ–≤</div>
      <div className="slide-amount">{formatSum(String(resultData.totalContribution))} ‚ÇΩ</div>
    </div>,
    <div key={1} className="detail-slide">
      <div className="slide-header">–ù–ê–ß–ò–°–õ–Ø–ï–ú –î–û–ü–û–õ–ù–ò–¢–ï–õ–¨–ù–û</div>
      <div className="slide-title">{`${resultData.accrualPercent}% –æ—Ç –∫–∞–∂–¥–æ–≥–æ –≤–∑–Ω–æ—Å–∞`}</div>
      <div className="slide-amount">{formatSum(String(resultData.accrualAmount))} ‚ÇΩ</div>
      <div className="slide-period">{periodicity}</div>
      <div className="slide-subtitle">–û–±—â–∞—è —Å—É–º–º–∞ –Ω–∞—á–∏—Å–ª–µ–Ω–∏–π</div>
      <div className="slide-amount">{formatSum(String(resultData.totalAccrual))} ‚ÇΩ</div>
    </div>,
    <div key={2} className="detail-slide">
      <div className="slide-header">–í–´ –ü–û–õ–£–ß–ê–ï–¢–ï:</div>
      <div className="slide-icon">üí∞</div>
      <div className="slide-amount-big">{formatSum(String(resultData.finalAnnual))} ‚ÇΩ</div>
      <div className="slide-period">–ï–∂–µ–≥–æ–¥–Ω—ã–µ –Ω–∞–∫–æ–ø–ª–µ–Ω–∏—è</div>
      <div className="slide-amount">{formatSum(String(resultData.finalTotal))} ‚ÇΩ</div>
      <div className="slide-subtitle">–í–∞—à –∫–∞–ø–∏—Ç–∞–ª —á–µ—Ä–µ–∑ {programTerm} –ª–µ—Ç</div>
    </div>
  ] : [];

  // === –æ—Ç–ø—Ä–∞–≤–∫–∞ –º–µ–Ω–µ–¥–∂–µ—Ä—É ===
  const handleManagerSubmit = async () => {
    if (!mgrSurname||!mgrName||!mgrCity) {
      setMgrError('–ó–∞–ø–æ–ª–Ω–∏—Ç–µ –≤—Å–µ –ø–æ–ª—è');
      return;
    }
    const payload = {
      surname:      mgrSurname.trim(),
      name:         mgrName.trim(),
      city:         mgrCity.trim(),
      email,
      gender,
      birthdate:    birthDate.toLocaleDateString('ru-RU',{day:'2-digit',month:'2-digit',year:'numeric'}),
      age:          getAge(birthDate),
      term:         programTerm,
      contribution: resultData.contribution
    };
    try {
      const res = await fetch('/api/carefuture/send_manager',{
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body:JSON.stringify(payload)
      });
      if (res.ok) navigate('/main-menu');
      else       setMgrError('–û—à–∏–±–∫–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏');
    } catch {
      setMgrError('–°–µ—Ç–µ–≤–∞—è –æ—à–∏–±–∫–∞');
    }
  };

  return (
    <div className="snp-container" style={{ backgroundImage:`url(${backgroundImage})` }}>

      {/* –∫–Ω–æ–ø–∫–∞ –î–æ–º–æ–π */}
      <div className="back-btn-container">
        <button className="back-btn" onClick={()=>navigate('/main-menu')}>
          <img src={mainmenuIcon} alt="–î–æ–º–æ–π"/>
        </button>
      </div>
      {/* –ª–æ–≥–æ—Ç–∏–ø */}
      <div className="logo-container">
        <img src={logoImage} alt="–õ–æ–≥–æ—Ç–∏–ø" className="logo-image"/>
      </div>
      {/* –∑–∞–≥–æ–ª–æ–≤–æ–∫ */}
      <h1 className="snp-title">–ó–∞–±–æ—Ç–∞ –æ –±—É–¥—É—â–µ–º</h1>

      {/* –®–∞–≥ 1 ‚Äî EMAIL */}
      {step==='email' && (
        <div className="snp-form">
          <div className="form-group">
            <label className="form-label">–ö–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω–∞—è –ø–æ—á—Ç–∞ –í–¢–ë:</label>
            <input type="email" className="insurance-sum-input"
                   value={email} onChange={e=>setEmail(e.target.value)}
                   placeholder="name@vtb.ru"/>
          </div>
          <button className="insurance-btn selected" onClick={handleEmailNext}>
            –ü–µ—Ä–µ–π—Ç–∏ –∫ —Ä–∞—Å—á—ë—Ç—É
          </button>
          {emailError && <div className="input-error-message">{emailError}</div>}
        </div>
      )}

      {/* –®–∞–≥ 2 ‚Äî –§–û–†–ú–ê */}
      {step==='form' && !isProcessing && !resultData && (
        <>
          <div className="snp-form">
            <div className="form-group">
              <label className="form-label">–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è:</label>
              <DateWheelPicker value={birthDateParts}
                onChange={val=>{
                  if (val instanceof Date) {
                    setBirthDateParts({
                      day: String(val.getDate()).padStart(2,'0'),
                      month: String(val.getMonth()+1).padStart(2,'0'),
                      year: String(val.getFullYear())
                    });
                  } else if (typeof val==='object') {
                    setBirthDateParts(prev=>({
                      ...prev,
                      ...(val.day   != null ? {day:String(val.day).padStart(2,'0')}   :{}),
                      ...(val.month != null ? {month:String(val.month).padStart(2,'0')}:{}),
                      ...(val.year  != null ? {year:String(val.year)}                  :{})
                    }));
                  } else if (typeof val==='string') {
                    const parts = val.includes('.') ? val.split('.') :
                                  val.includes('-') ? val.split('-').reverse() : [];
                    if (parts.length===3) {
                      setBirthDateParts({
                        day:   parts[0].padStart(2,'0'),
                        month: parts[1].padStart(2,'0'),
                        year:  parts[2]
                      });
                    }
                  }
                }}/>
            </div>

            <div className="form-group">
              <label className="form-label">–°—Ä–æ–∫ –ø—Ä–æ–≥—Ä–∞–º–º—ã (–ª–µ—Ç):</label>
              <select className="insurance-sum-input"
                      value={programTerm}
                      onChange={e=>setProgramTerm(Number(e.target.value))}>
                {Array.from({length:16},(_,i)=>5+i).map(v=>(
                  <option key={v} value={v}>{v}</option>
                ))}
              </select>
              {termError && <div className="input-error-message">{termError}</div>}
            </div>

            <div className="form-group">
              <label className="form-label">–ü–æ–ª:</label>
              <div className="gender-selector">
                {['–º—É–∂—Å–∫–æ–π','–∂–µ–Ω—Å–∫–∏–π'].map(v=>(
                  <div key={v}
                    className={`gender-option ${gender===v?'selected':''}`}
                    onClick={()=>setGender(v)}>
                    <div className="gender-label">{v==='–º—É–∂—Å–∫–æ–π'?'–ú—É–∂—Å–∫–æ–π':'–ñ–µ–Ω—Å–∫–∏–π'}</div>
                    <div className="gender-icon">{v==='–º—É–∂—Å–∫–æ–π'?'üë®':'üë©'}</div>
                  </div>
                ))}
              </div>
            </div>

            <div className="form-group">
              <label className="form-label">–†–∞—Å—á—ë—Ç –æ—Ç‚Ä¶</label>
              <div className="insurance-options">
                <button className={`insurance-btn ${calcType==='premium'?'selected':''}`}
                        onClick={()=>{ setCalcType('premium'); setAmount(''); setAmountError(''); }}>
                  –°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞
                </button>
                <button className={`insurance-btn ${calcType==='sum'?'selected':''}`}
                        onClick={()=>{ setCalcType('sum'); setAmount(''); setAmountError(''); }}>
                  –°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã
                </button>
              </div>
            </div>

            {calcType && (
              <div className="form-group">
                <label className="form-label">–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –≤ —Ä—É–±–ª—è—Ö</label>
                <input type="text" className="insurance-sum-input"
                       value={amount}
                       onChange={handleAmountChange}
                       placeholder={`–ú–∏–Ω–∏–º—É–º ${minAmount.toLocaleString('ru-RU')}`}/>
                {amountError && <div className="input-error-message">{amountError}</div>}
              </div>
            )}
          </div>

          <div className="top-btn-container">
            <button className="top-btn"
                    onClick={handleNext}
                    disabled={!canGoNext()}>
              <svg viewBox="0 0 24 24"><path d="M12 4l8 8-8 8"/></svg>
            </button>
          </div>
        </>
      )}

      {/* Processing */}
      {isProcessing && (
        <div className="processing-container">
          <p>–í—ã–ø–æ–ª–Ω—è–µ—Ç—Å—è —Ä–∞—Å—á—ë—Ç...</p>
          <div className="arrow-animation">‚Üí</div>
        </div>
      )}

      {/* –†–µ–∑—É–ª—å—Ç–∞—Ç + slider + –º–µ–Ω–µ–¥–∂–µ—Ä */}
      {step==='result' && resultData && !isProcessing && (
        <>
          <div className="result-container">
            <h2>–†–µ–∑—É–ª—å—Ç–∞—Ç—ã —Ä–∞—Å—á—ë—Ç–∞</h2>
            <p><strong>–í–æ–∑—Ä–∞—Å—Ç:</strong> {resultData.age} –ª–µ—Ç</p>
            <p><strong>–ü–æ–ª:</strong> {resultData.gender}</p>
            <p><strong>–°—Ä–æ–∫:</strong> {resultData.term} –ª–µ—Ç</p>
            <p><strong>–°—É–º–º–∞:</strong> {formatSum(String(resultData.sum))} ‚ÇΩ</p>
            <p><strong>–ò—Ç–æ–≥–æ–≤–∞—è –ø—Ä–µ–º–∏—è:</strong> {formatSum(String(resultData.totalPremium||0))} ‚ÇΩ</p>
          </div>
          <div className="detail-slider-container">
            {slides[slideIndex]}
            <div className="detail-nav">
              {slideIndex>0 &&
                <button onClick={()=>setSlideIndex(i=>i-1)} className="nav-btn left">‚Üê</button>}
              {slideIndex<slides.length-1 &&
                <button onClick={()=>setSlideIndex(i=>i+1)} className="nav-btn right">‚Üí</button>}
            </div>
          </div>
          <button className="insurance-btn selected"
                  onClick={()=>setStep('manager')}>
            –°–≤—è–∑–∞—Ç—å—Å—è —Å –º–µ–Ω–µ–¥–∂–µ—Ä–æ–º
          </button>
        </>
      )}

      {/* –§–æ—Ä–º–∞ –º–µ–Ω–µ–¥–∂–µ—Ä–∞ */}
      {step==='manager' && (
        <div className="snp-form">
          <div className="form-group">
            <label className="form-label">–§–∞–º–∏–ª–∏—è:</label>
            <input type="text" className="insurance-sum-input"
                   value={mgrSurname}
                   onChange={e=>setMgrSurname(e.target.value)}/>
          </div>
          <div className="form-group">
            <label className="form-label">–ò–º—è:</label>
            <input type="text" className="insurance-sum-input"
                   value={mgrName}
                   onChange={e=>setMgrName(e.target.value)}/>
          </div>
          <div className="form-group">
            <label className="form-label">–ì–æ—Ä–æ–¥ —Ä–∞–±–æ—Ç—ã:</label>
            <input type="text" className="insurance-sum-input"
                   value={mgrCity}
                   onChange={e=>setMgrCity(e.target.value)}/>
          </div>
          {mgrError && <div className="input-error-message">{mgrError}</div>}
          <div className="top-btn-container">
            <button className="top-btn"
                    onClick={handleManagerSubmit}>
              –û—Ç–ø—Ä–∞–≤–∏—Ç—å –º–µ–Ω–µ–¥–∂–µ—Ä—É
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
