import React, { useEffect, useState } from 'react';
import './fonts.css';  // Подключаем файл со шрифтами
import './Register.css';
import logo from './logo.png';  // Логотип

const Register = () => {
  const [userName, setUserName] = useState('Гость');

  useEffect(() => {
    console.log("Компонент Register загружен!");

    const getUserData = () => {
      if (window.Telegram && window.Telegram.WebApp) {
        try {
          const user = window.Telegram.WebApp.initDataUnsafe;
          console.log("User Data:", user);

          if (user && user.first_name) {
            setUserName(user.first_name);
          }
        } catch (error) {
          console.warn("Ошибка WebApp.initDataUnsafe:", error);
        }
      }
    };

    getUserData();
  }, []);

  return (
    <div className="register-container">
      <img src={logo} alt="Логотип" className="logo" />
      <h2 className="register-text">Добро пожаловать, {userName}</h2>
      <p className="register-subtext">Введите вашу корпоративную почту</p>
      <input type="email" className="register-input" placeholder="Введите email" />
      <button className="register-btn">Зарегистрироваться</button>
    </div>
  );
};

export default Register;
