// SNPPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import mainmenuIcon from './components/icons/mainmenu.png';
import logoImage from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';
import './SNPPage.css';

const SNPPage = () => {
  const navigate = useNavigate();

  // —Å–æ—Å—Ç–æ—è–Ω–∏–µ –¥–ª—è DateWheelPicker
  const [birthDateParts, setBirthDateParts] = useState(null);
  // –Ω–∞—Å—Ç–æ—è—â–∏–π –æ–±—ä–µ–∫—Ç Date
  const [birthDate, setBirthDate]       = useState(null);

  const [gender, setGender]             = useState(null);   // 'male' –∏–ª–∏ 'female'
  const [calcType, setCalcType]         = useState(null);   // 'premium' –∏–ª–∏ 'sum'
  const [amount, setAmount]             = useState('');     // —Å—Ç—Ä–æ–∫–∞ "100.000"
  const [amountError, setAmountError]   = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [resultData, setResultData]     = useState(null);

  // —Ñ–æ—Ä–º–∞—Ç–∏—Ä—É–µ–º —Å—É–º–º—É: "100000" ‚Üí "100.000"
  const formatSum = raw => {
    let digits = raw.replace(/\D/g, '');
    if (!digits) return '';
    let out = '';
    while (digits.length > 3) {
      out = '.' + digits.slice(-3) + out;
      digits = digits.slice(0, -3);
    }
    return digits + out;
  };

  const handleAmountChange = e => {
    const formatted = formatSum(e.target.value);
    setAmount(formatted);

    const num = parseInt(formatted.replace(/\./g, '')) || 0;
    if (calcType === 'premium') {
      setAmountError(num < 60000 ? '–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –æ—Ç 60 000 ‚ÇΩ' : '');
    } else if (calcType === 'sum') {
      setAmountError(num < 450000 ? '–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –æ—Ç 450 000 ‚ÇΩ' : '');
    }
  };

  const isAmountValid = () => {
    const num = parseInt(amount.replace(/\./g, '')) || 0;
    return (calcType === 'premium' && num >= 60000)
        || (calcType === 'sum'     && num >= 450000);
  };

  // –Ω–∞–¥—ë–∂–Ω—ã–π —Ä–∞—Å—á—ë—Ç –≤–æ–∑—Ä–∞—Å—Ç–∞
  const getAge = date => {
    if (!(date instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - date.getFullYear();
    const m = today.getMonth() - date.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < date.getDate())) {
      age--;
    }
    return age;
  };

  // –∫–Ω–æ–ø–∫–∞ ¬´–î–∞–ª–µ–µ¬ª –∞–∫—Ç–∏–≤–Ω–∞, –∫–æ–≥–¥–∞:
  // 1) –µ—Å—Ç—å –∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π Date –≤ birthDate,
  // 2) –≤—ã–±—Ä–∞–Ω –ø–æ–ª –∏ calcType,
  // 3) –≤–≤–µ–¥–µ–Ω–∞ –≤–∞–ª–∏–¥–Ω–∞—è —Å—É–º–º–∞,
  // 4) –≤–æ–∑—Ä–∞—Å—Ç ‚â• 18
  const canGoNext = () => (
    birthDate instanceof Date &&
    !isNaN(birthDate.getTime()) &&
    gender &&
    calcType &&
    amount &&
    !amountError &&
    getAge(birthDate) >= 18
  );

  const handleNext = async () => {
    if (!canGoNext()) return;

    const dateStrRu = birthDate.toLocaleDateString('ru-RU', {
      day: '2-digit', month: '2-digit', year: 'numeric'
    });
    const age = getAge(birthDate);

    const payload = {
      birthdate: dateStrRu,
      age,
      gender,
      payment: calcType === 'premium'
        ? '–°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞'
        : '–°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã',
      sum: parseInt(amount.replace(/\./g, ''), 10)
    };

    setIsProcessing(true);
    try {
      const res = await fetch('/api/snp/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const data = await res.json();
      if (res.ok) setResultData(data);
      else console.error('–°–µ—Ä–≤–µ—Ä –≤–µ—Ä–Ω—É–ª –æ—à–∏–±–∫—É:', data);
    } catch (e) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–ø—Ä–æ—Å–∞:', e);
    }
    setIsProcessing(false);
  };

  return (
    <div
      className="snp-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* –õ–æ–≥–æ—Ç–∏–ø */}
      <div className="logo-container">
        <img src={logoImage} alt="Logo" className="logo-image"/>
      </div>
      <h1 className="snp-title">
        –†–∞—Å—á—ë—Ç –ø–æ –ø—Ä–æ–≥—Ä–∞–º–º–µ "–°—Ç—Ä–∞—Ç–µ–≥–∏—è –Ω–∞ –ø—è—Ç—å. –ì–∞—Ä–∞–Ω—Ç"
      </h1>

      {/* –ü—Ä–æ—Ü–µ—Å—Å–∏–Ω–≥ –∏–ª–∏ —Ä–µ–∑—É–ª—å—Ç–∞—Ç –∏–ª–∏ —Ñ–æ—Ä–º–∞ */}
      {isProcessing ? (
        <div className="processing-container">
          <p>–í—ã–ø–æ–ª–Ω—è–µ—Ç—Å—è —Ä–∞—Å—á—ë—Ç...</p>
          <div className="arrow-animation">‚Üí</div>
        </div>
      ) : resultData ? (
        <div className="result-container">
          <h2>–†–µ–∑—É–ª—å—Ç–∞—Ç—ã —Ä–∞—Å—á—ë—Ç–∞</h2>
          <p><strong>–í–æ–∑—Ä–∞—Å—Ç:</strong> {resultData.age} –ª–µ—Ç</p>
          <p><strong>–ü–æ–ª:</strong> {resultData.gender}</p>
          <p><strong>–¢–∏–ø —Ä–∞—Å—á—ë—Ç–∞:</strong> {resultData.payment}</p>
          <p>
            <strong>–°—É–º–º–∞:</strong> {formatSum(resultData.sum.toString())} ‚ÇΩ
          </p>
          <p>
            <strong>–ò—Ç–æ–≥–æ–≤–∞—è –ø—Ä–µ–º–∏—è:</strong>{' '}
            {formatSum((resultData.totalPremium||0).toString())} ‚ÇΩ
          </p>
        </div>
      ) : (
        <div className="snp-form">
          {/* 1. DateWheelPicker */}
          <div className="form-group">
            <label className="form-label">–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è:</label>
            <DateWheelPicker
              value={birthDateParts}
              onChange={val => {
                // –µ—Å–ª–∏ —Å—Ä–∞–∑—É Date
                if (val instanceof Date) {
                  setBirthDate(val);
                  setBirthDateParts({
                    day: val.getDate(),
                    month: val.getMonth() + 1,
                    year: val.getFullYear()
                  });
                }
                // –µ—Å–ª–∏ –æ–±—ä–µ–∫—Ç { day, month, year }
                else if (val && val.day && val.month && val.year) {
                  const d = Number(val.day),
                        m = Number(val.month),
                        y = Number(val.year);
                  const dateObj = new Date(y, m - 1, d);
                  setBirthDate(dateObj);
                  setBirthDateParts({ day: d, month: m, year: y });
                }
                // –µ—Å–ª–∏ —Å—Ç—Ä–æ–∫–∞
                else if (typeof val === 'string') {
                  if (val.includes('.')) {
                    const [d, m, y] = val.split('.').map(Number);
                    const dateObj = new Date(y, m - 1, d);
                    setBirthDate(dateObj);
                    setBirthDateParts({ day: d, month: m, year: y });
                  } else if (val.includes('-')) {
                    const [y, m, d] = val.split('-').map(Number);
                    const dateObj = new Date(y, m - 1, d);
                    setBirthDate(dateObj);
                    setBirthDateParts({ day: d, month: m, year: y });
                  }
                }
              }}
            />
          </div>

          {/* 2. –ü–æ–ª */}
          <div className="form-group">
            <label className="form-label">–ü–æ–ª:</label>
            <div className="gender-selector">
              {['male','female'].map(g => (
                <div
                  key={g}
                  className={`gender-option ${gender===g?'selected':''}`}
                  onClick={()=>setGender(g)}
                >
                  <div className="gender-label">
                    {g==='male'?'–ú—É–∂—Å–∫–æ–π':'–ñ–µ–Ω—Å–∫–∏–π'}
                  </div>
                  <div className="gender-icon">
                    {g==='male'?'üë®':'üë©'}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* 3. –¢–∏–ø —Ä–∞—Å—á—ë—Ç–∞ */}
          <div className="form-group">
            <label className="form-label">–†–∞—Å—á—ë—Ç –æ—Ç‚Ä¶</label>
            <div className="insurance-options">
              <button
                className={`insurance-btn ${calcType==='premium'?'selected':''}`}
                onClick={()=>{
                  setCalcType('premium');
                  setAmount(''); setAmountError('');
                }}
              >–°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞</button>
              <button
                className={`insurance-btn ${calcType==='sum'?'selected':''}`}
                onClick={()=>{
                  setCalcType('sum');
                  setAmount(''); setAmountError('');
                }}
              >–°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã</button>
            </div>
          </div>

          {/* 4. –°—É–º–º–∞ */}
          {calcType && (
            <div className="form-group">
              <label className="form-label">
                –í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –≤ —Ä—É–±–ª—è—Ö
              </label>
              <input
                type="text"
                className="insurance-sum-input"
                value={amount}
                onChange={handleAmountChange}
                placeholder={calcType==='premium'
                  ? '–ú–∏–Ω–∏–º—É–º 60 000'
                  : '–ú–∏–Ω–∏–º—É–º 450 000'}
              />
              {amountError && (
                <div className="input-error-message">
                  {amountError}
                </div>
              )}
            </div>
          )}
        </div>
      )}

      {/* –ö–Ω–æ–ø–∫–∞ ¬´–ú–µ–Ω—é¬ª */}
      <div className="back-btn-container">
        <button
          className="back-btn mainmenu-btn"
          onClick={()=>navigate('/Main-Menu')}
        >
          <img src={mainmenuIcon} alt="Menu"/>
        </button>
      </div>

      {/* –ö–Ω–æ–ø–∫–∞ ¬´–î–∞–ª–µ–µ¬ª */}
      {!isProcessing && !resultData && (
        <div className="top-btn-container">
          <button
            className="top-btn"
            onClick={handleNext}
            disabled={!canGoNext()}
          >
            <svg viewBox="0 0 24 24">
              <path d="M12 4l8 8-8 8"/>
            </svg>
          </button>
        </div>
      )}
    </div>
  );
};

export default SNPPage;
