import React, { useState, useEffect } from 'react';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';
import './JustincasePage.css';

const JustincasePage = () => {
  // 1) –û–±—â–µ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
  const [userName, setUserName] = useState('');
  useEffect(() => {
    const storedName = localStorage.getItem('userName');
    setUserName(storedName || '–ì–æ—Å—Ç—å');
  }, []);

  // 2) –°–æ–±–∏—Ä–∞–µ–º—ã–µ –¥–∞–Ω–Ω—ã–µ
  const [birthDate, setBirthDate] = useState(null);         // —Ñ–æ—Ä–º–∞—Ç "dd/mm/yyyy"
  const [gender, setGender] = useState(null);               // 'male' / 'female'
  const [insuranceInfo, setInsuranceInfo] = useState(null);   // 'yes' / 'no'
  const [insuranceTerm, setInsuranceTerm] = useState('1');    // —Å—Ä–æ–∫ —Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏—è (–ª–µ—Ç)
  const [insuranceSum, setInsuranceSum] = useState('');       // —Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–Ω–∞—è —Å—É–º–º–∞
  const [insuranceFrequency, setInsuranceFrequency] = useState(''); // –ø–µ—Ä–∏–æ–¥–∏—á–Ω–æ—Å—Ç—å –æ–ø–ª–∞—Ç—ã

  // –ü–∞–∫–µ—Ç—ã (—à–∞–≥ 3)
  const [accidentPackage, setAccidentPackage] = useState(null); // 'yes' / 'no'
  const [criticalPackage, setCriticalPackage] = useState(null); // 'yes' / 'no'
  const [treatmentRegion, setTreatmentRegion] = useState(null);   // 'russia' / 'abroad'
  const [sportPackage, setSportPackage] = useState(null);         // 'yes' / 'no'

  // 3) –®–∞–≥–∏:
  // –®–∞–≥ 1 ‚Äì –¥–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è, –ø–æ–ª, –∑–Ω–∞–Ω–∏–µ –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤ –ø–æ–ª–∏—Å–∞
  // –®–∞–≥ 2 ‚Äì (–µ—Å–ª–∏ insuranceInfo === 'yes') ‚Äì –¥–µ—Ç–∞–ª–∏ —Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏—è: —Å—Ä–æ–∫, —Å—É–º–º–∞, –ø–µ—Ä–∏–æ–¥–∏—á–Ω–æ—Å—Ç—å
  // –®–∞–≥ 3 ‚Äì –ü–∞–∫–µ—Ç—ã: –≤–æ–ø—Ä–æ—Å—ã –ø–æ –ø–∞–∫–µ—Ç–∞–º ¬´–ù–µ—Å—á–∞—Å—Ç–Ω—ã–π —Å–ª—É—á–∞–π¬ª, ¬´–ö—Ä–∏—Ç–∏—á–µ—Å–∫–∏–µ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏—è¬ª (—Å —Ä–µ–≥–∏–æ–Ω–æ–º –ª–µ—á–µ–Ω–∏—è, –µ—Å–ª–∏ –≤—ã–±—Ä–∞–Ω–æ ¬´–î–∞¬ª) –∏ ¬´–õ—é–±–∏—Ç–µ–ª—å—Å–∫–∏–π —Å–ø–æ—Ä—Ç¬ª
  const [currentStep, setCurrentStep] = useState(1);

  // ---------- –í—Å–ø–æ–º–æ–≥–∞—Ç–µ–ª—å–Ω—ã–µ —Ñ—É–Ω–∫—Ü–∏–∏ ----------
  const parseDate = (dateStr) => {
    const [day, month, year] = dateStr.split('/');
    return new Date(+year, +month - 1, +day);
  };

  const calculateAge = (dateObj) => {
    const today = new Date();
    let age = today.getFullYear() - dateObj.getFullYear();
    const m = today.getMonth() - dateObj.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < dateObj.getDate())) {
      age--;
    }
    return age;
  };

  // –§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ —Å—É–º–º—ã (–Ω–∞–ø—Ä–∏–º–µ—Ä "1.000.000")
  const formatSum = (rawValue) => {
    let digits = rawValue.replace(/\D/g, '');
    if (!digits) return '';
    let result = '';
    while (digits.length > 3) {
      const chunk = digits.slice(-3);
      digits = digits.slice(0, -3);
      result = '.' + chunk + result;
    }
    result = digits + result;
    return result;
  };

  const handleSumChange = (e) => {
    const formatted = formatSum(e.target.value);
    setInsuranceSum(formatted);
  };

  const handleTermChange = (e) => {
    setInsuranceTerm(e.target.value);
  };

  const handleFrequencyChange = (e) => {
    setInsuranceFrequency(e.target.value);
  };

  // ---------- –ü–µ—Ä–µ—Ö–æ–¥—ã –º–µ–∂–¥—É —à–∞–≥–∞–º–∏ ----------
  const handleNext = () => {
    if (currentStep === 1) {
      // –®–∞–≥ 1: –ü—Ä–æ–≤–µ—Ä—è–µ–º –¥–∞—Ç—É —Ä–æ–∂–¥–µ–Ω–∏—è, –≤–æ–∑—Ä–∞—Å—Ç, –ø–æ–ª –∏ –æ—Ç–≤–µ—Ç –Ω–∞ –≤–æ–ø—Ä–æ—Å –æ –∑–Ω–∞–Ω–∏–∏ –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤
      if (!birthDate) {
        alert('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤—ã–±–µ—Ä–∏—Ç–µ –¥–∞—Ç—É —Ä–æ–∂–¥–µ–Ω–∏—è');
        return;
      }
      const bd = parseDate(birthDate);
      const age = calculateAge(bd);
      if (age < 18 || age > 70) {
        alert('–í–æ–∑—Ä–∞—Å—Ç –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –æ—Ç 18 –¥–æ 70 –ª–µ—Ç');
        return;
      }
      if (!gender) {
        alert('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤—ã–±–µ—Ä–∏—Ç–µ –ø–æ–ª');
        return;
      }
      if (!insuranceInfo) {
        alert('–£–∫–∞–∂–∏—Ç–µ, –∑–Ω–∞–µ—Ç–µ –ª–∏ –í—ã –Ω–∞ –∫–∞–∫–æ–π —Å—Ä–æ–∫ –∏ –Ω–∞ –∫–∞–∫—É—é —Å—É–º–º—É –æ—Ñ–æ—Ä–º–∏—Ç—å –ø–æ–ª–∏—Å');
        return;
      }
      // –ï—Å–ª–∏ –æ—Ç–≤–µ—Ç "yes", –ø–µ—Ä–µ—Ö–æ–¥–∏–º –∫ —à–∞–≥—É 2, –∏–Ω–∞—á–µ –ø—Ä–æ–ø—É—Å–∫–∞–µ–º –¥–µ—Ç–∞–ª–∏ –∏ —Å—Ä–∞–∑—É –ø–µ—Ä–µ—Ö–æ–¥–∏–º –∫ —à–∞–≥—É 3 (–ø–∞–∫–µ—Ç—ã)
      setCurrentStep(insuranceInfo === 'yes' ? 2 : 3);
    } else if (currentStep === 2) {
      // –®–∞–≥ 2: –ü—Ä–æ–≤–µ—Ä–∫–∞ –¥–µ—Ç–∞–ª–µ–π —Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏—è
      const termNum = Number(insuranceTerm);
      if (termNum < 1 || termNum > 30) {
        alert('–°—Ä–æ–∫ —Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏—è –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –æ—Ç 1 –¥–æ 30 –ª–µ—Ç');
        return;
      }
      const rawDigits = insuranceSum.replace(/\D/g, '');
      if (!rawDigits) {
        alert('–í–≤–µ–¥–∏—Ç–µ —Å—Ç—Ä–∞—Ö–æ–≤—É—é —Å—É–º–º—É');
        return;
      }
      const sumNumber = Number(rawDigits);
      if (sumNumber < 1000000) {
        alert('–°—Ç—Ä–∞—Ö–æ–≤–∞—è —Å—É–º–º–∞ –¥–æ–ª–∂–Ω–∞ –±—ã—Ç—å –æ—Ç 1 000 000 —Ä—É–±.');
        return;
      }
      if (!insuranceFrequency) {
        alert('–í—ã–±–µ—Ä–∏—Ç–µ –ø–µ—Ä–∏–æ–¥–∏—á–Ω–æ—Å—Ç—å –æ–ø–ª–∞—Ç—ã —Å—Ç—Ä–∞—Ö–æ–≤–æ–π –ø—Ä–µ–º–∏–∏');
        return;
      }
      setCurrentStep(3);
    } else if (currentStep === 3) {
      // –®–∞–≥ 3: –ü—Ä–æ–≤–µ—Ä–∫–∞ –ø–∞–∫–µ—Ç–æ–≤
      if (!accidentPackage) {
        alert('–£–∫–∞–∂–∏—Ç–µ, –ø–æ–¥–∫–ª—é—á–∞–µ–º –ª–∏ –í—ã –ø–∞–∫–µ—Ç "–ù–µ—Å—á–∞—Å—Ç–Ω—ã–π —Å–ª—É—á–∞–π"');
        return;
      }
      if (!criticalPackage) {
        alert('–£–∫–∞–∂–∏—Ç–µ, –ø–æ–¥–∫–ª—é—á–∞–µ–º –ª–∏ –í—ã –ø–∞–∫–µ—Ç "–ö—Ä–∏—Ç–∏—á–µ—Å–∫–∏–µ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏—è"');
        return;
      }
      if (criticalPackage === 'yes' && !treatmentRegion) {
        alert('–í—ã–±–µ—Ä–∏—Ç–µ —Ä–µ–≥–∏–æ–Ω –ª–µ—á–µ–Ω–∏—è –¥–ª—è –ø–∞–∫–µ—Ç–∞ "–ö—Ä–∏—Ç–∏—á–µ—Å–∫–∏–µ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏—è"');
        return;
      }
      if (!sportPackage) {
        alert('–£–∫–∞–∂–∏—Ç–µ, –ø–æ–¥–∫–ª—é—á–∞–µ–º –ª–∏ –í—ã –æ–ø—Ü–∏—é "–õ—é–±–∏—Ç–µ–ª—å—Å–∫–∏–π —Å–ø–æ—Ä—Ç"');
        return;
      }
      // –ï—Å–ª–∏ –≤—Å–µ –ø—Ä–æ–≤–µ—Ä–∫–∏ –ø—Ä–æ–π–¥–µ–Ω—ã, –∑–∞–≤–µ—Ä—à–∞–µ–º –æ–ø—Ä–æ—Å
      handleFinish();
    }
  };

  const handleFinish = () => {
    alert(`–î–∞–Ω–Ω—ã–µ:
–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è: ${birthDate}
–ü–æ–ª: ${gender}
–ó–Ω–∞–µ—Ç –ª–∏ –ø–∞—Ä–∞–º–µ—Ç—Ä—ã: ${insuranceInfo}
${insuranceInfo === 'yes' ? `–°—Ä–æ–∫ —Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏—è: ${insuranceTerm} –ª–µ—Ç
–°—Ç—Ä–∞—Ö–æ–≤–∞—è —Å—É–º–º–∞: ${insuranceSum} —Ä—É–±.
–ü–µ—Ä–∏–æ–¥–∏—á–Ω–æ—Å—Ç—å –æ–ø–ª–∞—Ç—ã: ${insuranceFrequency}` : ''}
–ü–∞–∫–µ—Ç "–ù–µ—Å—á–∞—Å—Ç–Ω—ã–π —Å–ª—É—á–∞–π": ${accidentPackage}
–ü–∞–∫–µ—Ç "–ö—Ä–∏—Ç–∏—á–µ—Å–∫–∏–µ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏—è": ${criticalPackage}
${criticalPackage === 'yes' ? `–†–µ–≥–∏–æ–Ω –ª–µ—á–µ–Ω–∏—è: ${treatmentRegion === 'russia' ? '–õ–µ—á–µ–Ω–∏–µ –≤ –†–§' : '–õ–µ—á–µ–Ω–∏–µ –∑–∞ —Ä—É–±–µ–∂–æ–º'}` : ''}
–û–ø—Ü–∏—è "–õ—é–±–∏—Ç–µ–ª—å—Å–∫–∏–π —Å–ø–æ—Ä—Ç": ${sportPackage}
`);
    // –ó–¥–µ—Å—å –º–æ–∂–Ω–æ –≤—ã–ø–æ–ª–Ω–∏—Ç—å –æ—Ç–ø—Ä–∞–≤–∫—É –¥–∞–Ω–Ω—ã—Ö –Ω–∞ —Å–µ—Ä–≤–µ—Ä –∏–ª–∏ –ø–µ—Ä–µ—Ö–æ–¥ –Ω–∞ —Ñ–∏–Ω–∞–ª—å–Ω—É—é —Å—Ç—Ä–∞–Ω–∏—Ü—É
  };

  // ---------- –†–µ–Ω–¥–µ—Ä —à–∞–≥–æ–≤ ----------
  const renderStep = () => {
    switch (currentStep) {
      case 1:
        return (
          <>
            <div className="form-group">
              <label className="form-label">–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è:</label>
              <DateWheelPicker onChange={(dateString) => setBirthDate(dateString)} />
            </div>
            <div className="form-group">
              <label className="form-label">–ü–æ–ª:</label>
              <div className="gender-selector">
                <div
                  className={`gender-option ${gender === 'male' ? 'selected' : ''}`}
                  onClick={() => setGender('male')}
                >
                  <div className="gender-label">–ú—É–∂—Å–∫–æ–π</div>
                  <div className="gender-icon">üë®</div>
                </div>
                <div
                  className={`gender-option ${gender === 'female' ? 'selected' : ''}`}
                  onClick={() => setGender('female')}
                >
                  <div className="gender-label">–ñ–µ–Ω—Å–∫–∏–π</div>
                  <div className="gender-icon">üë©</div>
                </div>
              </div>
            </div>
            <div className="form-group">
              <label className="form-label">
                –ó–Ω–∞–µ—Ç–µ –ª–∏ –í—ã –Ω–∞ –∫–∞–∫–æ–π —Å—Ä–æ–∫ –∏ –Ω–∞ –∫–∞–∫—É—é —Å—É–º–º—É –æ—Ñ–æ—Ä–º–∏—Ç—å —Å—Ç—Ä–∞—Ö–æ–≤–æ–π –ø–æ–ª–∏—Å?
              </label>
              <div className="insurance-options">
                <button
                  className={`insurance-btn ${insuranceInfo === 'yes' ? 'selected' : ''}`}
                  onClick={() => setInsuranceInfo('yes')}
                >
                  –î–∞
                </button>
                <button
                  className={`insurance-btn ${insuranceInfo === 'no' ? 'selected' : ''}`}
                  onClick={() => setInsuranceInfo('no')}
                >
                  –ù–µ—Ç
                </button>
              </div>
            </div>
          </>
        );
      case 2:
        return (
          <>
            <div className="form-group">
              <label className="form-label">–í—ã–±–µ—Ä–∏—Ç–µ —Å—Ä–æ–∫ —Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏—è (–ª–µ—Ç):</label>
              <select
                className="insurance-term-select"
                value={insuranceTerm}
                onChange={handleTermChange}
              >
                {Array.from({ length: 30 }, (_, i) => i + 1).map((year) => (
                  <option key={year} value={year}>
                    {year}
                  </option>
                ))}
              </select>
            </div>
            <div className="form-group">
              <label className="form-label">–í–≤–µ–¥–∏—Ç–µ —Å—Ç—Ä–∞—Ö–æ–≤—É—é —Å—É–º–º—É (–æ—Ç 1 –º–ª–Ω —Ä—É–±):</label>
              <input
                type="text"
                className="insurance-sum-input"
                placeholder="1.000.000"
                value={insuranceSum}
                onChange={handleSumChange}
              />
            </div>
            <div className="form-group">
              <label className="form-label">–ü–µ—Ä–∏–æ–¥–∏—á–Ω–æ—Å—Ç—å –æ–ø–ª–∞—Ç—ã —Å—Ç—Ä–∞—Ö–æ–≤–æ–π –ø—Ä–µ–º–∏–∏:</label>
              <select
                className="insurance-frequency-select"
                value={insuranceFrequency}
                onChange={handleFrequencyChange}
              >
                <option value="">–í—ã–±–µ—Ä–∏—Ç–µ –ø–µ—Ä–∏–æ–¥</option>
                {["–ï–∂–µ–º–µ—Å—è—á–Ω–æ", "–ï–∂–µ–≥–æ–¥–Ω–æ", "—Ä–∞–∑ –≤ –ø–æ–ª –≥–æ–¥–∞", "–µ–∂–µ–∫–≤–∞—Ä—Ç–∞–ª—å–Ω–æ"].map((option) => (
                  <option key={option} value={option}>
                    {option}
                  </option>
                ))}
              </select>
            </div>
          </>
        );
      case 3:
        return (
          <>
            <div className="form-group">
              <label className="form-label">
                –ü–æ–¥–∫–ª—é—á–∞–µ–º –ø–∞–∫–µ—Ç ¬´–ù–µ—Å—á–∞—Å—Ç–Ω—ã–π —Å–ª—É—á–∞–π¬ª
              </label>
              <div className="insurance-options">
                <button
                  className={`insurance-btn ${accidentPackage === 'yes' ? 'selected' : ''}`}
                  onClick={() => setAccidentPackage('yes')}
                >
                  –î–∞
                </button>
                <button
                  className={`insurance-btn ${accidentPackage === 'no' ? 'selected' : ''}`}
                  onClick={() => setAccidentPackage('no')}
                >
                  –ù–µ—Ç
                </button>
              </div>
            </div>
            <div className="form-group">
              <label className="form-label">
                –ü–æ–¥–∫–ª—é—á–∞–µ–º –ø–∞–∫–µ—Ç ¬´–ö—Ä–∏—Ç–∏—á–µ—Å–∫–∏–µ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏—è¬ª
              </label>
              <div className="insurance-options">
                <button
                  className={`insurance-btn ${criticalPackage === 'yes' ? 'selected' : ''}`}
                  onClick={() => setCriticalPackage('yes')}
                >
                  –î–∞
                </button>
                <button
                  className={`insurance-btn ${criticalPackage === 'no' ? 'selected' : ''}`}
                  onClick={() => setCriticalPackage('no')}
                >
                  –ù–µ—Ç
                </button>
              </div>
            </div>
            {criticalPackage === 'yes' && (
              <div className="form-group">
                <label className="form-label">
                  –ö–∞–∫–æ–π —Ä–µ–≥–∏–æ–Ω –ª–µ—á–µ–Ω–∏—è –ø–æ–¥–∫–ª—é—á–∞–µ–º?
                </label>
                <div className="insurance-options">
                  <button
                    className={`insurance-btn ${treatmentRegion === 'russia' ? 'selected' : ''}`}
                    onClick={() => setTreatmentRegion('russia')}
                  >
                    –õ–µ—á–µ–Ω–∏–µ –≤ –†–§
                  </button>
                  <button
                    className={`insurance-btn ${treatmentRegion === 'abroad' ? 'selected' : ''}`}
                    onClick={() => setTreatmentRegion('abroad')}
                  >
                    –õ–µ—á–µ–Ω–∏–µ –∑–∞ —Ä—É–±–µ–∂–æ–º
                  </button>
                </div>
              </div>
            )}
            {/* –ù–æ–≤—ã–π –≤–æ–ø—Ä–æ—Å "–õ—é–±–∏—Ç–µ–ª—å—Å–∫–∏–π —Å–ø–æ—Ä—Ç" –ø–µ—Ä–µ–Ω–µ—Å—ë–Ω –≤ –∫–æ–Ω–µ—Ü —à–∞–≥–∞ 3 */}
            <div className="form-group">
              <label className="form-label">
                –ü–æ–¥–∫–ª—é—á–∞–µ–º –æ–ø—Ü–∏—é ¬´–õ—é–±–∏—Ç–µ–ª—å—Å–∫–∏–π —Å–ø–æ—Ä—Ç¬ª?
              </label>
              <div className="insurance-options">
                <button
                  className={`insurance-btn ${sportPackage === 'yes' ? 'selected' : ''}`}
                  onClick={() => setSportPackage('yes')}
                >
                  –î–∞
                </button>
                <button
                  className={`insurance-btn ${sportPackage === 'no' ? 'selected' : ''}`}
                  onClick={() => setSportPackage('no')}
                >
                  –ù–µ—Ç
                </button>
              </div>
            </div>
          </>
        );
      default:
        return null;
    }
  };

  return (
    <div className="justincase-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <h1 className="justincase-title">
        –†–∞—Å—á–µ—Ç –ø–æ –ø—Ä–æ–≥—Ä–∞–º–º–µ "–ù–∞ –≤—Å—è–∫–∏–π —Å–ª—É—á–∞–π" –¥–ª—è —Å–æ—Ç—Ä—É–¥–Ω–∏–∫–∞
      </h1>

      <div className="justincase-form">{renderStep()}</div>

      {/* –ö–Ω–æ–ø–∫–∞ "–î–∞–ª–µ–µ" (—Å—Ç—Ä–µ–ª–∫–∞) */}
      <div className="top-btn-container">
        <button className="top-btn" onClick={handleNext}>
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none">
            <path d="M12 4l8 8-8 8" />
          </svg>
        </button>
      </div>
    </div>
  );
};

export default JustincasePage;
