import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';

import './PollsPage.css';
import './SNPPage.css';

export default function SNPPage() {
  const navigate = useNavigate();

  /*** 1. Состояния ***/
  const [step, setStep]                   = useState(1);
  const [email, setEmail]                 = useState('');
  const [emailError, setEmailError]       = useState('');
  const [birthDate, setBirthDate]         = useState(new Date());
  const [gender, setGender]               = useState(null);    // 'мужской' | 'женский' | null
  const [calcType, setCalcType]           = useState(null);    // 'premium' | 'sum' | null
  const [amountRaw, setAmountRaw]         = useState('');      // вводятся только цифры
  const [amountDisplay, setAmountDisplay] = useState('');      // строка с точками
  const [amountError, setAmountError]     = useState('');
  const [isProcessing, setIsProcessing]   = useState(false);
  const [resultData, setResultData]       = useState(null);    // сюда придут данные ответа от сервера: { h12, h13, h16, … }

  const [carouselIndex, setCarouselIndex] = useState(0);       // для карусели (0,1,2)

  // Шаг 3: контактные данные
  const [lastName, setLastName]   = useState('');
  const [firstName, setFirstName] = useState('');
  const [middleName, setMiddleName] = useState('');
  const [city, setCity]           = useState('');
  const [contactError, setContactError] = useState('');

  /*** 2. Валидация email ***/
  const emailValid = email.toLowerCase().endsWith('@vtb.ru');

  /*** 3. Функция для вычисления возраста по дате ***/
  function getAge(d) {
    if (!(d instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - d.getFullYear();
    if (today.getMonth() < d.getMonth() ||
       (today.getMonth() === d.getMonth() && today.getDate() < d.getDate())) {
      age--;
    }
    return age;
  }

  /*** 4. Валидация суммы ***/
  function isAmountValid() {
    const n = parseInt(amountRaw, 10) || 0;
    return (calcType === 'premium' && n >= 60000)
        || (calcType === 'sum'     && n >= 450000);
  }

  /*** 5. Можно ли перейти к следующему шагу (активация кнопки «Далее») ***/
  function canGoNext() {
    return step === 2
        && getAge(birthDate) >= 18
        && gender
        && calcType
        && amountRaw
        && isAmountValid();
  }

  /*** 6. Шаг 1 → Шаг 2: проверка email ***/
  function handleEmailNext() {
    if (!emailValid) {
      setEmailError('Используйте корпоративную почту домена @vtb.ru');
      return;
    }
    setEmailError('');

    // сбросим скрытые поля перед шагом 2
    setGender(null);
    setCalcType(null);
    setAmountRaw('');
    setAmountDisplay('');
    setAmountError('');
    setStep(2);
  }

  /*** 7. Обработка ввода суммы: только цифры, форматирование с точками, проверка min ***/
  function onAmountChange(e) {
    const raw = e.target.value.replace(/\D/g, '');
    setAmountRaw(raw);

    // формируем строку «дорожность» с разделителем тысяч
    let d = raw, o = '';
    while (d.length > 3) {
      o = '.' + d.slice(-3) + o;
      d = d.slice(0, -3);
    }
    setAmountDisplay(d + o);

    // показываем ошибку, если меньше минимума
    const n = parseInt(raw, 10) || 0;
    if      (calcType === 'premium' && n < 60000) setAmountError('Мин. 60 000 ₽');
    else if (calcType === 'sum'     && n < 450000) setAmountError('Мин. 450 000 ₽');
    else                                           setAmountError('');
  }

  /*** 8. Шаг 2 → Отправка и рендер результата ***/
  async function handleNext() {
    if (!canGoNext()) return;
    setIsProcessing(true);

    const dateStr = birthDate.toLocaleDateString('ru-RU', {
      day: '2-digit', month: '2-digit', year: 'numeric'
    });
    const payload = {
      email,
      birthdate: dateStr,
      age:        getAge(birthDate),
      gender,
      payment:    calcType === 'premium' ? 'Страхового взноса' : 'Страховой суммы',
      sum:        parseInt(amountRaw, 10)
    };

    try {
      const res = await fetch('/api/proxy/snp/save', {
        method:  'POST',
        headers: { 'Content-Type': 'application/json' },
        body:    JSON.stringify(payload)
      });
      if (!res.ok) {
        console.error('Ошибка сервера', res.status, await res.text());
        alert(`Ошибка ${res.status}: смотрите консоль`);
      } else {
        const data = await res.json();
        // предполагается, что сервер вернёт объект вида:
        // { h12: 44, h13: 52800, h16: 264000, … }
        setResultData(data);
        // После приёма результата сразу сбрасываем carouselIndex на 0
        setCarouselIndex(0);
      }
    } catch (err) {
      console.error('Ошибка запроса:', err);
      alert('Не удалось выполнить запрос');
    } finally {
      setIsProcessing(false);
    }
  }

  /*** 9. Переход «Домой» (назад) ***/
  function goHome() {
    navigate(-1);
  }

  /*** 10. Валидация контактных данных (шаг 3) ***/
  function handleContactSubmit() {
    if (!lastName || !firstName || !city) {
      setContactError('Пожалуйста, заполните обязательные поля.');
      return;
    }
    setContactError('');
    // Здесь вы можете отправить контактные данные на сервер или дальше обрабатывать
    alert(`Спасибо! Данные приняты.\nФИО: ${lastName} ${firstName} ${middleName}\nГород: ${city}`);
  }

  /***************************************************/
  /***  A) Экран «Выполняется расчёт…» ***/
  if (isProcessing) {
    return (
      <div className="processing-screen">
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип" />
        </div>
        <p className="processing-title">
          Выполняется расчёт по программе «Стратегия на пять. Гарант»
        </p>
        <div className="arrow-animation">→</div>
      </div>
    );
  }

  /***************************************************/
  /***  B) Если есть resultData — показываем итоговый экран с каруселью + контактами ***/
  if (resultData) {
    const todayStr = new Date().toLocaleDateString('ru-RU', {
      day: '2-digit', month: '2-digit', year: 'numeric'
    });
    const { h12, h13, h16 } = resultData; // { процент, C13, C16 }

    return (
      <div className="snp-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
        <div className="polls-header">
          {/* Домой */}
          <button className="back-btn" onClick={goHome}>
            <svg viewBox="0 0 24 24">
              <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z" />
            </svg>
          </button>

          {/* Логотип */}
          <div className="logo-wrapper animate-logo">
            <img className="poll-logo" src={logoImage} alt="Логотип" />
          </div>

          {/* Повторить расчёт */}
          <button
            className="repeat-btn"
            onClick={() => {
              // сбросим результат и вернёмся к шагу 2
              setResultData(null);
              setGender(null);
              setCalcType(null);
              setAmountRaw('');
              setAmountDisplay('');
              setAmountError('');
              setStep(2);
            }}
          >
            <svg viewBox="0 0 24 24">
              <path
                d="M12 4V1L8 5l4 4V6
                   c4.41 0 8 3.59 8 8
                   s-3.59 8-8 8-8-3.59-8-8h2
                   c0 3.31 2.69 6 6 6s6-2.69 6-6
                   -2.69-6-6-6v3l-4-4 4-4z"
              />
            </svg>
          </button>
        </div>

        <div className="content-wrapper">
          {/* Заголовок итогового экрана */}
          <h1 className="polls-title">
            Расчёт по программе «Стратегия на пять. Гарант»
          </h1>

          {/* Блок «Параметры» */}
          <div className="params-container">
            <h2>Параметры</h2>
            <p>Дата расчёта: {todayStr}</p>
            <p>Возраст: {getAge(birthDate)} лет</p>
            <p>Пол: {gender}</p>
            <p>Расчёт от: {calcType === 'premium' ? 'Страхового взноса' : 'Страховой суммы'}</p>
          </div>

          {/* ========== КАРУСЕЛЬ ========== */}
          <div className="carousel-container">
            {/* Индикаторы (полоски) */}
            <div className="carousel-indicators">
              <span className={carouselIndex === 0 ? 'active' : ''}></span>
              <span className={carouselIndex === 1 ? 'active' : ''}></span>
              <span className={carouselIndex === 2 ? 'active' : ''}></span>
            </div>

            {/* «Окно» карусели */}
            <div className="carousel-clipper">
              <div
                className="carousel-wrapper"
                style={{ transform: `translateX(-${carouselIndex * 100}%)` }}
              >
                {/* ————— ПЕРВЫЙ (1-й) слайд — «В течение 5 лет» ————— */}
                <div className="carousel-page">
                  <p className="first-line">В течение 5 лет</p>
                  <p className="unit">Вы вносите средства в программу</p>
                  <p className="value">
                    {Number(h13).toLocaleString('ru-RU', {
                      minimumFractionDigits: 2,
                      maximumFractionDigits: 2
                    })} ₽
                  </p>
                  <p className="unit">Ежегодно</p>
                  <p className="value">
                    {Number(h16).toLocaleString('ru-RU', {
                      minimumFractionDigits: 2,
                      maximumFractionDigits: 2
                    })} ₽
                  </p>
                  <p className="unit">Сумма ваших взносов</p>
                </div>

                {/* ————— ВТОРОЙ (2-й) слайд — «Начисляем дополнительно» ————— */}
                <div className="carousel-page">
                  <p className="first-line">Начисляем дополнительно</p>
                  <p className="unit">{h12}% от каждого взноса</p>
                  <p className="value">
                    {Number(h13 * (h12 / 100)).toLocaleString('ru-RU', {
                      minimumFractionDigits: 2,
                      maximumFractionDigits: 2
                    })} ₽
                  </p>
                  <p className="unit">Ежегодно</p>
                  <p className="value">
                    {Number(h16).toLocaleString('ru-RU', {
                      minimumFractionDigits: 2,
                      maximumFractionDigits: 2
                    })} ₽
                  </p>
                  <p className="unit">Общая сумма начислений</p>
                </div>

                {/* ————— ТРЕТИЙ (3-й) слайд — можно заполнить позже ————— */}
                <div className="carousel-page">
                  <p className="first-line">Здесь будет 3-й слайд</p>
                  {/* Сейчас пусто или «контент для 3-го слайда» */}
                </div>
              </div>

              {/* Кнопки «‹» и «›» */}
              <button
                className="carousel-control prev"
                onClick={() => setCarouselIndex(prev => Math.max(prev - 1, 0))}
                disabled={carouselIndex === 0}
              >
                ‹
              </button>
              <button
                className="carousel-control next"
                onClick={() => setCarouselIndex(prev => Math.min(prev + 1, 2))}
                disabled={carouselIndex === 2}
              >
                ›
              </button>
            </div>
          </div>

          {/* Кнопка «Связаться с менеджером» */}
          <button
            className="polls-btn"
            style={{ marginTop: '24px', maxWidth: '320px' }}
            onClick={() => setStep(3)}
          >
            Связаться с менеджером
          </button>
        </div>
      </div>
    );
  }

  /***************************************************/
  /***  C) Шаг 3: форма ввода контактных данных ***/
  if (step === 3) {
    return (
      <div className="snp-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
        <div className="polls-header">
          {/* Домой */}
          <button className="back-btn" onClick={goHome}>
            <svg viewBox="0 0 24 24">
              <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z" />
            </svg>
          </button>

          {/* Логотип */}
          <div className="logo-wrapper animate-logo">
            <img className="poll-logo" src={logoImage} alt="Логотип" />
          </div>
        </div>

        <div className="content-wrapper">
          <h1 className="polls-title">
            Ваши контактные данные
          </h1>
          <div className="contact-form">
            <label className="contact-label">Фамилия:</label>
            <input
              type="text"
              placeholder="Введите фамилию"
              value={lastName}
              onChange={e => setLastName(e.target.value)}
            />

            <label className="contact-label">Имя:</label>
            <input
              type="text"
              placeholder="Введите имя"
              value={firstName}
              onChange={e => setFirstName(e.target.value)}
            />

            <label className="contact-label">Отчество (необязательно):</label>
            <input
              type="text"
              placeholder="Введите отчество"
              value={middleName}
              onChange={e => setMiddleName(e.target.value)}
            />

            <label className="contact-label">Город работы:</label>
            <input
              type="text"
              placeholder="Введите город"
              value={city}
              onChange={e => setCity(e.target.value)}
            />

            {contactError && (
              <div className="input-error">{contactError}</div>
            )}

            <button
              className="submit-btn"
              onClick={handleContactSubmit}
            >
              Отправить
            </button>
          </div>
        </div>
      </div>
    );
  }

  /***************************************************/
  /***  D) Шаг 1 и Шаг 2: основной UI ***/
  return (
    <div className="snp-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="polls-header">
        {/* Домой */}
        <button className="back-btn" onClick={goHome}>
          <svg viewBox="0 0 24 24">
            <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z" />
          </svg>
        </button>

        {/* Логотип */}
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип" />
        </div>

        {/* Кнопка «Далее/Продолжить» (активна в зависимости от шага и валидаций) */}
        <button
          className="forward-btn"
          onClick={step === 1 ? handleEmailNext : handleNext}
          disabled={step === 1 ? !emailValid : !canGoNext()}
        >
          <span className="arrow-icon">→</span>
        </button>
      </div>

      <div className="content-wrapper">
        {step === 1 && (
          <>
            <div className="email-step">
              <label className="email-label">Корпоративная почта ВТБ:</label>
              <input
                type="email"
                placeholder="example@vtb.ru"
                value={email}
                onChange={e => setEmail(e.target.value)}
              />
            </div>
            {emailError && <div className="input-error">{emailError}</div>}
            <button
              className="polls-btn"
              onClick={handleEmailNext}
              disabled={!emailValid}
            >
              Продолжить
            </button>
          </>
        )}

        {step === 2 && (
          <>
            <h1 className="polls-title">
              Расчёт по программе «Стратегия на пять. Гарант»
            </h1>

            <div className="date-section">
              <label className="section-label">Дата рождения:</label>
              <DateWheelPicker
                value={birthDate}
                onChange={val => {
                  if (val instanceof Date) {
                    setBirthDate(val);
                  } else if (val && typeof val.day !== 'undefined') {
                    setBirthDate(new Date(val.year, val.month - 1, val.day));
                  }
                }}
              />
            </div>

            <div className="section-label">Выберите пол:</div>
            <div className="gender-section">
              <button
                className={`insurance-btn ${gender === 'мужской' ? 'selected' : ''}`}
                onClick={() => setGender('мужской')}
              >
                <div>Мужской</div>
                <div className="emoji">👨</div>
              </button>
              <button
                className={`insurance-btn ${gender === 'женский' ? 'selected' : ''}`}
                onClick={() => setGender('женский')}
              >
                <div>Женский</div>
                <div className="emoji">👩</div>
              </button>
            </div>

            <div className="section-label">Расчёт от…</div>
            <div className="insurance-options">
              <button
                className={`insurance-btn ${calcType === 'premium' ? 'selected' : ''}`}
                onClick={() => setCalcType('premium')}
              >
                <div>Страхового</div>
                <div>взноса</div>
              </button>
              <button
                className={`insurance-btn ${calcType === 'sum' ? 'selected' : ''}`}
                onClick={() => setCalcType('sum')}
              >
                <div>Страховой</div>
                <div>суммы</div>
              </button>
            </div>

            {calcType && (
              <>
                <div className="section-label">Сумма (₽):</div>
                <div className="sum-section">
                  <input
                    type="text"
                    placeholder={calcType === 'premium' ? 'Мин. 60 000' : 'Мин. 450 000'}
                    value={amountDisplay}
                    onChange={onAmountChange}
                  />
                </div>
                {amountError && <div className="input-error">{amountError}</div>}
              </>
            )}
          </>
        )}
      </div>
    </div>
  );
}
