import React, { useState, useEffect, useRef } from 'react';
import { useSwipeable } from 'react-swipeable';
import Autosuggest from 'react-autosuggest';
import ScrollIntoView from 'react-scroll-into-view-if-needed';
import logo from './components/logo.png';
import backgroundImage from './components/background.png';
import addSpeakerIcon from './components/icons/партнер.png'; // Иконка для "Добавить спикера"
import partners from './components/autosuggest/partners.json';
import speakersData from './components/autosuggest/speakers.json';
import happyGif from './components/animations/happy.gif';
import loyalGif from './components/animations/loyal.gif';
import sadGif from './components/animations/sad.gif';

import './FeedbackPage.css';

const FeedbackPage = () => {
  /********** Шаг 1: Ввод партнёра **********/
  const [partner, setPartner] = useState('');
  const [partnerSuggestions, setPartnerSuggestions] = useState([]);

  const getPartnerSuggestions = (value) => {
    const inputValue = (value || '').trim().toLowerCase();
    if (inputValue.length < 2) return [];
    return partners.filter(
      (p) => typeof p === 'string' && p.toLowerCase().includes(inputValue)
    );
  };

  const handlePartnerChange = (e, { newValue }) => {
    setPartner(newValue);
  };

  const partnerInputProps = {
    placeholder: 'Введите название партнёра (например, ВТБ)',
    value: partner,
    onChange: handlePartnerChange,
    autoComplete: 'off',
    onFocus: handleFocus,
    onBlur: handleBlur,
  };

  /********** Шаг 2: Ввод ФИО спикера(ов) **********/
  const [speakersList, setSpeakersList] = useState([{ fullName: '', id: 1 }]);
  const [speakersSuggestions, setSpeakersSuggestions] = useState([]);

  const getSpeakerSuggestions = (value) => {
    const inputValue = (value || '').trim().toLowerCase();
    if (inputValue.length < 2) return [];
    return speakersData.filter(
      (s) => typeof s === 'string' && s.toLowerCase().includes(inputValue)
    );
  };

  const handleSpeakerChange = (index, e, { newValue }) => {
    const updated = [...speakersList];
    updated[index].fullName = newValue;
    setSpeakersList(updated);
  };

  const handleAddSpeaker = () => {
    setSpeakersList([...speakersList, { fullName: '', id: speakersList.length + 1 }]);
  };

  const speakerInputProps = (index) => ({
    placeholder: 'Введите ФИО спикера (например, Иванов Иван)',
    value: speakersList[index].fullName,
    onChange: (e, { newValue }) => handleSpeakerChange(index, e, { newValue }),
    autoComplete: 'off',
    onFocus: handleFocus,
    onBlur: handleBlur,
  });

  const onSpeakersFetchRequested = ({ value }) => {
    const suggestions = getSpeakerSuggestions(value);
    setSpeakersSuggestions(suggestions);
  };
  const onSpeakersClearRequested = () => setSpeakersSuggestions([]);

  /********** Шаг 3: Оценка качеств спикера **********/
  const qualityRows = [
    { positive: 'Экспертный', negative: 'Компетентный' },
    { positive: 'Энергичный', negative: 'Пассивный' },
    { positive: 'Мотивирующий', negative: 'Невдохновляющий' },
    { positive: 'Харизматичный', negative: 'Душный' },
    { positive: 'Доступно доносит информацию', negative: 'Говорит сложными терминами' },
  ];
  const [speakerQualities, setSpeakerQualities] = useState([]);
  const [currentSpeakerIndex, setCurrentSpeakerIndex] = useState(0);

  useEffect(() => {
    setSpeakerQualities(speakersList.map(() => Array(5).fill(null)));
  }, [speakersList]);

  const handleQualitySelectForSpeaker = (rowIndex, type) => {
    const updated = [...speakerQualities];
    updated[currentSpeakerIndex][rowIndex] =
      updated[currentSpeakerIndex][rowIndex] === type ? null : type;
    setSpeakerQualities(updated);
  };

  /********** Шаг 4: Фактическая ценность информации **********/
  const [usefulness, setUsefulness] = useState('');

  /********** Шаг 5: Яркие мысли **********/
  const [brightThoughts, setBrightThoughts] = useState('');

  /********** Шаг 6: Дополнительные предложения **********/
  const additionalOptions = [
    'Практики',
    'Клиентских историй',
    'Статистических данных',
    'Фишек продаж',
    "Перерывов / 'Кофе'-пауз",
    'Разбор продуктовой линейки',
    'Детальный разбор параметров продукта',
    'Ящик Prosecco',
  ];
  const [additionalSelections, setAdditionalSelections] = useState([]);
  const toggleAdditionalSelection = (option) => {
    if (additionalSelections.includes(option)) {
      setAdditionalSelections(additionalSelections.filter((item) => item !== option));
    } else {
      setAdditionalSelections([...additionalSelections, option]);
    }
  };

  /********** Шаг 7: Общее впечатление от тренинга **********/
  const [impression, setImpression] = useState('');

  /********** Подъём содержимого при открытии клавиатуры **********/
  const containerRef = useRef(null);
  const nextButtonRef = useRef(null);

  function handleFocus() {
    if (containerRef.current) {
      containerRef.current.classList.add('keyboard-up');
    }
    setTimeout(() => {
      if (nextButtonRef.current) {
        nextButtonRef.current.scrollIntoView({ behavior: 'smooth', block: 'end' });
      }
    }, 300);
  }

  function handleBlur() {
    if (containerRef.current) {
      containerRef.current.classList.remove('keyboard-up');
    }
  }

  /********** Логика шагов **********/
  const [currentStep, setCurrentStep] = useState(1);
  const [isFinished, setIsFinished] = useState(false);
  const [isFinishClicked, setIsFinishClicked] = useState(false);

  const canGoNext = () => {
    switch (currentStep) {
      case 1:
        return partner.trim() !== '' && partners.includes(partner);
      case 2:
        return speakersList.every(
          (s) => s.fullName.trim() !== '' && speakersData.includes(s.fullName)
        );
      case 3:
        return speakerQualities[currentSpeakerIndex].every((q) => q !== null);
      case 4:
        return usefulness.trim() !== '';
      case 5:
        return brightThoughts.trim().length >= 5;
      case 7:
        return impression.trim() !== '';
      default:
        return true;
    }
  };

  const handleNext = () => {
    if (!canGoNext()) {
      alert('Не выполнены условия для перехода к следующему шагу');
      return;
    }
    if (currentStep === 3 && currentSpeakerIndex < speakersList.length - 1) {
      setCurrentSpeakerIndex((prev) => prev + 1);
      return;
    }
    setCurrentStep((prev) => prev + 1);
  };

  const handlePrev = () => {
    if (currentStep > 1) {
      if (currentStep === 3 && currentSpeakerIndex > 0) {
        setCurrentSpeakerIndex((prev) => prev - 1);
        return;
      }
      setCurrentStep((prev) => prev - 1);
    }
  };

  const handleFinish = async () => {
    if (!canGoNext()) {
      alert('Не выполнены условия для завершения опроса');
      return;
    }
    if (isFinishClicked) return;
    setIsFinishClicked(true);
    setIsFinished(true);

    // Собираем положительные/отрицательные качества
    const positiveQualitiesList = [];
    const negativeQualitiesList = [];
    speakersList.forEach((speaker, idx) => {
      qualityRows.forEach((row, j) => {
        const selection = speakerQualities[idx][j];
        if (selection === 'positive') {
          positiveQualitiesList.push(`${row.positive} (${speaker.fullName})`);
        } else if (selection === 'negative') {
          negativeQualitiesList.push(`${row.negative} (${speaker.fullName})`);
        }
      });
    });

    let impressionStr = '';
    if (impression === 'happy') impressionStr = 'Круто!';
    else if (impression === 'neutral') impressionStr = 'Средне';
    else if (impression === 'sad') impressionStr = 'Плохо';

    const data = {
      partner,
      dateTime: new Date().toISOString(),
      answers: {
        speakers: speakersList.map((s) => s.fullName).join(', '),
        positiveQualities: positiveQualitiesList.join(', '),
        negativeQualities: negativeQualitiesList.join(', '),
        usefulness,
        brightThoughts,
        additionalSuggestions: additionalSelections.join(', '),
        impression: impressionStr,
      },
    };

    try {
      const response = await fetch('https://rgszh-miniapp.org/api/proxy/feedback/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
        credentials: 'include',
      });
      if (!response.ok) {
        alert('Ошибка при сохранении данных');
      }
    } catch (error) {
      console.error('Ошибка при отправке данных:', error);
      alert('Ошибка соединения с сервером');
    }
  };

  /********** Свайпы для перехода между шагами **********/
  const swipeHandlers = useSwipeable({
    onSwipedLeft: () => handlePrev(),
    onSwipedRight: () => {
      if (canGoNext()) handleNext();
    },
  });

  /********** Рендеринг шагов **********/
  const renderStep = () => {
    switch (currentStep) {
      case 1:
        return (
          <div className="autosuggest-container">
            <h2>Введите название партнёра</h2>
            <Autosuggest
              suggestions={partnerSuggestions}
              onSuggestionsFetchRequested={({ value }) =>
                setPartnerSuggestions(getPartnerSuggestions(value))
              }
              onSuggestionsClearRequested={() => setPartnerSuggestions([])}
              getSuggestionValue={(suggestion) => suggestion}
              renderSuggestion={(suggestion) => (
                <div
                  style={{
                    textAlign: 'center',
                    backgroundColor: '#fff',
                    color: '#000',
                    fontSize: '16px',
                  }}
                >
                  {suggestion}
                </div>
              )}
              inputProps={partnerInputProps}
            />
          </div>
        );

      case 2:
        return (
          <div>
            <h2>Фамилия и имя</h2>
            {speakersList.map((speaker, index) => (
              <div key={speaker.id} className="speaker-row">
                <Autosuggest
                  suggestions={speakersSuggestions}
                  onSuggestionsFetchRequested={({ value }) =>
                    setSpeakersSuggestions(getSpeakerSuggestions(value))
                  }
                  onSuggestionsClearRequested={onSpeakersClearRequested}
                  getSuggestionValue={(suggestion) => suggestion}
                  renderSuggestion={(suggestion) => (
                    <div
                      style={{
                        textAlign: 'center',
                        backgroundColor: '#fff',
                        color: '#000',
                        fontSize: '16px',
                      }}
                    >
                      {suggestion}
                    </div>
                  )}
                  inputProps={speakerInputProps(index)}
                />
                {index === speakersList.length - 1 && (
                  <button type="button" className="add-speaker-btn" onClick={handleAddSpeaker}>
                    <span className="add-speaker-symbol">+</span>
                  </button>
                )}
              </div>
            ))}
          </div>
        );

      case 3:
        return (
          <div>
            <h2>{speakersList[currentSpeakerIndex].fullName}</h2>
            <div style={{ marginBottom: '10px' }}>
              {/* Можно добавить подсказку/текст, если нужно */}
            </div>
            {qualityRows.map((row, idx) => (
              <div key={idx} className="quality-row">
                <button
                  className={`quality-btn ${
                    speakerQualities[currentSpeakerIndex][idx] === 'positive' ? 'green' : ''
                  }`}
                  onClick={() => handleQualitySelectForSpeaker(idx, 'positive')}
                >
                  {row.positive}
                </button>
                <button
                  className={`quality-btn ${
                    speakerQualities[currentSpeakerIndex][idx] === 'negative' ? 'red' : ''
                  }`}
                  onClick={() => handleQualitySelectForSpeaker(idx, 'negative')}
                >
                  {row.negative}
                </button>
              </div>
            ))}
          </div>
        );

      case 4:
        return (
          <div>
            <h2>Фактическая ценность информации</h2>
            <div className="usefulness-container" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '15px' }}>
              <button
                className={`quality-btn ${
                  usefulness === 'Полезно в работе (буду применять на практике)' ? 'green' : ''
                }`}
                style={{ width: '100%' }}
                onClick={() => setUsefulness('Полезно в работе (буду применять на практике)')}
              >
                Полезно в работе (буду применять на практике)
              </button>
              <button
                className={`quality-btn ${
                  usefulness === 'Бесполезно в работе (с моими клиентами это не работает)' ? 'red' : ''
                }`}
                style={{ width: '100%' }}
                onClick={() => setUsefulness('Бесполезно в работе (с моими клиентами это не работает)')}
              >
                Бесполезно в работе (с моими клиентами это не работает)
              </button>
            </div>
          </div>
        );

      case 5:
        return (
          <div className="input-button-container">
            <h2>Самые яркие мысли с мероприятия</h2>
            <div className="input-button-wrapper">
              <input
                type="text"
                className="input-field"
                value={brightThoughts}
                onChange={(e) => setBrightThoughts(e.target.value)}
                placeholder="Введите текст (минимум 5 символов)"
              />
              <button
                className="input-button"
                onClick={canGoNext() ? handleNext : undefined}
              >
                Далее
              </button>
            </div>
          </div>
        );

      case 6:
        return (
          <div>
            <h2>Что стоило бы добавить в тренинг?</h2>
            {additionalOptions.map((opt, idx) => (
              <button
                key={idx}
                className={`add-option-btn ${additionalSelections.includes(opt) ? 'selected' : ''}`}
                onClick={() => toggleAdditionalSelection(opt)}
              >
                {opt}
              </button>
            ))}
          </div>
        );

      case 7:
        return (
          <div>
            <h2>Общее впечатление от тренинга</h2>
            <div className="impression-container" style={{ flexDirection: 'row', justifyContent: 'center' }}>
              <button
                className={`impression-btn ${impression === 'happy' ? 'happy-selected' : ''}`}
                onClick={() => setImpression('happy')}
              >
                <img src={happyGif} alt="Happy" style={{ width: '20vw', borderRadius: '10px' }} />
              </button>
              <button
                className={`impression-btn ${impression === 'neutral' ? 'neutral-selected' : ''}`}
                onClick={() => setImpression('neutral')}
              >
                <img src={loyalGif} alt="Neutral" style={{ width: '20vw', borderRadius: '10px' }} />
              </button>
              <button
                className={`impression-btn ${impression === 'sad' ? 'sad-selected' : ''}`}
                onClick={() => setImpression('sad')}
              >
                <img src={sadGif} alt="Sad" style={{ width: '20vw', borderRadius: '10px' }} />
              </button>
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  if (isFinished) {
    return (
      <div
        className="thank-you-message"
        style={{
          backgroundImage: `url(${backgroundImage})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
        }}
      >
        <h2>Благодарим за прохождение опроса!</h2>
      </div>
    );
  }

  return (
    <div
      {...swipeHandlers}
      ref={containerRef}
      className="container page-content"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      <div className="logo">
        <img src={logo} alt="Логотип" className="logo-image" />
      </div>

      {renderStep()}

      {currentStep !== 5 && (
        <ScrollIntoView options={{ behavior: 'smooth', block: 'end' }}>
          {currentStep < 7 ? (
            <button
              ref={nextButtonRef}
              className={`full-width-btn ${canGoNext() ? 'enabled' : 'disabled'}`}
              onClick={canGoNext() ? handleNext : undefined}
            >
              Далее
            </button>
          ) : (
            <button
              ref={nextButtonRef}
              className={`full-width-btn ${canGoNext() ? 'enabled' : 'disabled'}`}
              onClick={canGoNext() ? handleFinish : undefined}
            >
              Завершить опрос
            </button>
          )}
        </ScrollIntoView>
      )}
    </div>
  );
};

export default FeedbackPage;
