import React, { useEffect, useState, useLayoutEffect } from 'react';
import { useSwipeable } from 'react-swipeable';
import { useNavigate } from 'react-router-dom';
import backgroundImage from './components/background.png';
import assessmentQR from './components/QR/assessment.png'; // пример иконки для QR
import './PollsPage.css';

const PollsPage = () => {
  const navigate = useNavigate();
  const [qrModal, setQrModal] = useState(null);
  const [qrTitle, setQrTitle] = useState('');
  const [isQRLoading, setIsQRLoading] = useState(true);

  // При заходе на страницу скроллим вверх
  useLayoutEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  useEffect(() => {
    setTimeout(() => {
      window.scrollTo(0, 0);
    }, 100);
  }, []);

  // Обработчики свайпа
  const swipeHandlers = useSwipeable({
    onSwipedLeft: () => navigate('/register'), // пример: свайп влево переходит на /register
  });

  // Пример данных пользователя
  const user = {
    isRegistered: true,
    accessLevel: 1, // 1 — допустим, может генерировать QR
  };

  // Функция генерации URL для QR-кода (через quickchart.io)
  const getQRCodeUrl = (targetUrl) => {
    return `https://quickchart.io/qr?text=${encodeURIComponent(targetUrl)}&size=200`;
  };

  // Список опросов
  const polls = [
    { label: 'Оценка кандидата', path: '/assessment' },
    { label: 'Обратная связь', path: '/feedback' },
  ];

  // Клик по иконке QR
  const handleQRClick = (poll) => {
    const fullUrl = window.location.origin + poll.path;
    const qrUrl = getQRCodeUrl(fullUrl);
    setQrTitle(poll.label);
    setIsQRLoading(true);
    // небольшая задержка, потом показываем
    setTimeout(() => {
      setQrModal(qrUrl);
    }, 100);
  };

  return (
    <div
      className="container-polls"
      style={{ backgroundImage: `url(${backgroundImage})` }}
      {...swipeHandlers}
    >
      <h2>Список опросов</h2>

      {polls.map((poll, index) => (
        <div key={index} className="poll-container">
          {/* Кнопка с названием опроса */}
          <button
            className="poll-btn"
            onClick={() => navigate(poll.path)}
          >
            <span className="button-text">{poll.label}</span>
          </button>

          {/* Кнопка с QR (доступна, если user.accessLevel === 1) */}
          {user.isRegistered && user.accessLevel === 1 && (
            <button className="qr-btn" onClick={() => handleQRClick(poll)}>
              <img
                src={assessmentQR}
                alt="QR Code"
                className="button-icon"
              />
            </button>
          )}
        </div>
      ))}

      {/* Модальное окно с QR-кодом, если пользователь кликнул на иконку */}
      {qrModal && (
        <div
          className="qr-modal-overlay"
          onClick={() => setQrModal(null)}
        >
          <div className="qr-modal-content">
            <h3 className="qr-title">{qrTitle}</h3>
            <div
              className="qr-image-wrapper"
              style={{
                position: 'relative',
                width: '280px',
                height: '280px',
              }}
            >
              {isQRLoading && (
                <svg className="spinner" viewBox="0 0 50 50">
                  <circle
                    className="path"
                    cx="25"
                    cy="25"
                    r="20"
                    fill="none"
                    strokeWidth="5"
                  />
                </svg>
              )}
              <img
                src={qrModal}
                alt="QR Code"
                className="qr-image"
                onLoad={() => setIsQRLoading(false)}
                style={isQRLoading ? { display: 'none' } : {}}
              />
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PollsPage;
