import React from 'react';
import { useNavigate } from 'react-router-dom';
import logo from './components/logo.png';
import techsupportIcon from './components/icons/техподдержка.png';
import icon1 from './components/icons/настройки.png';
import icon2 from './components/icons/база.png';
import icon3 from './components/icons/рейтинг.png';
import icon4 from './components/icons/чат.png';
import backgroundImage from './components/background.png';
import NewsTicker from './NewsTicker'; // Импортируем компонент ленты новостей
import './MainMenu.css';

const MainMenu = () => {
  const navigate = useNavigate();

  const handlePolls = () => {
    navigate('/polls');
  };

  const handleProfile = () => {
    navigate('/profile');
  };

  const handleNavigate = (route) => {
    navigate(route);
  };

  const handleTechSupport = () => {
    navigate('/support');
  };

  return (
    <div
      className="mainmenu-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      <div className="logo-mainmenu">
        <img src={logo} alt="Logo" className="logo-image" />
      </div>
      <NewsTicker />
      <h1 className="mainmenu-title">Главное Меню</h1>
      <div className="mainmenu-buttons">
        <button className="mainmenu-btn" onClick={handlePolls}>
          Опросы
        </button>
        <button className="mainmenu-btn" onClick={handleProfile}>
          Профиль
        </button>
      </div>
      <div className="bottom-container">
        <button className="tech-support-btn" onClick={handleTechSupport}>
          <img src={techsupportIcon} alt="Техподдержка" className="tech-support-icon" />
        </button>
        <button className="tech-support-btn" onClick={() => handleNavigate('/ProfilePage')}>
          <img src={icon1} alt="Настройки" className="tech-support-icon" />
        </button>
        <button className="tech-support-btn" onClick={() => handleNavigate('/KnowledgePage')}>
          <img src={icon2} alt="База" className="tech-support-icon" />
        </button>
        <button className="tech-support-btn" onClick={() => handleNavigate('/RaitingPage')}>
          <img src={icon3} alt="Рейтинг" className="tech-support-icon" />
        </button>
        <button className="tech-support-btn" onClick={() => handleNavigate('/ChatPage')}>
          <img src={icon4} alt="Чат" className="tech-support-icon" />
        </button>
      </div>
    </div>
  );
};

export default MainMenu;
