import React, { useState } from 'react';
import MobilePicker from 'react-mobile-picker';

const DateWheelPicker = ({ onChange }) => {
  const [pickerValue, setPickerValue] = useState({
    day: '01',
    month: '01',
    year: '2000'
  });

  const dataSource = {
    day: Array.from({ length: 31 }, (_, i) =>
      (i + 1).toString().padStart(2, '0')
    ),
    month: Array.from({ length: 12 }, (_, i) =>
      (i + 1).toString().padStart(2, '0')
    ),
    year: Array.from({ length: 100 }, (_, i) =>
      (2025 - i).toString()
    )
  };

  const handleChange = (name, value) => {
    const newValue = { ...pickerValue, [name]: value };
    setPickerValue(newValue);
    // Форматируем дату в виде "dd/mm/yyyy"
    const dateString = `${newValue.day}/${newValue.month}/${newValue.year}`;
    onChange(dateString);
  };

  return (
    <MobilePicker
      dataSource={dataSource}
      value={pickerValue}
      onChange={handleChange}
    />
  );
};

export default DateWheelPicker;
