import React from 'react';
import { useNavigate } from 'react-router-dom';
import backgroundImage from './components/background.png';
import logoImage       from './components/logo.png';
import './MainMenu.css';

const MainMenu = () => {
  const navigate = useNavigate();

  const handlePolls    = () => navigate('/polls');
  const handleProfile  = () => navigate('/profile');
  const handleEmployee = () => navigate('/employee');

  return (
    <div
      className="mainmenu-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* Логотип сверху */}
      <div className="mainmenu-logo-container">
        <img src={logoImage} alt="Логотип" className="mainmenu-logo" />
      </div>

      <div className="mainmenu-buttons">
        <button className="mainmenu-btn" onClick={handlePolls}>
          Опросы
        </button>
        <button className="mainmenu-btn" onClick={handleProfile}>
          Профиль
        </button>
        <button className="mainmenu-btn" onClick={handleEmployee}>
          Сотруднику
        </button>
      </div>
    </div>
  );
};

export default MainMenu;
