// src/SNPPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import mainmenuIcon from './components/icons/mainmenu.png';
import logoImage from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';
import './SNPPage.css';

const SNPPage = () => {
  const navigate = useNavigate();

  // Порядок шагов
  const [step, setStep] = useState(1);

  // Шаг 1: почта
  const [email, setEmail] = useState('');
  const [emailError, setEmailError] = useState('');

  // Шаг 2: остальные данные
  const [birthDate, setBirthDate] = useState(null);
  const [gender, setGender] = useState(null);
  const [calcType, setCalcType] = useState(null);
  const [amount, setAmount] = useState('');
  const [amountError, setAmountError] = useState('');

  // Состояния обработки и результата
  const [isProcessing, setIsProcessing] = useState(false);
  const [resultData, setResultData] = useState(null);

  // Форматирование суммы с разделителем тысяч
  const formatSum = (raw) => {
    let digits = raw.replace(/\D/g, '');
    if (!digits) return '';
    let out = '';
    while (digits.length > 3) {
      out = '.' + digits.slice(-3) + out;
      digits = digits.slice(0, -3);
    }
    return digits + out;
  };

  // Подсчёт возраста
  const getAge = (date) => {
    if (!(date instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - date.getFullYear();
    if (
      today.getMonth() < date.getMonth() ||
      (today.getMonth() === date.getMonth() && today.getDate() < date.getDate())
    ) {
      age--;
    }
    return age;
  };

  // Проверка минимальной суммы
  const isAmountValid = () => {
    const numeric = parseInt(amount.replace(/\./g, ''), 10) || 0;
    return (
      (calcType === 'premium' && numeric >= 60000) ||
      (calcType === 'sum' && numeric >= 450000)
    );
  };

  // Готовность к финальному запросу
  const canGoNext = () => {
    return (
      birthDate instanceof Date &&
      getAge(birthDate) >= 18 &&
      gender &&
      calcType &&
      amount &&
      isAmountValid()
    );
  };

  // Обработчик шага 1 – проверка почты
  const handleEmailNext = () => {
    if (!email.toLowerCase().endsWith('@vtb.ru')) {
      setEmailError('Используйте корпоративную почту домена @vtb.ru');
      return;
    }
    setEmailError('');
    // Сохраняем email (если нужно, в localStorage или в контексте)
    // localStorage.setItem('snpEmail', email);
    setStep(2);
  };

  // Обработчик изменения суммы
  const handleAmountChange = (e) => {
    const formatted = formatSum(e.target.value);
    setAmount(formatted);
    const numeric = parseInt(formatted.replace(/\./g, ''), 10) || 0;
    if (calcType === 'premium') {
      setAmountError(numeric < 60000 ? 'Введите сумму от 60 000 ₽' : '');
    } else if (calcType === 'sum') {
      setAmountError(numeric < 450000 ? 'Введите сумму от 450 000 ₽' : '');
    }
  };

  // Финальный запрос на расчёт
  const handleNext = async () => {
    if (!canGoNext()) return;

    const dateStr = birthDate.toLocaleDateString('ru-RU', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
    const age = getAge(birthDate);
    const payload = {
      email,                   // добавили email
      birthdate: dateStr,
      age,
      gender,
      payment: calcType === 'premium' ? 'Страхового взноса' : 'Страховой суммы',
      sum: parseInt(amount.replace(/\./g, ''), 10) || 0
    };

    try {
      setIsProcessing(true);
      const res = await fetch('/api/proxy/snp/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const data = await res.json();
      setIsProcessing(false);
      if (res.ok) {
        setResultData(data);
      } else {
        console.error('Ошибка сервера:', data);
      }
    } catch (err) {
      setIsProcessing(false);
      console.error('Ошибка запроса:', err);
    }
  };

  // Кнопка «Домой»
  const goHome = () => navigate('/MainMenu');

  return (
    <div
      className="snp-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* Кнопка «Домой» */}
      <button className="back-btn" onClick={goHome}>
        <img src={mainmenuIcon} alt="Домой" />
      </button>

      {/* Лого */}
      <div className="logo-container">
        <img src={logoImage} alt="Логотип" />
      </div>

      <h1 className="snp-title">
        Расчёт по программе «Стратегия на пять. Гарант»
      </h1>

      {/* ===== Шаг 1: ввод почты ===== */}
      {!isProcessing && !resultData && step === 1 && (
        <>
          <div className="form-step email-step">
            <label>Корпоративная почта ВТБ:</label>
            <input
              type="email"
              placeholder="example@vtb.ru"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            {emailError && <div className="error-text">{emailError}</div>}
          </div>
          <button
            className="primary-btn"
            onClick={handleEmailNext}
          >
            Далее
          </button>
        </>
      )}

      {/* ===== Шаг 2: дата, пол, тип расчёта, сумма ===== */}
      {!isProcessing && !resultData && step === 2 && (
        <>
          <div className="form-step data-step">
            <label>Дата рождения:</label>
            <DateWheelPicker
              onChange={(val) => {
                if (val instanceof Date) {
                  setBirthDate(val);
                } else if (val && val.day) {
                  setBirthDate(
                    new Date(val.year, val.month - 1, val.day)
                  );
                }
              }}
            />
          </div>

          <div className="gender-selector">
            <button
              className={`insurance-btn ${gender === 'male' ? 'selected' : ''}`}
              onClick={() => setGender('male')}
            >
              Мужской
            </button>
            <button
              className={`insurance-btn ${gender === 'female' ? 'selected' : ''}`}
              onClick={() => setGender('female')}
            >
              Женский
            </button>
          </div>

          <div className="insurance-options">
            <button
              className={`insurance-btn ${calcType === 'premium' ? 'selected' : ''}`}
              onClick={() => {
                setCalcType('premium');
                setAmount('');
                setAmountError('');
              }}
            >
              Страхового взноса
            </button>
            <button
              className={`insurance-btn ${calcType === 'sum' ? 'selected' : ''}`}
              onClick={() => {
                setCalcType('sum');
                setAmount('');
                setAmountError('');
              }}
            >
              Страховой суммы
            </button>
          </div>

          {calcType && (
            <div className="form-step">
              <label>Введите сумму в рублях</label>
              <input
                type="text"
                placeholder={calcType === 'premium' ? 'Минимум 60 000' : 'Минимум 450 000'}
                value={amount}
                onChange={handleAmountChange}
              />
              {amountError && <div className="error-text">{amountError}</div>}
            </div>
          )}

          <button
            className="primary-btn"
            onClick={handleNext}
            disabled={!canGoNext()}
          >
            Перейти к расчёту
          </button>
        </>
      )}

      {/* ===== Индикатор обработки ===== */}
      {isProcessing && (
        <div className="processing-container">
          <p>Выполняется расчёт...</p>
          <div className="arrow-animation">→</div>
        </div>
      )}

      {/* ===== Результаты ===== */}
      {resultData && (
        <div className="result-container">
          <h2>Результаты расчёта</h2>
          <p><strong>Возраст:</strong> {resultData.age} лет</p>
          <p><strong>Пол:</strong> {resultData.gender}</p>
          <p><strong>Тип расчёта:</strong> {resultData.payment}</p>
          <p><strong>Сумма:</strong> {formatSum(resultData.sum.toString())} ₽</p>
          <p><strong>Итоговая премия:</strong> {formatSum((resultData.totalPremium||0).toString())} ₽</p>
        </div>
      )}
    </div>
  );
};

export default SNPPage;
