import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSwipeable } from 'react-swipeable';
import backgroundImage from './components/background.png';
import logoImage from './components/logo.png';
import './WelcomePage.css';

const WelcomePage = () => {
  const navigate = useNavigate();
  const [animateLogo, setAnimateLogo] = useState(false);
  const [animateText, setAnimateText] = useState(false);
  const [greeting, setGreeting] = useState('');

  useEffect(() => {
    // Выбираем приветствие по времени суток
    const hour = new Date().getHours();
    let text = '';
    if (hour >= 6 && hour < 11) text = 'Доброе утро';
    else if (hour >= 11 && hour < 17) text = 'Добрый день';
    else if (hour >= 17 && hour < 24) text = 'Добрый вечер';
    else text = 'Доброй ночи';
    setGreeting(text);

    // Стартуем анимации
    setTimeout(() => setAnimateLogo(true), 100);
    setTimeout(() => setAnimateText(true), 500);

    // Переход на главное меню через 3 секунды
    const timer = setTimeout(() => navigate('/main-menu'), 3000);
    return () => clearTimeout(timer);
  }, [navigate]);

  const swipeHandlers = useSwipeable({ preventDefault: true });

  return (
    <div
      className="mainmenu-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
      {...swipeHandlers}
    >
      {/* полупрозрачный контейнер для логотипа */}
      <div className={`logo-wrapper ${animateLogo ? 'animate-logo' : ''}`}>
        <img src={logoImage} alt="Логотип РГС Жизнь" className="mainmenu-logo" />
      </div>

      {/* обёртка для текста-приветствия */}
      <div className={`text-wrapper ${animateText ? 'animate-text' : ''}`}>
        <h1 className="welcome-title">{greeting}</h1>
      </div>
    </div>
  );
};

export default WelcomePage;
