import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import backgroundImage from './components/background.png';
import logoImage from './components/logo.png';
import DateWheelPicker from './DateWheelPicker';
import './SNPPage.css';

const SNPPage = () => {
  const navigate = useNavigate();

  // Шаг 1: ввод корпоративной почты
  const [email, setEmail] = useState('');
  const [emailError, setEmailError] = useState('');

  // Шаг 2−N: остальные поля
  const [birthDate, setBirthDate] = useState(null);
  const [gender, setGender] = useState(null);
  const [calcType, setCalcType] = useState(null);
  const [amount, setAmount] = useState('');
  const [amountError, setAmountError] = useState('');
  const [step, setStep] = useState(1);

  // Проверка корпоративной почты и переход к основному шагу
  const handleProceed = () => {
    if (!email.endsWith('@vtb.ru')) {
      setEmailError('Домен вашей почты не является корпоративным. Попробуйте ещё раз.');
      return;
    }
    setEmailError('');
    setStep(2);
  };

  // Утилиты для суммы
  const formatSum = raw => {
    let digits = raw.replace(/\D/g, '');
    if (!digits) return '';
    return digits.replace(/\B(?=(\d{3})+(?!\d))/g, '.');
  };
  const handleAmountChange = e => {
    const formatted = formatSum(e.target.value);
    setAmount(formatted);

    const numeric = parseInt(formatted.replace(/\./g, ''), 10) || 0;
    if (calcType === 'premium') {
      setAmountError(numeric < 60000 ? 'Введите сумму от 60 000 ₽' : '');
    } else if (calcType === 'sum') {
      setAmountError(numeric < 450000 ? 'Введите сумму от 450 000 ₽' : '');
    }
  };

  // Проверка возможности перейти дальше
  const canGoNext = () => {
    const num = parseInt(amount.replace(/\./g, ''), 10) || 0;
    return (
      birthDate instanceof Date &&
      gender &&
      calcType &&
      num >= (calcType === 'premium' ? 60000 : 450000)
    );
  };

  // Обработчик «Далее» после заполнения данных
  const handleNext = () => {
    if (!canGoNext()) return;
    // тут ваш fetch('/api/snp/save', ...)
    setStep(3);
  };

  return (
    <div className="snp-container">
      {/* Back */}
      <button className="back-btn" onClick={() => navigate(-1)} aria-label="Назад">
        <svg width="24" height="24" viewBox="0 0 24 24">
          <path d="M15 18l-6-6 6-6" stroke="#fff" strokeWidth="2" fill="none" strokeLinecap="round"/>
        </svg>
      </button>

      {/* Логотип */}
      <div className="logo-container">
        <img src={logoImage} alt="РГС Жизнь" />
      </div>

      {/* Заголовок */}
      <h1 className="snp-title">Расчёт по программе «Стратегия на пять. Гарант»</h1>

      {/* Шаг 1: email */}
      {step === 1 && (
        <div className="form-step email-step">
          <label>Корпоративная почта ВТБ:</label>
          <input
            type="email"
            value={email}
            onChange={e => setEmail(e.target.value)}
            placeholder="name@vtb.ru"
          />
          {emailError && <div className="error-text">{emailError}</div>}
          <button className="primary-btn" onClick={handleProceed}>
            Перейти к расчёту
          </button>
        </div>
      )}

      {/* Шаг 2: ввод даты, пола, типа расчёта и суммы */}
      {step === 2 && (
        <div className="form-step data-step">
          <label>Дата рождения:</label>
          <DateWheelPicker
            onChange={val => {
              if (val instanceof Date) setBirthDate(val);
              else {
                const [d, m, y] = ('' + val).split('.').map(Number);
                setBirthDate(new Date(y, m - 1, d));
              }
            }}
          />

          <label>Пол:</label>
          <div className="gender-selector">
            {['male', 'female'].map(g => (
              <button
                key={g}
                className={`insurance-btn ${gender === g ? 'selected' : ''}`}
                onClick={() => setGender(g)}
              >
                {g === 'male' ? 'Мужской' : 'Женский'}
              </button>
            ))}
          </div>

          <label>Расчёт от…</label>
          <div className="insurance-options">
            <button
              className={`insurance-btn ${calcType === 'premium' ? 'selected' : ''}`}
              onClick={() => { setCalcType('premium'); setAmount(''); setAmountError(''); }}
            >
              Страхового взноса
            </button>
            <button
              className={`insurance-btn ${calcType === 'sum' ? 'selected' : ''}`}
              onClick={() => { setCalcType('sum'); setAmount(''); setAmountError(''); }}
            >
              Страховой суммы
            </button>
          </div>

          {calcType && (
            <>
              <label>Введите сумму в рублях</label>
              <input
                type="text"
                value={amount}
                onChange={handleAmountChange}
                placeholder={calcType === 'premium' ? 'Минимум 60 000' : 'Минимум 450 000'}
              />
              {amountError && <div className="error-text">{amountError}</div>}
            </>
          )}

          <button
            className="primary-btn"
            disabled={!canGoNext()}
            onClick={handleNext}
          >
            Далее
          </button>
        </div>
      )}

      {/* Шаг 3: результат */}
      {step === 3 && (
        <div className="form-step result-step">
          {/* … ваш вывод результатных данных … */}
        </div>
      )}
    </div>
  );
};

export default SNPPage;
