import React, { useState, useEffect, useRef } from 'react';
import { useSwipeable } from 'react-swipeable';
import Autosuggest from 'react-autosuggest';
import ScrollIntoView from 'react-scroll-into-view-if-needed';
import logo from './components/logo.png';
import backgroundImage from './components/background.png';
import particleGif from './components/animations/particles.gif';
import partners from './components/autosuggest/partners.json';
import speakersData from './components/autosuggest/speakers.json';
import addSpeakerIcon from './components/icons/партнер.png';
import './FeedbackPage.css';

const FeedbackPage = () => {
  // ---------- Шаг 1: Партнёр ----------
  const [partner, setPartner] = useState('');
  const [partnerSuggestions, setPartnerSuggestions] = useState([]);

  const getPartnerSuggestions = (value) => {
    const inputValue = (value || '').trim().toLowerCase();
    if (inputValue.length < 2) return [];
    return partners.filter(p => typeof p === 'string' && p.toLowerCase().includes(inputValue));
  };

  const handlePartnerChange = (e, { newValue }) => {
    setPartner(newValue);
  };

  const partnerInputProps = {
    placeholder: 'Введите название партнёра (например, ВТБ)',
    value: partner,
    onChange: handlePartnerChange,
    autoComplete: 'off'
  };

  // ---------- Шаг 2: Спикеры ----------
  const [speakersList, setSpeakersList] = useState([{ fullName: '', id: 1 }]);
  const [speakersSuggestions, setSpeakersSuggestions] = useState([]);

  const getSpeakerSuggestions = (value) => {
    const inputValue = (value || '').trim().toLowerCase();
    if (inputValue.length < 2) return [];
    return speakersData.filter(s => typeof s === 'string' && s.toLowerCase().includes(inputValue));
  };

  const handleSpeakerChange = (index, e, { newValue }) => {
    const updated = [...speakersList];
    updated[index].fullName = newValue;
    setSpeakersList(updated);
  };

  const speakerInputProps = (index) => ({
    placeholder: 'Укажите спикера',
    value: speakersList[index].fullName,
    onChange: (e, { newValue }) => handleSpeakerChange(index, e, { newValue }),
    autoComplete: 'off'
  });

  const handleAddSpeaker = () => {
    setSpeakersList([...speakersList, { fullName: '', id: speakersList.length + 1 }]);
  };

  // Функции для автодополнения спикеров (общие для всех)
  const handleFetchSpeakerSuggestions = ({ value }) => {
    const suggestions = getSpeakerSuggestions(value);
    setSpeakersSuggestions(suggestions);
  };

  const handleClearSpeakerSuggestions = () => {
    setSpeakersSuggestions([]);
  };

  // ---------- Шаг 3: Оценка качеств спикера ----------
  const qualityRows = [
    { positive: 'Экспертный', negative: 'Компетентный' },
    { positive: 'Энергичный', negative: 'Пассивный' },
    { positive: 'Мотивирующий', negative: 'Невдохновляющий' },
    { positive: 'Харизматичный', negative: 'Душный' },
    { positive: 'Доступно доносит информацию', negative: 'Говорит сложными терминами' },
  ];
  const [speakerQualities, setSpeakerQualities] = useState([]);
  const [currentSpeakerIndex, setCurrentSpeakerIndex] = useState(0);

  useEffect(() => {
    setSpeakerQualities(speakersList.map(() => Array(5).fill(null)));
  }, [speakersList]);

  const handleQualitySelectForSpeaker = (rowIndex, type) => {
    const updated = [...speakerQualities];
    updated[currentSpeakerIndex][rowIndex] = type;
    setSpeakerQualities(updated);
  };

  // ---------- Шаг 4: Фактическая ценность информации ----------
  const [usefulness, setUsefulness] = useState('');
  const handleUsefulness = (val) => setUsefulness(val);

  // ---------- Шаг 5: Самые яркие мысли с мероприятия ----------
  const [brightThoughts, setBrightThoughts] = useState('');
  const handleBrightThoughtsChange = (e) => setBrightThoughts(e.target.value);

  // ---------- Шаг 6: Что добавить в тренинг ----------
  const additionalOptions = [
    'Практики',
    'Клиентских историй',
    'Статистических данных',
    'Фишек продаж',
    "Перерывов / 'Кофе'-пауз",
    'Разбор продуктовой линейки',
    'Детальный разбор параметров продукта',
    'Ящик Prosecco',
  ];
  const [additionalSelections, setAdditionalSelections] = useState([]);
  const toggleAdditionalSelection = (option) => {
    if (additionalSelections.includes(option)) {
      setAdditionalSelections(additionalSelections.filter(item => item !== option));
    } else {
      setAdditionalSelections([...additionalSelections, option]);
    }
  };

  // ---------- Шаг 7: Общее впечатление ----------
  const [impression, setImpression] = useState('');
  const handleImpression = (val) => setImpression(val);

  // ---------- Логика шагов ----------
  const [currentStep, setCurrentStep] = useState(1);
  const [isFinished, setIsFinished] = useState(false);
  const [isFinishClicked, setIsFinishClicked] = useState(false);

  // Функция проверки, можно ли перейти к следующему шагу
  const canGoNext = () => {
    if (currentStep === 1) {
      return partner.trim() !== '' && partners.includes(partner);
    }
    if (currentStep === 2) {
      return speakersList.every(s => s.fullName.trim() !== '' && speakersData.includes(s.fullName));
    }
    if (currentStep === 3) {
      return speakerQualities[currentSpeakerIndex][0] !== null;
    }
    if (currentStep === 4) {
      return usefulness.trim() !== '';
    }
    if (currentStep === 5) {
      return brightThoughts.trim().length >= 5;
    }
    if (currentStep === 7) {
      return impression.trim() !== '';
    }
    return true;
  };

  const handleNext = () => {
    if (!canGoNext()) {
      alert('Не выполнены условия для перехода к следующему шагу');
      return;
    }
    if (currentStep === 3 && currentSpeakerIndex < speakersList.length - 1) {
      setCurrentSpeakerIndex(currentSpeakerIndex + 1);
      return;
    }
    setCurrentStep(currentStep + 1);
  };

  const handlePrev = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleFinish = async () => {
    if (!canGoNext()) {
      alert('Не выполнены условия для завершения опроса');
      return;
    }
    if (isFinishClicked) return;
    setIsFinishClicked(true);
    setIsFinished(true);
    // Формирование данных для отправки (пример)
    const positiveQualitiesList = [];
    const negativeQualitiesList = [];
    speakersList.forEach((speaker, idx) => {
      qualityRows.forEach((row, j) => {
        const selection = speakerQualities[idx][j];
        if (selection === 'positive') {
          positiveQualitiesList.push(`${row.positive} (${speaker.fullName})`);
        } else if (selection === 'negative') {
          negativeQualitiesList.push(`${row.negative} (${speaker.fullName})`);
        }
      });
    });
    const data = {
      partner,
      speakers: speakersList.map(s => s.fullName),
      answers: {
        speakerQualities,
        usefulness,
        brightThoughts,
        additionalSuggestions: additionalSelections,
        impression,
      },
      dateTime: new Date().toISOString(),
      positiveQualities: positiveQualitiesList.join(', '),
      negativeQualities: negativeQualitiesList.join(', '),
    };

    try {
      const response = await fetch('https://rgszh-miniapp.org/api/feedback/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
        credentials: 'include',
      });
      if (!response.ok) {
        alert('Ошибка при сохранении данных');
      }
    } catch (error) {
      console.error('Ошибка при отправке данных:', error);
      alert('Ошибка соединения с сервером');
    }
  };

  // ---------- Свайпы для перехода между шагами ----------
  const swipeHandlers = useSwipeable({
    onSwipedLeft: () => {
      if (currentStep > 1) handlePrev();
    },
    onSwipedRight: () => {
      if (canGoNext()) handleNext();
    },
  });

  // ---------- Фоновая анимация частиц (GIF) с прозрачностью 10% ----------
  const particleStyle = {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundImage: `url(${particleGif})`,
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
    opacity: 0.1,
    pointerEvents: 'none',
    zIndex: -0.5,
  };

  // ---------- Рендер шагов ----------
  const renderStep = () => {
    switch (currentStep) {
      case 1:
        return (
          <div className="step">
            <h2>Введите название партнёра</h2>
            <Autosuggest
              suggestions={partnerSuggestions}
              onSuggestionsFetchRequested={({ value }) => setPartnerSuggestions(getPartnerSuggestions(value))}
              onSuggestionsClearRequested={() => setPartnerSuggestions([])}
              getSuggestionValue={(suggestion) => suggestion}
              renderSuggestion={(suggestion) => <div>{suggestion}</div>}
              inputProps={{
                ...partnerInputProps,
                onFocus: handleFocus,
                onBlur: handleBlur,
              }}
            />
          </div>
        );
      case 2:
        return (
          <div className="step">
            <h2>Введите ФИО спикера(ов)</h2>
            {speakersList.map((speaker, index) => (
              <div key={speaker.id} className="speaker-row">
                <Autosuggest
                  suggestions={speakersSuggestions}
                  onSuggestionsFetchRequested={({ value }) => setSpeakersSuggestions(getSpeakerSuggestions(value))}
                  onSuggestionsClearRequested={() => setSpeakersSuggestions([])}
                  getSuggestionValue={(suggestion) => suggestion}
                  renderSuggestion={(suggestion) => <div>{suggestion}</div>}
                  inputProps={{
                    ...speakerInputProps(index),
                    onFocus: handleFocus,
                    onBlur: handleBlur,
                  }}
                />
                {index === speakersList.length - 1 && (
                  <button type="button" className="add-speaker-btn" onClick={handleAddSpeaker}>
                    <img src={addSpeakerIcon} alt="Добавить спикера" />
                  </button>
                )}
              </div>
            ))}
          </div>
        );
      case 3:
        return (
          <div className="step">
            <h2>Оцените качества спикера</h2>
            <p>
              Спикер: <strong>{speakersList[currentSpeakerIndex].fullName}</strong>
            </p>
            {qualityRows.map((row, idx) => (
              <div key={idx} className="quality-row">
                <button
                  onClick={() => handleQualitySelectForSpeaker(idx, 'positive')}
                  className={speakerQualities[currentSpeakerIndex][idx] === 'positive' ? 'green' : ''}
                >
                  {row.positive}
                </button>
                <button
                  onClick={() => handleQualitySelectForSpeaker(idx, 'negative')}
                  className={speakerQualities[currentSpeakerIndex][idx] === 'negative' ? 'red' : ''}
                >
                  {row.negative}
                </button>
              </div>
            ))}
            {speakersList.length > 1 && currentSpeakerIndex < speakersList.length - 1 && (
              <button className="btn next-btn" onClick={handleNext}>
                Далее для следующего спикера
              </button>
            )}
          </div>
        );
      case 4:
        return (
          <div className="step">
            <h2>Фактическая ценность информации</h2>
            <div className="usefulness-container">
              <button
                onClick={() => handleUsefulness('Полезно в работе (буду применять на практике)')}
                className={usefulness === 'Полезно в работе (буду применять на практике)' ? 'green' : ''}
              >
                Полезно в работе (буду применять на практике)
              </button>
              <button
                onClick={() => handleUsefulness('Бесполезно в работе (с моими клиентами это не работает)')}
                className={usefulness === 'Бесполезно в работе (с моими клиентами это не работает)' ? 'red' : ''}
              >
                Бесполезно в работе (с моими клиентами это не работает)
              </button>
            </div>
          </div>
        );
      case 5:
        return (
          <div className="step">
            <h2>Самые яркие мысли с мероприятия</h2>
            <input
              type="text"
              value={brightThoughts}
              onChange={handleBrightThoughtsChange}
              placeholder="Введите текст (минимум 5 символов)"
              className="partner-input"
            />
          </div>
        );
      case 6:
        return (
          <div className="step">
            <h2>Что стоило бы добавить в тренинг?</h2>
            <div className="additional-container">
              {additionalOptions.map((opt, idx) => (
                <button
                  key={idx}
                  onClick={() => toggleAdditionalSelection(opt)}
                  className={additionalSelections.includes(opt) ? 'selected' : ''}
                >
                  {opt}
                </button>
              ))}
            </div>
          </div>
        );
      case 7:
        return (
          <div className="step">
            <h2>Общее впечатление от тренинга</h2>
            <div className="impression-container">
              <button onClick={() => handleImpression('sad')}>
                <img src="/sad_smiley.png" alt="С грустным смайлом" />
              </button>
              <button onClick={() => handleImpression('neutral')}>
                <img src="/neutral_smiley.png" alt="Нейтральный смайл" />
              </button>
              <button onClick={() => handleImpression('happy')}>
                <img src="/happy_smiley.png" alt="Счастливый смайл" />
              </button>
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div
      {...swipeHandlers}
      ref={containerRef}
      className="container page-content"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* Слой с частицами (GIF) между фоном и остальным содержимым */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        backgroundImage: `url(${particleGif})`,
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
        opacity: 0.1,
        pointerEvents: 'none',
        zIndex: -0.5,
      }} />
      <div className="logo">
        <img src={logo} alt="Логотип" className="logo-image" />
      </div>
      {renderStep()}
      <ScrollIntoView options={{ behavior: 'smooth', block: 'end' }}>
        {currentStep < 7 ? (
          <button
            ref={nextButtonRef}
            className={`full-width-btn ${canGoNext() ? 'enabled' : 'disabled'}`}
            onClick={canGoNext() ? handleNext : undefined}
          >
            Далее
          </button>
        ) : (
          <button
            ref={nextButtonRef}
            className={`full-width-btn ${canGoNext() ? 'enabled' : 'disabled'}`}
            onClick={canGoNext() ? handleFinish : undefined}
          >
            Завершить опрос
          </button>
        )}
      </ScrollIntoView>
    </div>
  );
};

export default FeedbackPage;
