import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Register.css';

const Register = () => {
  const [email, setEmail] = useState('');
  const [status, setStatus] = useState('');
  const [emailValid, setEmailValid] = useState(false);
  const [verificationCode, setVerificationCode] = useState('');
  const [enteredCode, setEnteredCode] = useState('');
  const [userEmail, setUserEmail] = useState('');
  const navigate = useNavigate();

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleEmailSubmit = async () => {
    console.log("–û—Ç–ø—Ä–∞–≤–ª—è–µ–º email –Ω–∞ —Å–µ—Ä–≤–µ—Ä:", email);  // üîç –û—Ç–ª–∞–¥–∫–∞
    try {
      const response = await axios.post('http://127.0.0.1:5000/check_email', { email });

      console.log("–û—Ç–≤–µ—Ç —Å–µ—Ä–≤–µ—Ä–∞:", response.data);  // üîç –ü—Ä–æ–≤–µ—Ä—è–µ–º –æ—Ç–≤–µ—Ç —Å–µ—Ä–≤–µ—Ä–∞

      if (response.data.isValid) {
        setUserEmail(email);
        setEmailValid(true);
        setStatus('–ù–∞ –≤–∞—à email –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω –∫–æ–¥ –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è.');
      } else {
        setStatus(response.data.message);
      }
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞:', error);
      setStatus('–û—à–∏–±–∫–∞ –ø—Ä–æ–≤–µ—Ä–∫–∏ email. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ —Å–Ω–æ–≤–∞.');
    }
  };

  const handleCodeSubmit = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:5000/verify_code', { email: userEmail, code: enteredCode });

      if (response.data.success) {
        setStatus(response.data.message);
        setTimeout(() => navigate('/success'), 2000); // –ü–µ—Ä–µ—Ö–æ–¥ –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü—É —É—Å–ø–µ—Ö–∞
      } else {
        setStatus(response.data.message);
      }
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞:', error);
      setStatus('–û—à–∏–±–∫–∞ –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è –∫–æ–¥–∞.');
    }
  };

  return (
    <div className="register-container">
      <h2>–í–≤–µ–¥–∏—Ç–µ –≤–∞—à—É –∫–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω—É—é –ø–æ—á—Ç—É</h2>
      <input type="email" value={email} onChange={handleEmailChange} className="register-input" placeholder="–í–≤–µ–¥–∏—Ç–µ email" />
      <button onClick={handleEmailSubmit} className="register-btn">–ó–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞—Ç—å—Å—è</button>
      <button onClick={() => navigate('/')} className="guest-btn">–í–æ–π—Ç–∏ –∫–∞–∫ –≥–æ—Å—Ç—å</button>

      {emailValid && (
        <>
          <h3>{status}</h3>
          <input type="text" value={enteredCode} onChange={(e) => setEnteredCode(e.target.value)} className="register-input" placeholder="–í–≤–µ–¥–∏—Ç–µ –∫–æ–¥" />
          <button onClick={handleCodeSubmit} className="register-btn">–ü—Ä–æ–≤–µ—Ä–∏—Ç—å –∫–æ–¥</button>
        </>
      )}

      {!emailValid && <h3>{status}</h3>}
    </div>
  );
};

export default Register;
