import React, { useEffect, useState } from 'react'; // Импорт useState
import { useNavigate } from 'react-router-dom';
import logo from './components/logo.png';
import './WelcomePage.css';

const WelcomePage = () => {
  const navigate = useNavigate();
  const [textVisible, setTextVisible] = useState(true);
  const [isMovingUp, setIsMovingUp] = useState(false);// Объявление состояния

  useEffect(() => {
    // Через 4 секунды запускаем анимацию перемещения логотипа вверх
    const moveUpTimer = setTimeout(() => {
      setIsMovingUp(true); // Использование setIsMovingUp
    }, 4000);

    // Через 5 секунд перенаправляем на страницу регистрации
    const redirectTimer = setTimeout(() => {
      navigate('/register');
    }, 5000);

    // Очистка таймеров при размонтировании компонента
    return () => {
      clearTimeout(moveUpTimer);
      clearTimeout(redirectTimer);
    };
  }, [navigate]);

  return (
    <div className="welcome-container">
      <div className={`logo ${isMovingUp ? 'move-up' : ''}`} />
      <h1 className={`welcome-text ${textVisible ? '' : 'disappear'}`}>Добро пожаловать!</h1>
    </div>
  );
};

export default WelcomePage;
