import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import backgroundImage from './components/background.png';
import logo from './components/logo.png';
import './MarzaPollPage.css';

// Используйте один и тот же домен/порт для всех запросов
const SERVER_URL = 'https://rgszh-miniapp.org';

// Подключаем socket.io к серверу
const socket = io(SERVER_URL);

const MarzaPollPage = () => {
  const pollQuestion = "Зачем продавать маржа?";
  const initialOptions = [
    { text: "Чтобы отстал руководитель", votes: 0 },
    { text: "Чтобы перевыполнить план и заработать много деняк", votes: 0 },
    { text: "Чтобы победить в конкурсе и полетать на самолёте", votes: 0 },
    { text: "Чтобы клиент меня любил сильнее, чем маму", votes: 0 },
    { text: "Какого ещё маржа? Я не в курсе", votes: 0 }
  ];

  const [options, setOptions] = useState(initialOptions);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [isVoted, setIsVoted] = useState(false);

  useEffect(() => {
    socket.on('pollResults', (results) => {
      setOptions(results);
    });
    // Получаем начальные данные опроса
    fetch(`${SERVER_URL}/api/poll`)
      .then(response => response.json())
      .then(data => setOptions(data))
      .catch(err => console.error(err));
    return () => {
      socket.off('pollResults');
    };
  }, []);

  const totalVotes = options.reduce((sum, opt) => sum + opt.votes, 0);

  // При выборе варианта сразу отправляем голос
  const handleOptionSelect = (index) => {
    if (isVoted) return;
    setSelectedIndex(index);
    fetch(`${SERVER_URL}/api/poll/vote`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ index })
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          setIsVoted(true);
        }
      })
      .catch(err => console.error(err));
  };

  // Кнопка "Сбросить результаты": отправляем запрос на endpoint /api/poll/reset
  const handleReset = () => {
    fetch(`${SERVER_URL}/api/poll/reset`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
    })
      .then(response => response.json())
      .then(data => {
        if (data.success) {
          // Обнуляем состояния
          setOptions(initialOptions);
          setIsVoted(false);
          setSelectedIndex(null);
        }
      })
      .catch(err => console.error(err));
  };

  // Кнопка "Назад": переходим на предыдущую страницу (или на страницу с опросами)
  const handleBack = () => {
    window.history.back();
  };

  return (
    <div className="marza-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="marza-logo-container">
        <img src={logo} alt="Logo" className="marza-logo" />
      </div>

      {/* Кнопка "Назад" слева вверху */}
      <div className="back-btn-container">
        <button className="back-btn" onClick={handleBack}>
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none">
            <path d="M15 18l-6-6 6-6" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
        </button>
      </div>

      {/* Кнопка "Сбросить результаты" справа вверху */}
      <div className="top-btn-container">
        <button className="top-btn reset-btn" onClick={handleReset}>
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none">
            {/* Пример иконки сброса: круговая стрелка */}
            <path d="M21 2v6h-6" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            <path d="M3.51 8.67a9 9 0 0 1 14.85-2.84L21 8" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
        </button>
      </div>

      <div className="marza-content-wrapper">
        <h2 className="marza-title">{pollQuestion}</h2>
        {/* Счётчик голосов под вопросом */}
        <div className="marza-votes-counter">
          {totalVotes > 0 ? `Всего голосов: ${totalVotes}` : 'Нет голосов'}
        </div>

        {!isVoted ? (
          <div className="marza-options-container">
            {options.map((option, idx) => (
              <label key={idx} className="marza-option-label">
                <input
                  type="radio"
                  name="marzaPoll"
                  value={idx}
                  checked={selectedIndex === idx}
                  onChange={() => handleOptionSelect(idx)}
                />
                <span className="marza-option-text">{option.text}</span>
              </label>
            ))}
          </div>
        ) : (
          <div className="marza-results">
            <h3 className="marza-results-title">Результаты:</h3>
            {options.map((option, idx) => {
              const percent = totalVotes > 0 ? (option.votes / totalVotes) * 100 : 0;
              return (
                <div key={idx} className="marza-result-row">
                  <div className="marza-result-text">
                    {option.text} — {option.votes} голос(ов)
                  </div>
                  <div className="marza-progress-bar">
                    <div
                      className="marza-progress-bar-filled"
                      style={{ width: `${percent}%` }}
                    ></div>
                  </div>
                  <div className="marza-result-percent">
                    {percent.toFixed(1)}%
                  </div>
                </div>
              );
            })}
            <p className="marza-total-votes">Всего голосов: {totalVotes}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default MarzaPollPage;
