// AdaptationPage.js

import React from 'react';
import { useNavigate } from 'react-router-dom';
import './AdaptationPage.css';
import homeIcon from './components/icons/mainmenu.png'; // поправьте путь при необходимости

const AdaptationPage = () => {
  const navigate = useNavigate();

  // Возвращаемся на предыдущий экран (главное меню)
  const goHome = () => navigate(-1);

  const goDay = (day) => () => navigate(`/FirstDayAdaptation?day=${day}`);

  return (
    <div className="adaptation-container">

      {/* Кнопка «Домой» */}
      <div className="back-btn-container" style={{ top: '80px' }}>
        <button className="back-btn mainmenu-btn" onClick={goHome}>
          <img src={homeIcon} alt="Домой" />
        </button>
      </div>

      {/* Заголовок */}
      <h1 className="adaptation-title">План адаптации</h1>

      {/* Список дней */}
      <div className="adaptation-buttons">
        <button className="adaptation-btn" onClick={goDay(1)}>1 день</button>
        <button className="adaptation-btn" onClick={goDay(2)}>2 день</button>
        <button className="adaptation-btn" onClick={goDay(3)}>3 день</button>
        <button className="adaptation-btn" onClick={goDay(4)}>4 день</button>
      </div>

    </div>
  );
};

export default AdaptationPage;
