import React, { useState, useEffect } from 'react';
import mainmenuIcon from './components/icons/mainmenu.png';
import logoImage from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';
import './SNPPage.css';

const SNPPage = () => {
  const [userName, setUserName] = useState('');
  const [birthDate, setBirthDate] = useState(null);
  const [gender, setGender] = useState(null);
  const [calcType, setCalcType] = useState(null); // 'premium' | 'sum'
  const [amount, setAmount] = useState('');
  const [currentStep, setCurrentStep] = useState(1);

  useEffect(() => {
    const storedName = localStorage.getItem('userName');
    setUserName(storedName || '–ì–æ—Å—Ç—å');
  }, []);

  const formatSum = raw => {
    let digits = raw.replace(/\D/g, '');
    if (!digits) return '';
    let out = '';
    while (digits.length > 3) {
      out = '.' + digits.slice(-3) + out;
      digits = digits.slice(0, -3);
    }
    return digits + out;
  };

  const handleAmountChange = e => setAmount(formatSum(e.target.value));


const getAge = (birthStr) => {
  const [day, month, year] = birthStr.split('.').map(Number);
  const birth = new Date(year, month - 1, day);
  const today = new Date();
  let age = today.getFullYear() - birth.getFullYear();
  if (
    today.getMonth() < birth.getMonth() ||
    (today.getMonth() === birth.getMonth() && today.getDate() < birth.getDate())
  ) {
    age--;
  }
  return age;
};

const canGoNext = () => {
    if (!birthDate || !gender || !calcType || !amount) return false;
    const numeric = parseInt(amount.replace(/\./g, '')) || 0;
    const age = getAge(birthDate);
    if (age < 18) return false;
    if (calcType === 'premium' && numeric < 60000) return false;
    if (calcType === 'sum' && numeric < 450000) return false;
    return true;
  };

  const handleNext = () => {
    if (!canGoNext()) {
      alert('–ó–∞–ø–æ–ª–Ω–∏—Ç–µ –≤—Å–µ –ø–æ–ª—è');
      return;
    }
    // –∑–¥–µ—Å—å –ø–æ–∫–∞ –ø—Ä–æ—Å—Ç–æ —Å–æ–æ–±—â–µ–Ω–∏–µ ‚Äî –≤ –±—É–¥—É—â–µ–º –±—É–¥–µ—Ç —Ä–∞—Å—á—ë—Ç
    alert('–î–∞–Ω–Ω—ã–µ –≥–æ—Ç–æ–≤—ã –∫ –ø–µ—Ä–µ–¥–∞—á–µ –Ω–∞ —Ä–∞—Å—á—ë—Ç');
  };

  const handlePrev = () => setCurrentStep(1);
  const goToMenu = () => window.location.href = '/menu';

  return (
    <div className="snp-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      {/* –õ–æ–≥–æ—Ç–∏–ø */}
      <div className="logo-container">
        <img src={logoImage} alt="Logo" className="logo-image" />
      </div>

      {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ */}
      <h1 className="snp-title">
        –†–∞—Å—á—ë—Ç –ø–æ –ø—Ä–æ–≥—Ä–∞–º–º–µ "–°—Ç—Ä–∞—Ç–µ–≥–∏—è –Ω–∞ –ø—è—Ç—å. –ì–∞—Ä–∞–Ω—Ç"
      </h1>

      <div className="snp-form">
        {/* –®–∞–≥ 1 */}
        <div className="form-group">
          <label className="form-label">–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è:</label>
          <DateWheelPicker onChange={setBirthDate} />
        </div>

        <div className="form-group">
          <label className="form-label">–ü–æ–ª:</label>
          <div className="gender-selector">
            {['male', 'female'].map(g => (
              <div
                key={g}
                className={`gender-option ${gender === g ? 'selected' : ''}`}
                onClick={() => setGender(g)}
              >
                <div className="gender-label">{g === 'male' ? '–ú—É–∂—Å–∫–æ–π' : '–ñ–µ–Ω—Å–∫–∏–π'}</div>
                <div className="gender-icon">{g === 'male' ? 'üë®' : 'üë©'}</div>
              </div>
            ))}
          </div>
        </div>

        <div className="form-group">
          <label className="form-label">–†–∞—Å—á—ë—Ç –æ—Ç‚Ä¶</label>
          <div className="insurance-options">
            <button
              className={`insurance-btn ${calcType === 'premium' ? 'selected' : ''}`}
              onClick={() => setCalcType('premium')}
            >
              –°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞
            </button>
            <button
              className={`insurance-btn ${calcType === 'sum' ? 'selected' : ''}`}
              onClick={() => setCalcType('sum')}
            >
              –°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã
            </button>
          </div>
        </div>

        {calcType && (
          <div className="form-group">
            <label className="form-label">–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É</label>
            <input
              type="text"
              className="insurance-sum-input"
              value={amount}
              onChange={handleAmountChange}
              placeholder={calcType === 'premium' ? '–ú–∏–Ω–∏–º—É–º 60.000' : '–ú–∏–Ω–∏–º—É–º 450.000'}
            />
          </div>
        )}
      </div>

      {/* –ö–Ω–æ–ø–∫–∏ */}
      <div className="back-btn-container">
        <button className="back-btn mainmenu-btn" onClick={goToMenu}>
          <img src={mainmenuIcon} alt="Menu" />
        </button>
      </div>
      <div className="top-btn-container">
        <button className="top-btn" onClick={handleNext} disabled={!canGoNext()}>
          <svg viewBox="0 0 24 24"><path d="M12 4l8 8-8 8" /></svg>
        </button>
      </div>
    </div>
  );
};

export default SNPPage;
