// src/SNPPage.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';

import './PollsPage.css';
import './SNPPage.css';

export default function SNPPage() {
  const navigate = useNavigate();

  // состояния
  const [step, setStep]                   = useState(1);
  const [email, setEmail]                 = useState('');
  const [emailError, setEmailError]       = useState('');
  const [birthDate, setBirthDate]         = useState(new Date());
  const [gender, setGender]               = useState(null);    // 'мужской' | 'женский' | null
  const [calcType, setCalcType]           = useState(null);    // 'premium' | 'sum' | null
  const [amountRaw, setAmountRaw]         = useState('');      // чистые цифры
  const [amountDisplay, setAmountDisplay] = useState('');      // с точками
  const [amountError, setAmountError]     = useState('');
  const [isProcessing, setIsProcessing]   = useState(false);
  const [resultData, setResultData]       = useState(null);
  const [carouselIndex, setCarouselIndex] = useState(0);       // текущая страница карусели (0,1,2)

  // Проверка email
  const emailValid = email.toLowerCase().endsWith('@vtb.ru');

  function getAge(d) {
    if (!(d instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - d.getFullYear();
    if (today.getMonth() < d.getMonth() ||
       (today.getMonth() === d.getMonth() && today.getDate() < d.getDate())) {
      age--;
    }
    return age;
  }

  function isAmountValid() {
    const n = parseInt(amountRaw, 10) || 0;
    return (calcType === 'premium' && n >= 60000)
        || (calcType === 'sum'     && n >= 450000);
  }

  function canGoNext() {
    return step === 2
        && getAge(birthDate) >= 18
        && gender
        && calcType
        && amountRaw
        && isAmountValid();
  }

  // Шаг 1 → шаг 2
  function handleEmailNext() {
    if (!emailValid) {
      setEmailError('Используйте корпоративную почту домена @vtb.ru');
      return;
    }
    setEmailError('');
    // Сброс скрытых полей перед вторым шагом
    setGender(null);
    setCalcType(null);
    setAmountRaw('');
    setAmountDisplay('');
    setAmountError('');
    setStep(2);
  }

  function onAmountChange(e) {
    const raw = e.target.value.replace(/\D/g, '');
    setAmountRaw(raw);
    let d = raw, o = '';
    while (d.length > 3) {
      o = '.' + d.slice(-3) + o;
      d = d.slice(0, -3);
    }
    setAmountDisplay(d + o);

    const n = parseInt(raw, 10) || 0;
    if      (calcType === 'premium' && n < 60000) setAmountError('Мин. 60 000 ₽');
    else if (calcType === 'sum'     && n < 450000) setAmountError('Мин. 450 000 ₽');
    else                                           setAmountError('');
  }

  // Шаг 2 → отправка
  async function handleNext() {
    if (!canGoNext()) return;
    setIsProcessing(true);

    const dateStr = birthDate.toLocaleDateString('ru-RU', {
      day: '2-digit', month: '2-digit', year: 'numeric'
    });
    const payload = {
      email,
      birthdate: dateStr,
      age:        getAge(birthDate),
      gender,
      payment:    calcType === 'premium' ? 'Страхового взноса' : 'Страховой суммы',
      sum:        parseInt(amountRaw, 10)
    };

    try {
      const res = await fetch('/api/proxy/snp/save', {
        method:  'POST',
        headers: {'Content-Type':'application/json'},
        body:    JSON.stringify(payload)
      });
      if (!res.ok) {
        console.error('Ошибка сервера', res.status, await res.text());
        alert(`Ошибка ${res.status}: смотрите консоль`);
      } else {
        const data = await res.json();
        setResultData(data);
        // После приёма результата сразу сбрасываем carouselIndex на 0
        setCarouselIndex(0);
      }
    } catch (err) {
      console.error('Ошибка запроса:', err);
      alert('Не удалось выполнить запрос');
    } finally {
      setIsProcessing(false);
    }
  }

  function goHome() {
    navigate(-1);
  }

  // «Выполняется расчёт…»
  if (isProcessing) {
    return (
      <div className="processing-screen" style={{ backgroundImage:`url(${backgroundImage})` }}>
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип"/>
        </div>
        <p className="processing-title">
          Выполняется расчёт по программе «Стратегия на пять. Гарант»
        </p>
        <div className="arrow-animation">→</div>
      </div>
    );
  }

  // Результат с каруселью
  if (resultData) {
    const todayStr = new Date().toLocaleDateString('ru-RU', {
      day:'2-digit', month:'2-digit', year:'numeric'
    });
    // Достаём нужные поля из результата, который вернул сервер:
    const {
      gender:       resGender,
      payment:      resPayment,
      termYears,            // из server.py это H? (либо rv("C?"))
      frequency,            // rv("C?")
      contribution,          // rv("C13")
      totalContribution,     // rv("C16")
      accrualPercent,       // rv("H12") — в процентах (например 44, а не 0.44)
      accrualAmount,        // rv("H13")
      totalAccrual          // rv("H16")
    } = resultData;

    return (
      <div className="snp-container" style={{ backgroundImage:`url(${backgroundImage})` }}>
        <div className="polls-header">
          <button className="back-btn" onClick={goHome}>
            <svg viewBox="0 0 24 24">
              <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z"/>
            </svg>
          </button>
          <div className="logo-wrapper animate-logo">
            <img className="poll-logo" src={logoImage} alt="Логотип"/>
          </div>
          <button
            className="repeat-btn"
            onClick={() => {
              // при повторном расчёте — сброс всех вводов и возврат на шаг 2
              setResultData(null);
              setGender(null);
              setCalcType(null);
              setAmountRaw('');
              setAmountDisplay('');
              setAmountError('');
              setStep(2);
            }}
          >
            <svg viewBox="0 0 24 24">
              <path
                d="M12 4V1L8 5l4 4V6
                   c4.41 0 8 3.59 8 8
                   s-3.59 8-8 8-8-3.59-8-8h2
                   c0 3.31 2.69 6 6 6s6-2.69 6-6
                   -2.69-6-6-6v3l-4-4 4-4z"
                fill="currentColor"
              />
            </svg>
          </button>
        </div>

        <div className="content-wrapper">
          <h1 className="polls-title">
            Расчёт по программе «Стратегия на пять. Гарант»
          </h1>

          <div className="params-container">
            <h2>Параметры</h2>
            <p>Дата расчёта: {todayStr}</p>
            <p>Возраст: {getAge(birthDate)} лет</p>
            <p>Пол: {resGender}</p>
            <p>Расчёт от: {resPayment}</p>
          </div>

          {/* ==== КАРУСЕЛЬ ==== */}
          <div className="carousel-container">
            {/* Индикаторы (три полоски сверху) */}
            <div className="carousel-indicators">
              <span className={carouselIndex === 0 ? 'active' : ''}></span>
              <span className={carouselIndex === 1 ? 'active' : ''}></span>
              <span className={carouselIndex === 2 ? 'active' : ''}></span>
            </div>

            {/* Это «окно» с overflow: hidden */}
            <div className="carousel-clipper">
              <div
                className="carousel-wrapper"
                style={{ transform: `translateX(-${carouselIndex * 100}%)` }}
              >
                {/* ===== Страница 1 ===== */}
                <div className="carousel-page">
                  <div className="first-line">В течение 5 лет</div>
                  <div className="unit">Вы вносите средства в программу</div>
                  <div className="value">
                    {contribution.toLocaleString('ru-RU', { minimumFractionDigits: 0 })} ₽
                  </div>
                  <div className="unit">Ежегодно</div>
                  <div className="value">
                    {totalContribution.toLocaleString('ru-RU', { minimumFractionDigits: 0 })} ₽
                  </div>
                </div>

                {/* ===== Страница 2 ===== */}
                <div className="carousel-page">
                  <div className="first-line">Начисляем дополнительно</div>
                  <div className="unit">{accrualPercent}% от каждого взноса</div>
                  <div className="value">
                    {accrualAmount.toLocaleString('ru-RU', { minimumFractionDigits: 0 })} ₽
                  </div>
                  <div className="unit">Ежегодно</div>
                  <div className="value">
                    {totalAccrual.toLocaleString('ru-RU', { minimumFractionDigits: 0 })} ₽
                  </div>
                </div>

                {/* ===== Страница 3 ===== */}
                <div className="carousel-page">
                  <div className="first-line">Итоговая годовая выплата</div>
                  <div className="unit">Сумма с начислениями</div>
                  <div className="value">
                    { (contribution + accrualAmount).toLocaleString('ru-RU', { minimumFractionDigits: 0 }) } ₽
                  </div>
                  <div className="unit">Сумма за 5 лет</div>
                  <div className="value">
                    { (totalContribution + totalAccrual).toLocaleString('ru-RU', { minimumFractionDigits: 0 }) } ₽
                  </div>
                </div>
              </div>
            </div>

            {/* Кнопки «‹» и «›» — вынесены наружу, чтобы не обрезались overflow: hidden */}
            <button
              className="carousel-control prev"
              onClick={() => setCarouselIndex(idx => idx - 1)}
              disabled={carouselIndex === 0}
            >
              ‹
            </button>
            <button
              className="carousel-control next"
              onClick={() => setCarouselIndex(idx => idx + 1)}
              disabled={carouselIndex === 2}
            >
              ›
            </button>
          </div>
          {/* ==== конец КАРУСЕЛИ ==== */}
        </div>
      </div>
    );
  }

  // Основной UI (шаг 1 и шаг 2)
  return (
    <div className="snp-container" style={{ backgroundImage:`url(${backgroundImage})` }}>
      <div className="polls-header">
        <button className="back-btn" onClick={goHome}>
          <svg viewBox="0 0 24 24">
            <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z"/>
          </svg>
        </button>
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип"/>
        </div>
        <button
          className="forward-btn"
          onClick={step === 1 ? handleEmailNext : handleNext}
          disabled={step === 1 ? !emailValid : !canGoNext()}
        >
          <span className="arrow-icon">→</span>
        </button>
      </div>

      <div className="content-wrapper">
        {step === 1 && (
          <>
            <div className="email-step">
              <label className="email-label">Корпоративная почта ВТБ:</label>
              <input
                type="email"
                placeholder="example@vtb.ru"
                value={email}
                onChange={e => setEmail(e.target.value)}
              />
            </div>
            {emailError && <div className="input-error">{emailError}</div>}
            <button
              className="polls-btn btn1"
              onClick={handleEmailNext}
              disabled={!emailValid}
            >
              Продолжить
            </button>
          </>
        )}

        {step === 2 && (
          <>
            <h1 className="polls-title">
              Расчёт по программе «Стратегия на пять. Гарант»
            </h1>

            <div className="date-section">
              <label className="section-label">Дата рождения:</label>
              <DateWheelPicker
                value={birthDate}
                onChange={val => {
                  if (val instanceof Date) setBirthDate(val);
                  else if (val?.day)
                    setBirthDate(new Date(val.year, val.month - 1, val.day));
                }}
              />
            </div>

            <div className="section-subtitle">Выберите пол:</div>
            <div className="gender-section">
              <button
                className={`insurance-btn ${gender === 'мужской' ? 'selected' : ''}`}
                onClick={() => setGender('мужской')}
              >
                <div>Мужской</div>
                <div className="emoji">👨</div>
              </button>
              <button
                className={`insurance-btn ${gender === 'женский' ? 'selected' : ''}`}
                onClick={() => setGender('женский')}
              >
                <div>Женский</div>
                <div className="emoji">👩</div>
              </button>
            </div>

            <div className="section-subtitle">Расчёт от…</div>
            <div className="insurance-options">
              <button
                className={`insurance-btn ${calcType === 'premium' ? 'selected' : ''}`}
                onClick={() => setCalcType('premium')}
              >
                <div>Страхового</div>
                <div>взноса</div>
              </button>
              <button
                className={`insurance-btn ${calcType === 'sum' ? 'selected' : ''}`}
                onClick={() => setCalcType('sum')}
              >
                <div>Страховой</div>
                <div>суммы</div>
              </button>
            </div>

            {calcType && (
              <>
                <div className="section-subtitle">Сумма (₽):</div>
                <div className="sum-section">
                  <input
                    type="text"
                    placeholder={calcType === 'premium' ? 'Мин. 60 000' : 'Мин. 450 000'}
                    value={amountDisplay}
                    onChange={onAmountChange}
                  />
                </div>
                {amountError && <div className="input-error">{amountError}</div>}
              </>
            )}
          </>
        )}
      </div>
    </div>
  );
}
