import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import mainmenuIcon    from './components/icons/mainmenu.png';
import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';
import './SNPPage.css';

export default function SNPPage() {
  const navigate = useNavigate();

  // –°–æ—Å—Ç–æ—è–Ω–∏—è
  const [birthDateParts, setBirthDateParts] = useState({ day: null, month: null, year: null });
  const [birthDate,      setBirthDate]      = useState(null);
  const [gender,         setGender]         = useState(null);   // —Ç–µ–ø–µ—Ä—å "–º—É–∂—Å–∫–æ–π" –∏–ª–∏ "–∂–µ–Ω—Å–∫–∏–π"
  const [calcType,       setCalcType]       = useState(null);
  const [amount,         setAmount]         = useState('');
  const [amountError,    setAmountError]    = useState('');
  const [isProcessing,   setIsProcessing]   = useState(false);
  const [resultData,     setResultData]     = useState(null);

  // –ü–∞—Ä–∞–º–µ—Ç—Ä—ã –ø—Ä–æ–≥—Ä–∞–º–º—ã
  const term = 5;                       // "–°—Ç—Ä–∞—Ç–µ–≥–∏—è –Ω–∞ –ø—è—Ç—å"
  const periodicity = '–ï–∂–µ–≥–æ–¥–Ω–æ';
  const currency = '—Ä—É–±–ª–∏';

  // –°–æ–±–∏—Ä–∞–µ–º –Ω–∞—Å—Ç–æ—è—â–∏–π Date –∏–∑ birthDateParts
  useEffect(() => {
    const { day, month, year } = birthDateParts;
    if (day && month && year) {
      const d = Number(day), m = Number(month), y = Number(year);
      const dt = new Date(y, m - 1, d);
      if (!isNaN(dt.getTime()) && dt.getDate() === d && dt.getMonth() + 1 === m && dt.getFullYear() === y) {
        setBirthDate(dt);
      } else {
        setBirthDate(null);
      }
    } else {
      setBirthDate(null);
    }
  }, [birthDateParts]);

  // –§–æ—Ä–º–∞—Ç —Å—É–º–º—ã —Å —Ä–∞–∑–¥–µ–ª–∏—Ç–µ–ª–µ–º —Ç—ã—Å—è—á
  const formatSum = raw => {
    let digits = raw.replace(/\D/g, '');
    if (!digits) return '';
    let out = '';
    while (digits.length > 3) {
      out = '.' + digits.slice(-3) + out;
      digits = digits.slice(0, -3);
    }
    return digits + out;
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –≤–≤–æ–¥–∞ —Å—É–º–º—ã
  const handleAmountChange = e => {
    const fmt = formatSum(e.target.value);
    setAmount(fmt);
    const num = parseInt(fmt.replace(/\./g, ''), 10) || 0;
    if (calcType === 'premium') {
      setAmountError(num < 60000 ? '–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –æ—Ç 60 000 ‚ÇΩ' : '');
    } else if (calcType === 'sum') {
      setAmountError(num < 450000 ? '–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –æ—Ç 450 000 ‚ÇΩ' : '');
    }
  };

  const isAmountValid = () => {
    const num = parseInt(amount.replace(/\./g, ''), 10) || 0;
    return (calcType === 'premium' && num >= 60000) || (calcType === 'sum' && num >= 450000);
  };

  // –¢–æ—á–Ω—ã–π —Ä–∞—Å—á—ë—Ç –≤–æ–∑—Ä–∞—Å—Ç–∞
  const getAge = date => {
    if (!(date instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - date.getFullYear();
    const m = today.getMonth() - date.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < date.getDate())) age--;
    return age;
  };

  // –ü—Ä–æ–≤–µ—Ä–∫–∞ –≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç–∏ –ø–µ—Ä–µ–π—Ç–∏ –¥–∞–ª—å—à–µ
  const canGoNext = () => (
    birthDate instanceof Date &&
    !isNaN(birthDate.getTime()) &&
    gender &&                    // —Ç–µ–ø–µ—Ä—å "–º—É–∂—Å–∫–æ–π" –∏–ª–∏ "–∂–µ–Ω—Å–∫–∏–π"
    calcType &&
    amount &&
    !amountError &&
    getAge(birthDate) >= 18
  );

  // –û—Ç–ø—Ä–∞–≤–∫–∞ –∑–∞–ø—Ä–æ—Å–∞ —Ä–∞—Å—á—ë—Ç–∞
  const handleNext = async () => {
    if (!canGoNext()) return;
    const dateRu = birthDate.toLocaleDateString('ru-RU', { day: '2-digit', month: '2-digit', year: 'numeric' });
    const age    = getAge(birthDate);
    const payload = {
      birthdate: dateRu,
      age,
      gender,                    // –æ—Ç–ø—Ä–∞–≤–ª—è–µ–º –Ω–∞ —Ä—É—Å—Å–∫–æ–º
      payment: calcType === 'premium' ? '–°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞' : '–°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã',
      sum: parseInt(amount.replace(/\./g, ''), 10)
    };

    setIsProcessing(true);
    try {
      const res = await fetch('/api/snp/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const data = await res.json();
      if (res.ok) setResultData(data);
      else console.error('–û—à–∏–±–∫–∞ —Å–µ—Ä–≤–µ—Ä–∞:', data);
    } catch (err) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–ø—Ä–æ—Å–∞:', err);
    }
    setIsProcessing(false);
  };

  // –¢–µ–∫—É—â–∞—è –¥–∞—Ç–∞ —Ä–∞—Å—á—ë—Ç–∞
  const todayStr = new Date().toLocaleDateString('ru-RU', { day: '2-digit', month: '2-digit', year: 'numeric' });

  return (
    <div className="snp-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      {/* –®–∞–ø–∫–∞ */}
      <div className="logo-container">
        <img src={logoImage} alt="Logo" className="logo-image"/>
      </div>
      <h1 className="snp-title">–†–∞—Å—á—ë—Ç –ø–æ –ø—Ä–æ–≥—Ä–∞–º–º–µ "–°—Ç—Ä–∞—Ç–µ–≥–∏—è –Ω–∞ –ø—è—Ç—å. –ì–∞—Ä–∞–Ω—Ç"</h1>

      {/* –§–æ—Ä–º–∞ –≤–≤–æ–¥–∞ */}
      {!isProcessing && !resultData && (
        <div className="snp-form">
          {/* –î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è */}
          <div className="form-group">
            <label className="form-label">–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è:</label>
            <DateWheelPicker
              value={birthDateParts}
              onChange={val => {
                setBirthDateParts(prev => {
                  if (val instanceof Date) {
                    return {
                      day:   val.getDate().toString().padStart(2,'0'),
                      month: (val.getMonth()+1).toString().padStart(2,'0'),
                      year:  val.getFullYear().toString()
                    };
                  }
                  if (Array.isArray(val) && val.length === 3) {
                    const [d,m,y] = val;
                    return { day: d, month: m, year: y };
                  }
                  if (val && typeof val === 'object') {
                    return {
                      ...prev,
                      ...(val.day   != null ? { day:   String(val.day).padStart(2,'0')   } : {}),
                      ...(val.month != null ? { month: String(val.month).padStart(2,'0') } : {}),
                      ...(val.year  != null ? { year:  String(val.year)                } : {})
                    };
                  }
                  if (typeof val === 'string') {
                    if (val.includes('.')) {
                      const [d,m,y] = val.split('.').map(s => s.padStart(2,'0'));
                      return { day: d, month: m, year: y };
                    }
                    if (val.includes('-')) {
                      const [y,m,d] = val.split('-').map(s => s.padStart(2,'0'));
                      return { day: d, month: m, year: y };
                    }
                  }
                  return prev;
                });
              }}
            />
          </div>

          {/* –ü–æ–ª */}
          <div className="form-group">
            <label className="form-label">–ü–æ–ª:</label>
            <div className="gender-selector">
              {['–º—É–∂—Å–∫–æ–π','–∂–µ–Ω—Å–∫–∏–π'].map(value => (
                <div
                  key={value}
                  className={`gender-option ${gender === value ? 'selected' : ''}`}
                  onClick={() => setGender(value)}
                >
                  <div className="gender-label">{value === '–º—É–∂—Å–∫–æ–π' ? '–ú—É–∂—Å–∫–æ–π' : '–ñ–µ–Ω—Å–∫–∏–π'}</div>
                  <div className="gender-icon">{value === '–º—É–∂—Å–∫–æ–π' ? 'üë®' : 'üë©'}</div>
                </div>
              ))}
            </div>
          </div>

          {/* –¢–∏–ø —Ä–∞—Å—á—ë—Ç–∞ */}
          <div className="form-group">
            <label className="form-label">–†–∞—Å—á—ë—Ç –æ—Ç‚Ä¶</label>
            <div className="insurance-options">
              <button
                className={`insurance-btn ${calcType === 'premium' ? 'selected' : ''}`}
                onClick={() => { setCalcType('premium'); setAmount(''); setAmountError(''); }}
              >
                –°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞
              </button>
              <button
                className={`insurance-btn ${calcType === 'sum' ? 'selected' : ''}`}
                onClick={() => { setCalcType('sum'); setAmount(''); setAmountError(''); }}
              >
                –°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã
              </button>
            </div>
          </div>

          {/* –°—É–º–º–∞ */}
          {calcType && (
            <div className="form-group">
              <label className="form-label">–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –≤ —Ä—É–±–ª—è—Ö</label>
              <input
                type="text"
                className="insurance-sum-input"
                value={amount}
                onChange={handleAmountChange}
                placeholder={calcType === 'premium' ? '–ú–∏–Ω–∏–º—É–º 60 000' : '–ú–∏–Ω–∏–º—É–º 450 000'}
              />
              {amountError && <div className="input-error-message">{amountError}</div>}
            </div>
          )}

          {/* –ö–Ω–æ–ø–∫–∏ –Ω–∞–≤–∏–≥–∞—Ü–∏–∏ */}
          <div className="back-btn-container">
            <button className="back-btn mainmenu-btn" onClick={() => navigate('/Main-Menu')}>
              <img src={mainmenuIcon} alt="–ú–µ–Ω—é"/>
            </button>
          </div>
          <div className="top-btn-container">
            <button className="top-btn" onClick={handleNext} disabled={!canGoNext()}>
              <svg viewBox="0 0 24 24"><path d="M12 4l8 8-8 8"/></svg>
            </button>
          </div>
        </div>
      )}

      {/* –ü—Ä–æ—Ü–µ—Å—Å–∏–Ω–≥ */}
      {isProcessing && (
        <div className="processing-container">
          <p>–í—ã–ø–æ–ª–Ω—è–µ—Ç—Å—è —Ä–∞—Å—á—ë—Ç...</p>
          <div className="arrow-animation">‚Üí</div>
        </div>
      )}

      {/* –†–µ–∑—É–ª—å—Ç–∞—Ç */}
      {!isProcessing && resultData && (
        <div className="result-container">
          <h2>–†–µ–∑—É–ª—å—Ç–∞—Ç—ã —Ä–∞—Å—á—ë—Ç–∞</h2>
          <p><strong>–î–∞—Ç–∞ —Ä–∞—Å—á—ë—Ç–∞:</strong> {todayStr}</p>
          <p><strong>–ü–æ–ª:</strong> {resultData.gender}</p>
          <p><strong>–í–æ–∑—Ä–∞—Å—Ç –ó–∞—Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–Ω–æ–≥–æ:</strong> {resultData.age} –ª–µ—Ç</p>
          <p><strong>–°—Ä–æ–∫ —Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏—è:</strong> {term} –ª–µ—Ç</p>
          <p><strong>–ü–µ—Ä–∏–æ–¥–∏—á–Ω–æ—Å—Ç—å –≤–∑–Ω–æ—Å–æ–≤:</strong> {periodicity}</p>
          <p><strong>–í–∞–ª—é—Ç–∞:</strong> {currency}</p>
          <hr/>
          <p className="contribution-label">–í—ã –≤–Ω–æ—Å–∏—Ç–µ</p>
+         <p className="contribution-amount">
+           {formatSum(String(resultData.contribution))} ‚ÇΩ
+         </p>
          <p className="contribution-amount">{formatSum(String(resultData.sum))} ‚ÇΩ</p>
          <p className="contribution-frequency">{periodicity}</p>
          <p className="total-label">–°—É–º–º–∞ –≤–∞—à–∏—Ö –≤–∑–Ω–æ—Å–æ–≤</p>
+         <p className="total-amount">
+           {formatSum(String(resultData.totalContribution))} ‚ÇΩ
+         </p>
        </div>
      )}
    </div>
  );
}
