import React from 'react';
import { useNavigate } from 'react-router-dom';
import backgroundImage from './components/background.png';
import logoImage       from './components/logo.png';
import './EmployeePage.css';

export default function EmployeePage() {
  const navigate = useNavigate();

  return (
    <div
      className="employee-container"
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center'
      }}
    >
      {/* Полупрозрачный размытой фон (glass layer) */}
      <div className="employee-overlay" />

      {/* Кнопка «Домой» */}
      <button
        className="back-btn"
        onClick={() => navigate('/main-menu')}
      >
        <img src={require('./components/icons/mainmenu.png')} alt="Назад" />
      </button>

      {/* Логотип */}
      <img
        src={logoImage}
        alt="Логотип РГС Жизнь"
        className="employee-logo"
      />

      {/* Заголовок */}
      <h1 className="employee-title">Страница для сотрудника</h1>

      {/* Кнопки калькуляторов */}
      <div className="employee-buttons">
        <button className="employee-btn" onClick={() => navigate('/justincase')}>
          На всякий случай
        </button>
        <button className="employee-btn" onClick={() => navigate('/snp')}>
          Стратегия на пять. Гарант
        </button>
        <button className="employee-btn" onClick={() => navigate('/carefuture')}>
          Забота о будущем
        </button>
      </div>
    </div>
);
}
