// src/EmployeePage.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import backgroundImage from './components/background.png'; // градиент-фон
import logoImage       from './components/logo.png';
import './EmployeePage.css';

export default function EmployeePage() {
  const navigate  = useNavigate();
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    // запускаем анимацию появления логотипа и кнопок
    setAnimate(true);
  }, []);

  const products = [
    { to: '/justincase', label: 'На всякий случай' },
    { to: '/snp',         label: 'Стратегия на пять. Гарант' },
    { to: '/carefuture',  label: 'Забота о будущем' }
  ];

  // ripple + задержка навигации
  const handleClick = (e, route) => {
    const btn = e.currentTarget;
    const circle = document.createElement('span');
    const d = Math.max(btn.clientWidth, btn.clientHeight);
    const r = d / 2;
    circle.style.width  = circle.style.height = `${d}px`;
    circle.style.left   = `${e.clientX - btn.offsetLeft  - r}px`;
    circle.style.top    = `${e.clientY - btn.offsetTop   - r}px`;
    circle.classList.add('ripple');
    const old = btn.getElementsByClassName('ripple')[0];
    if (old) old.remove();
    btn.appendChild(circle);

    setTimeout(() => navigate(route), 300);
  };

  return (
    <div
      className="mainmenu-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* полупрозрачный overlay */}
      <div className="mainmenu-overlay" />

      {/* Кнопка “Домой” */}
      <button className="back-btn" onClick={() => navigate('/main-menu')}>
        <svg width="24" height="24" viewBox="0 0 24 24">
          <path
            d="M3 11l9-9 9 9v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z"
            fill="none"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
        </svg>
      </button>

      {/* Логотип */}
      <div className={`logo-wrapper ${animate ? 'animate-logo' : ''}`}>
        <img src={logoImage} alt="Логотип" className="mainmenu-logo" />
      </div>

      {/* Заголовок */}
      <h2 className={`animate-title`} style={{ animationDelay: '0.5s' }}>
        Страница для сотрудника
      </h2>

      {/* Кнопки-продукты */}
      <div className="mainmenu-buttons">
        {products.map((item, idx) => (
          <button
            key={item.to}
            className={`mainmenu-btn animate-btn btn${idx + 1}`}
            onClick={e => handleClick(e, item.to)}
          >
            {item.label}
            {/* span.ripple создаётся программно */}
          </button>
        ))}
      </div>
    </div>
  );
}
