import React from 'react';
import { useNavigate } from 'react-router-dom';
import './MainMenu.css';
import backgroundImage from './components/background.png';
import logoImage       from './components/logo.png';
// Если собираетесь добавлять иконки в кнопки, положите их в src/components и подключите здесь.
// import pollsIcon    from './components/icons/polls.svg';
// import profileIcon  from './components/icons/profile.svg';
// import employeeIcon from './components/icons/employee.svg';

export default function MainMenu() {
  const navigate = useNavigate();

  return (
    <div
      className="mainmenu-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* полупрозрачный размытой фон */}
      <div className="mainmenu-overlay" />

      {/* Логотип */}
      <img src={logoImage} alt="Логотип" className="mainmenu-logo" />

      {/* Кнопки */}
      <div className="mainmenu-buttons">
        <button
          className="mainmenu-btn"
          onClick={() => navigate('/polls')}
        >
          {/* Пример иконки слева от текста */}
          {/* <img src={pollsIcon} alt="" className="btn-icon" /> */}
          Опросы
        </button>
        <button
          className="mainmenu-btn"
          onClick={() => navigate('/profile')}
        >
          {/* <img src={profileIcon} alt="" className="btn-icon" /> */}
          Профиль
        </button>
        <button
          className="mainmenu-btn"
          onClick={() => navigate('/employee')}
        >
          {/* <img src={employeeIcon} alt="" className="btn-icon" /> */}
          Сотруднику
        </button>
      </div>
    </div>
  );
}
