// src/WelcomePage.js

import React, { useEffect, useState } from 'react';
import { useNavigate }              from 'react-router-dom';
import { useSwipeable }             from 'react-swipeable';
import { motion, AnimatePresence }  from 'framer-motion';

import backgroundImage from './components/background.png';
import './WelcomePage.css';

const WelcomePage = () => {
  const navigate = useNavigate();

  /* а) Логические состояния для рамки */
  // animateLogo = true → запускает entry-анимацию (.animate-logo)
  // exitLogo    = true → запускает exit-анимацию (.animate-logo-exit)
  const [animateLogo, setAnimateLogo] = useState(false);
  const [exitLogo, setExitLogo]       = useState(false);

  /* б) Логическое состояние для текста (Framer Motion) */
  // showText = true → текст монтируется → играет анимация slide-in
  // showText = false → играет анимация slide-out → размонтируется
  const [showText, setShowText]       = useState(false);

  /* в) Текст-приветствие («Доброе утро/день/вечер/ночи») */
  const [greeting, setGreeting]       = useState('');

  useEffect(() => {
    // 1) Определяем текст согласно времени суток
    const hour = new Date().getHours();
    let text = '';
    if (hour >= 6 && hour < 11)       text = 'Доброе утро';
    else if (hour >= 11 && hour < 17) text = 'Добрый день';
    else if (hour >= 17 && hour < 24) text = 'Добрый вечер';
    else                               text = 'Доброй ночи';
    setGreeting(text);

    // 2) Приводим рамку к entry-состоянию через 100ms
    const logoEntryTimer = setTimeout(() => setAnimateLogo(true), 100);

    // 3) Запускаем анимацию текста (slide-in) через 900ms
    const textInTimer = setTimeout(() => setShowText(true), 900);

    // 4) Скрываем текст (slide-out) через 3500ms
    const textOutTimer = setTimeout(() => setShowText(false), 3500);

    // 5) Запускаем анимацию рамки exit через 4000ms (после того, как текст уехал)
    const logoExitTimer = setTimeout(() => setExitLogo(true), 4000);

    // 6) Переходим на /main-menu через 4800ms (когда рамка уже уехала)
    const navTimer = setTimeout(() => navigate('/main-menu'), 4800);

    return () => {
      clearTimeout(logoEntryTimer);
      clearTimeout(textInTimer);
      clearTimeout(textOutTimer);
      clearTimeout(logoExitTimer);
      clearTimeout(navTimer);
    };
  }, [navigate]);

  const swipeHandlers = useSwipeable({ preventDefault: true });

  // Строим класс для рамки:
  //  • если animateLogo = true → добавляем .animate-logo
  //  • если exitLogo    = true → добавляем .animate-logo-exit
  let logoClass = 'logo-wrapper';
  if (animateLogo)      logoClass += ' animate-logo';
  if (exitLogo)         logoClass += ' animate-logo-exit';

  return (
    <div
      className="welcome-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
      {...swipeHandlers}
    >
      {/* ===== Рамка с логотипом ===== */}
      <div className={logoClass}>
        <img
          className="logo-image"
          src={require('./components/logo.png')}
          alt="Логотип РГС Жизнь"
        />
      </div>

      {/* ===== Текст (Framer Motion) ===== */}
      <div className="text-wrapper">
        <AnimatePresence>
          {showText && (
            <motion.h1
              className="welcome-title"
              initial={{ x: '-100%', opacity: 0 }}
              animate={{ x: 0,       opacity: 1 }}
              exit={{ x: '100%',     opacity: 0 }}
              transition={{ duration: 0.8, ease: 'easeOut' }}
            >
              {greeting}
            </motion.h1>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default WelcomePage;
