// src/PollsPage.js
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import backgroundImage from './components/background.png';
import logoImage from './components/logo.png';
import './PollsPage.css';

export default function PollsPage() {
  const navigate = useNavigate();
  const containerRef = useRef(null);
  const logoRef = useRef(null);
  const backBtnRef = useRef(null);
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    // стартуем анимации
    setAnimate(true);

    // выровняем кнопку "Домой" по центру логотипа
    const cr = containerRef.current.getBoundingClientRect();
    const lr = logoRef.current.getBoundingClientRect();
    const centerY = (lr.top - cr.top) + lr.height / 2;
    backBtnRef.current.style.top = `${centerY}px`;
  }, []);

  const polls = [
    { label: 'Оценка кандидата', path: '/assessment' },
    { label: 'Обратная связь',   path: '/feedback'   },
    { label: 'Маржа продаж',     path: '/marzapoll'  },
  ];

  const handleClick = (e, path) => {
    // ripple
    const btn = e.currentTarget;
    const circle = document.createElement('span');
    const d = Math.max(btn.clientWidth, btn.clientHeight);
    const r = d/2;
    circle.style.width = circle.style.height = `${d}px`;
    const rect = btn.getBoundingClientRect();
    circle.style.left = `${e.clientX - rect.left - r}px`;
    circle.style.top  = `${e.clientY - rect.top  - r}px`;
    circle.classList.add('ripple');
    const old = btn.getElementsByClassName('ripple')[0];
    if (old) old.remove();
    btn.appendChild(circle);

    setTimeout(() => navigate(path), 300);
  };

  return (
    <div
      className="polls-container"
      ref={containerRef}
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      <div className="polls-overlay" />

      <button
        className="back-btn"
        ref={backBtnRef}
        onClick={() => navigate('/main-menu')}
        aria-label="Домой"
      >
        <svg viewBox="0 0 24 24">
          <path d="M10 20v-6h4v6h5v-8h3L12 3 2 12h3v8z"/>
        </svg>
      </button>

      <div className={`logo-wrapper ${animate ? 'animate-logo' : ''}`} ref={logoRef}>
        <img src={logoImage} alt="Логотип" className="poll-logo" />
      </div>

      <h2 className={`polls-title ${animate ? 'animate-title' : ''}`}>
        Список опросов
      </h2>

      <div className="polls-buttons">
        {polls.map((p, i) => (
          <button
            key={p.path}
            className={`polls-btn ${animate ? `animate-btn btn${i+1}` : ''}`}
            onClick={e => handleClick(e, p.path)}
          >
            {p.label}
          </button>
        ))}
      </div>
    </div>
  );
}
