import React, { useEffect, useState } from 'react'; // Импорт useState
import { useNavigate } from 'react-router-dom';
import logo from './components/logo.png';
import './WelcomePage.css';

const WelcomePage = () => {
  const navigate = useNavigate();
  const [textVisible, setTextVisible] = useState(true); // Объявление состояния

  useEffect(() => {
    // Через 3 секунды текст начинает исчезать
    const textDisappearTimer = setTimeout(() => {
      setTextVisible(false); // Использование setTextVisible
    }, 3000);

    // Через 4 секунды перенаправляем на страницу register
    const redirectTimer = setTimeout(() => {
      navigate('/register');
    }, 4000);

    // Очистка таймеров при размонтировании компонента
    return () => {
      clearTimeout(textDisappearTimer);
      clearTimeout(redirectTimer);
    };
  }, [navigate]);

  return (
    <div className="welcome-container">
      <img src={logo} alt="Логотип" className="logo" />
      <h1 className={`welcome-text ${textVisible ? '' : 'disappear'}`}>Добро пожаловать!</h1>
    </div>
  );
};

export default WelcomePage;
