u0441\u044F\u043A\u0438\u0439 \u0441\u043B\u0443\u0447\u0430\u0439\" \u0434\u043B\u044F \u0441\u043E\u0442\u0440\u0443\u0434\u043D\u0438\u043A\u0430"}),/*#__PURE__*/_jsx("div",{className:"justincase-form",children:renderStep()}),currentStep>1&&/*#__PURE__*/_jsx("div",{className:"back-btn-container",children:/*#__PURE__*/_jsx("button",{className:"back-btn",onClick:handlePrev,children:/*#__PURE__*/_jsx("svg",{xmlns:"http://www.w3.org/2000/svg",viewBox:"0 0 24 24",fill:"none",children:/*#__PURE__*/_jsx("path",{d:"M12 20l-8-8 8-8"})})})}),/*#__PURE__*/_jsx("div",{className:"top-btn-container",children:/*#__PURE__*/_jsx("button",{className:"top-btn",onClick:handleNext,children:/*#__PURE__*/_jsx("svg",{xmlns:"http://www.w3.org/2000/svg",viewBox:"0 0 24 24",fill:"none",children:/*#__PURE__*/_jsx("path",{d:"M12 4l8 8-8 8"})})})})]});};export default JustincasePage;

