import React, { useState, useEffect } from 'react';
import mainmenuIcon from './components/icons/mainmenu.png';
import logoImage from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';
import './SNPPage.css';
import { useNavigate } from 'react-router-dom';

const SNPPage = () => {
  const navigate = useNavigate();

  const [birthDate, setBirthDate] = useState('');
  const [gender, setGender] = useState(null);
  const [calcType, setCalcType] = useState(null);
  const [amount, setAmount] = useState('');
  const [amountError, setAmountError] = useState('');
  const [currentStep, setCurrentStep] = useState(1);
  const [isProcessing, setIsProcessing] = useState(false);
  const [resultData, setResultData] = useState(null);

  const formatSum = (raw) => {
    let digits = raw.replace(/\D/g, '');
    if (!digits) return '';
    let out = '';
    while (digits.length > 3) {
      out = '.' + digits.slice(-3) + out;
      digits = digits.slice(0, -3);
    }
    return digits + out;
  };

  const handleAmountChange = (e) => {
    const formatted = formatSum(e.target.value);
    setAmount(formatted);

    const numeric = parseInt(formatted.replace(/\./g, '')) || 0;
    if (calcType === 'premium') {
      setAmountError(numeric < 60000 ? '–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –æ—Ç 60.000 —Ä—É–±–ª–µ–π' : '');
    } else if (calcType === 'sum') {
      setAmountError(numeric < 450000 ? '–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –æ—Ç 450.000 —Ä—É–±–ª–µ–π' : '');
    }
  };

  const isAmountValid = () => {
    const numeric = parseInt(amount.replace(/\./g, '')) || 0;
    return (calcType === 'premium' && numeric >= 60000) || (calcType === 'sum' && numeric >= 450000);
  };

  const getAge = (dateStr) => {
    if (!dateStr || typeof dateStr !== 'string') return 0;
    const [day, month, year] = dateStr.split('.').map(Number);
    const date = new Date(year, month - 1, day);
    const today = new Date();
    let age = today.getFullYear() - date.getFullYear();
    if (
      today.getMonth() < date.getMonth() ||
      (today.getMonth() === date.getMonth() && today.getDate() < date.getDate())
    ) {
      age--;
    }
    return age;
  };

  const canGoNext = () => {
    if (!birthDate || !gender || !calcType || !amount || !isAmountValid()) return false;

    const dateStr = (typeof birthDate === 'string') 
      ? birthDate 
      : birthDate instanceof Date 
        ? birthDate.toLocaleDateString('ru-RU').split('.').map((v, i) => i < 2 ? v.padStart(2, '0') : v).join('.') 
        : '';
    return getAge(dateStr) >= 18;

  };

  const handleNext = async () => {
    if (!canGoNext()) return;

    const dateStr = (typeof birthDate === 'string') 
      ? birthDate 
      : birthDate instanceof Date 
        ? birthDate.toLocaleDateString('ru-RU').split('.').map((v, i) => i < 2 ? v.padStart(2, '0') : v).join('.') 
        : '';
    const age = getAge(dateStr);

    const payload = {
      birthdate: dateStr,
      age,
      gender,
      payment: calcType === 'premium' ? '–°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞' : '–°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã',
      sum: parseInt(amount.replace(/\./g, '')) || 0
    };

    try {
      setIsProcessing(true);
      const res = await fetch('/api/snp/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const data = await res.json();
      setIsProcessing(false);

      if (!res.ok) {
        console.error('–û—à–∏–±–∫–∞ –æ—Ç —Å–µ—Ä–≤–µ—Ä–∞:', data);
        return;
      }

      setResultData(data);
      setCurrentStep(2);
    } catch (err) {
      setIsProcessing(false);
      console.error('–û—à–∏–±–∫–∞ –∑–∞–ø—Ä–æ—Å–∞:', err);
    }
  };

  const goToMenu = () => navigate('/Main-Menu');

  return (
    <div className="snp-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="logo-container">
        <img src={logoImage} alt="Logo" className="logo-image" />
      </div>

      <h1 className="snp-title">–†–∞—Å—á—ë—Ç –ø–æ –ø—Ä–æ–≥—Ä–∞–º–º–µ "–°—Ç—Ä–∞—Ç–µ–≥–∏—è –Ω–∞ –ø—è—Ç—å. –ì–∞—Ä–∞–Ω—Ç"</h1>

      {isProcessing ? (
        <div className="processing-container">
          <p>–í—ã–ø–æ–ª–Ω—è–µ—Ç—Å—è —Ä–∞—Å—á–µ—Ç...</p>
          <div className="arrow-animation">‚Üí</div>
        </div>
      ) : resultData ? (
        <div className="result-container">
          <h2>–†–µ–∑—É–ª—å—Ç–∞—Ç—ã —Ä–∞—Å—á—ë—Ç–∞</h2>
          <p><strong>–í–æ–∑—Ä–∞—Å—Ç:</strong> {resultData.age} –ª–µ—Ç</p>
          <p><strong>–ü–æ–ª:</strong> {resultData.gender}</p>
          <p><strong>–¢–∏–ø —Ä–∞—Å—á—ë—Ç–∞:</strong> {resultData.payment}</p>
          <p><strong>–°—É–º–º–∞:</strong> {formatSum(resultData.sum.toString())} —Ä—É–±.</p>
          <p><strong>–ò—Ç–æ–≥–æ–≤–∞—è –ø—Ä–µ–º–∏—è:</strong> {formatSum(resultData.totalPremium?.toString() || '0')} —Ä—É–±.</p>
        </div>
      ) : (
        <div className="snp-form">
          <div className="form-group">
            <label className="form-label">–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è:</label>
            <DateWheelPicker onChange={(val) => setBirthDate(val)} />
          </div>

