import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './MainMenu.css';
import logoImage from './components/logo.png';

export default function MainMenu() {
  const navigate = useNavigate();
  const [theme, setTheme] = useState('dark');
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    // –ó–∞–ø—É—Å—Ç–∏—Ç—å –∞–Ω–∏–º–∞—Ü–∏—é –ø–æ—è–≤–ª–µ–Ω–∏—è –ª–æ–≥–æ—Ç–∏–ø–∞ –∏ –∫–Ω–æ–ø–æ–∫
    setAnimate(true);
  }, []);

  const toggleTheme = () => {
    setTheme(t => (t === 'dark' ? 'light' : 'dark'));
  };

  // ripple effect: –¥–æ–±–∞–≤–ª—è–µ–º –∫—Ä—É–≥ –∏ –∂–¥—ë–º –ø–µ—Ä–µ–¥ –Ω–∞–≤–∏–≥–∞—Ü–∏–µ–π
  const handleClick = (e, to) => {
    const btn = e.currentTarget;
    const circle = document.createElement('span');
    const diameter = Math.max(btn.clientWidth, btn.clientHeight);
    const radius = diameter / 2;
    circle.style.width = circle.style.height = `${diameter}px`;
    circle.style.left  = `${e.clientX - btn.offsetLeft - radius}px`;
    circle.style.top   = `${e.clientY - btn.offsetTop  - radius}px`;
    circle.classList.add('ripple');
    const old = btn.getElementsByClassName('ripple')[0];
    if (old) old.remove();
    btn.appendChild(circle);

    // –ñ–¥—ë–º —á—É—Ç—å-—á—É—Ç—å, —á—Ç–æ–±—ã —É–≤–∏–¥–µ—Ç—å –∞–Ω–∏–º–∞—Ü–∏—é, –∏ —Ç–æ–ª—å–∫–æ –ø–æ—Ç–æ–º –ø–µ—Ä–µ—Ö–æ–¥–∏–º
    setTimeout(() => navigate(to), 300);
  };

  const buttons = [
    { to: '/surveys',  label: '–û–ø—Ä–æ—Å—ã'    },
    { to: '/profile',  label: '–ü—Ä–æ—Ñ–∏–ª—å'    },
    { to: '/employee', label: '–°–æ—Ç—Ä—É–¥–Ω–∏–∫—É' }
  ];

  return (
    <div className={`mainmenu-container ${theme}`}>
      <div className="mainmenu-overlay"/>

      <button className="theme-toggle" onClick={toggleTheme}>
        {theme === 'dark' ? 'üåû' : 'üåô'}
      </button>

      <img
        src={logoImage}
        alt="–õ–æ–≥–æ—Ç–∏–ø"
        className={`mainmenu-logo ${animate ? 'animate-logo' : ''}`}
      />

      <div className="mainmenu-buttons">
        {buttons.map((btn, i) => (
          <button
            key={btn.to}
            className={`mainmenu-btn ${animate ? `animate-btn btn${i+1}` : ''}`}
            onClick={e => handleClick(e, btn.to)}
          >
            {btn.label}
          </button>
        ))}
      </div>
    </div>
  );
}
