import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from './components/logo.png';
import backgroundImage from './components/background.jpg';
import shield from './components/icons/замок.png';
import guestIcon from './components/icons/гость.png';
import './RegisterPage.css';

const RegisterPage = () => {
  const [email, setEmail] = useState('');
  const [status, setStatus] = useState('');
  const navigate = useNavigate();
  const [offsetY, setOffsetY] = useState(0);

  const handleScroll = () => {
    setOffsetY(window.pageYOffset);
  };

  useEffect(() => {
    const setVh = () => {
      const vh = window.innerHeight * 0.01;
      document.documentElement.style.setProperty('--vh', `${vh}px`);
    };
    setVh();
    window.addEventListener('resize', setVh);
    return () => window.removeEventListener('resize', setVh);
  }, []);

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleFocus = () => {
    setTimeout(() => window.scrollTo(0, document.body.scrollHeight), 300);
  };

  const handleRegister = async () => {
    setStatus('Проверка email...');

    try {
      const response = await fetch('http://127.0.0.1:5000/check_email', {
        method: 'POST',
        body: JSON.stringify({ email }),
        headers: { 'Content-Type': 'application/json' },
      });

      const result = await response.json();

      if (result.isValid) {
        setStatus('Почта проверена, отправляем код...');
        await fetch('http://127.0.0.1:5000/send_code', {
          method: 'POST',
          body: JSON.stringify({ email }),
          headers: { 'Content-Type': 'application/json' },
        });
        navigate('/confirm-code', { state: { email } });
      } else {
        setStatus('Этот email не зарегистрирован в системе.');
      }
    } catch (error) {
      setStatus('Ошибка при проверке email. Попробуйте снова.');
      console.error(error);
      setTimeout(() => {
        setStatus('');
      }, 4000);
    }
  };

  const handleGuestLogin = () => {
    navigate('/polls');
  };

  return (
    <div className="container" style={{ backgroundImage: `url(${backgroundImage})`, backgroundPositionY: `${offsetY * 0.5}px` }}>
      <div className="logo">
        <img src={logo} alt="Logo" className="logo" />
      </div>
      <h2>Зарегистрируйтесь или войдите как гость</h2>
      <button className="full-width-btn" onClick={() => { handleFocus(); handleRegister(); }}>
        <div className="button-icon-container">
          <img src={shield} alt="Shield Icon" className="button-icon" />
        </div>
        <span className="button-text">Зарегистрироваться</span>
      </button>
      <button className="full-width-btn2" onClick={() => { handleFocus(); handleGuestLogin(); }}>
        <div className="button-icon-container">
          <img src={guestIcon} alt="Guest Icon" className="button-icon" />
        </div>
        <span className="button-text">Войти как гость</span>
      </button>
      <p>{status}</p>
    </div>
  );
};

export default RegisterPage;
