import React, { useState, useEffect } from 'react';
import Autosuggest from 'react-autosuggest';
import backgroundImage from './components/background.png'; // Фоновое изображение
import happyGif from './components/animations/laughing_2.png';
import loyalGif from './components/animations/smiling.png';
import sadGif from './components/animations/pokerface.png';
import { useSwipeable } from 'react-swipeable';

import './FeedbackPage.css';

// Импорт данных партнёров и спикеров
import partners from './components/autosuggest/partners.json';
import speakersData from './components/autosuggest/speakers.json';

const FeedbackPage = () => {
  // ... [ваши остальные состояния и функции] ...

  /********** Шаг 7: Общее впечатление от тренинга **********/
  const [impression, setImpression] = useState('');

  // --------- Новые состояния для шагов 8 и 9 ---------
  // Шаг 8: Настроение на мероприятии
  const [mood, setMood] = useState('');

  // Шаг 9: Готовность рекомендовать (NPS 0–10, например)
  const [recommendation, setRecommendation] = useState(null);

  /********** Логика шагов **********/
  // Теперь у нас 9 шагов (1–9), а финал будет на шаге 10
  // 1 – партнёр, 2 – спикеры, 3 – качества, 4 – полезность, 5 – яркие мысли,
  // 6 – доп. предложения, 7 – общее впечатление, 8 – моё настроение, 9 – готовность рекомендовать.
  // Шаг 10 = финальный экран.

  const [currentStep, setCurrentStep] = useState(1);
  const [isFinished, setIsFinished] = useState(false);

  // ... [getSpeakersFeedback(), canGoNext(), handleNext(), handlePrev() — всё как у вас, но обновим canGoNext] ...

  const canGoNext = () => {
    switch (currentStep) {
      case 1:
        return partner.trim() !== '' && partners.includes(partner);
      case 2:
        return speakersList.every(
          (s) => s.fullName.trim() !== '' && speakersData.includes(s.fullName)
        );
      case 3:
        return countSelectedQualities() >= 3;
      case 4:
        return (
          usefulness === 'Полезно в работе (буду применять на практике)' ||
          usefulness === 'Бесполезно в работе (с моими клиентами это не работает)'
        );
      case 5:
        return brightThoughts.trim().length >= 5;
      case 6:
        return additionalSelections.length > 0;
      case 7:
        return impression.trim() !== '';
      // Новые проверки:
      case 8:
        // mood должен быть выбран
        return mood.trim() !== '';
      case 9:
        // recommendation должен быть числом от 0 до 10 (или 1–10)
        return recommendation !== null;
      default:
        return true;
    }
  };

  // Обновим handleNext, чтобы шаг 9 шёл к шагу 10 (финал)
  const handleNext = () => {
    if (!canGoNext()) {
      alert('Не выполнены условия для перехода к следующему шагу');
      return;
    }
    if (currentStep === 3 && currentSpeakerIndex < speakersList.length - 1) {
      setCurrentSpeakerIndex(currentSpeakerIndex + 1);
      return;
    }
    setCurrentStep((prev) => prev + 1);
  };

  // ... handlePrev и т.д. остаются как есть ...

  const handleFinish = async () => {
    if (!canGoNext()) {
      alert('Не выполнены условия для завершения опроса');
      return;
    }
    setIsFinished(true);

    // Дополняем данные: mood, recommendation
    const data = {
      partner,
      dateTime: new Date().toISOString(),
      commonAnswers: {
        usefulness,
        brightThoughts,
        additionalSuggestions: additionalSelections.join(', '),
        impression,
        mood,            // добавили настроение
        recommendation,  // добавили оценку готовности рекомендовать
      },
      speakersFeedback: getSpeakersFeedback(),
    };

    try {
      const response = await fetch('https://rgszh-miniapp.org/api/proxy/feedback/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
        credentials: 'include',
      });
      if (!response.ok) {
        alert('Ошибка при сохранении данных');
      }
    } catch (error) {
      console.error('Ошибка при отправке данных:', error);
      alert('Ошибка соединения с сервером');
    }
  };

  const swipeHandlers = useSwipeable({
    onSwipedLeft: () => handlePrev(),
    onSwipedRight: () => {
      if (canGoNext()) handleNext();
    },
    preventDefault: true,
  });

  const renderStep = () => {
    switch (currentStep) {
      // ... шаги 1–7 без изменений ...

      // --- Шаг 8: Мое настроение ---
      case 8:
        return (
          <div>
            <h2>Мое настроение на мероприятии</h2>
            <div className="mood-container">
              {/* Пример 5 смайлов: грустно, устало, весело, задумчиво, кринжово */}
              <button
                className={`mood-btn ${mood === 'sad' ? 'mood-selected' : ''}`}
                onClick={() => setMood('sad')}
              >
                😢
              </button>
              <button
                className={`mood-btn ${mood === 'tired' ? 'mood-selected' : ''}`}
                onClick={() => setMood('tired')}
              >
                😩
              </button>
              <button
                className={`mood-btn ${mood === 'fun' ? 'mood-selected' : ''}`}
                onClick={() => setMood('fun')}
              >
                😄
              </button>
              <button
                className={`mood-btn ${mood === 'thoughtful' ? 'mood-selected' : ''}`}
                onClick={() => setMood('thoughtful')}
              >
                🤔
              </button>
              <button
                className={`mood-btn ${mood === 'cringe' ? 'mood-selected' : ''}`}
                onClick={() => setMood('cringe')}
              >
                😬
              </button>
            </div>
          </div>
        );

      // --- Шаг 9: Готовность рекомендовать ---
      case 9:
        return (
          <div>
            <h2>Насколько готовы рекомендовать тренинг?</h2>
            <p>Оцените по шкале от 0 до 10</p>
            <div className="recommendation-container">
              {/* Набор кнопок от 0 до 10 (можно сделать 1..10) */}
              {[...Array(11)].map((_, i) => (
                <button
                  key={i}
                  className={`recommendation-btn ${recommendation === i ? 'recommendation-selected' : ''}`}
                  onClick={() => setRecommendation(i)}
                >
                  {i}
                </button>
              ))}
            </div>
          </div>
        );

      // --- Шаг 10: финал ---
      default:
        return null;
    }
  };

  if (isFinished) {
    // ФИНАЛ: шаг 10
    return (
      <div className="thank-you-message" style={{ backgroundImage: `url(${backgroundImage})` }}>
        <h2>Спасибо за прохождение опроса!</h2>
      </div>
    );
  }

  return (
    <div className="feedback-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="content-wrapper" {...swipeHandlers}>
        {renderStep()}
        <div className="top-btn-container">
          {currentStep < 9 ? (
            // если шаг < 9, показываем кнопку "далее"
            <button className="top-btn" onClick={handleNext} disabled={!canGoNext()}>
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none">
                <path d="M12 4l8 8-8 8" />
              </svg>
            </button>
          ) : (
            // на шаге 9 — кнопка "Завершить"
            <button className="top-btn" onClick={handleFinish} disabled={!canGoNext()}>
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none">
                <path d="M5 13l4 4L19 7" />
              </svg>
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default FeedbackPage;
