// src/MainMenu.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import backgroundImage from './components/background.png';
import logoImage       from './components/logo.png';
import './MainMenu.css';

export default function MainMenu() {
  const navigate = useNavigate();
  const [theme, setTheme]     = useState('dark');
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    // –ó–∞–ø—É—Å–∫–∞–µ–º –∞–Ω–∏–º–∞—Ü–∏—é –ø–æ—è–≤–ª–µ–Ω–∏—è
    setAnimate(true);
  }, []);

  // –ü–µ—Ä–µ–∫–ª—é—á–∞—Ç–µ–ª—å —Ç–µ–º—ã
  const toggleTheme = () => {
    setTheme(t => (t === 'dark' ? 'light' : 'dark'));
  };

  // –û–±—ë—Ä—Ç–∫–∞ —Å ripple-—ç—Ñ—Ñ–µ–∫—Ç–æ–º –∏ –∑–∞–¥–µ—Ä–∂–∫–æ–π –ø–µ—Ä–µ–¥ –Ω–∞–≤–∏–≥–∞—Ü–∏–µ–π
  const handleClick = (e, route) => {
    const btn = e.currentTarget;
    const circle = document.createElement('span');
    const diameter = Math.max(btn.clientWidth, btn.clientHeight);
    const radius = diameter / 2;
    circle.style.width  = circle.style.height = `${diameter}px`;
    circle.style.left   = `${e.clientX - btn.offsetLeft  - radius}px`;
    circle.style.top    = `${e.clientY - btn.offsetTop   - radius}px`;
    circle.classList.add('ripple');
    const old = btn.getElementsByClassName('ripple')[0];
    if (old) old.remove();
    btn.appendChild(circle);

    setTimeout(() => navigate(route), 300);
  };

  // –û—Å—Ç–∞–ª–∏—Å—å —Ç–æ–ª—å–∫–æ –¥–≤–µ –∫–Ω–æ–ø–∫–∏: ¬´–û–ø—Ä–æ—Å—ã¬ª –∏ ¬´–°–æ—Ç—Ä—É–¥–Ω–∏–∫—É¬ª
  const buttons = [
    { to: '/polls',    label: '–û–ø—Ä–æ—Å—ã'    },
    { to: '/employee', label: '–°–æ—Ç—Ä—É–¥–Ω–∏–∫—É' },
  ];

  return (
    <div
      className={`mainmenu-container ${theme}`}
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      <div className="mainmenu-overlay" />

      <button className="theme-toggle" onClick={toggleTheme}>
        {theme === 'dark' ? 'üåû' : 'üåô'}
      </button>

      <div className={`logo-wrapper ${animate ? 'animate-logo' : ''}`}>
        <img src={logoImage} alt="–õ–æ–≥–æ—Ç–∏–ø –†–ì–° –ñ–∏–∑–Ω—å" className="mainmenu-logo" />
      </div>

      <div className="mainmenu-buttons">
        {buttons.map((btn, idx) => (
          <button
            key={btn.to}
            className={`mainmenu-btn ${animate ? `animate-btn btn${idx+1}` : ''}`}
            onClick={e => handleClick(e, btn.to)}
          >
            {btn.label}
          </button>
        ))}
      </div>
    </div>
  );
}
