// src/WelcomePage.js
import React, { useEffect, useState } from 'react';
import { useNavigate }               from 'react-router-dom';
import { useSwipeable }              from 'react-swipeable';

import './Styles/global.css';      // Montserrat + сбросы
import './Styles/background.css';  // градиент + шум + subtle-dot
import './Styles/logo.css';        // стили/анимация логотипа
import './WelcomePage.css';        // стили конкретно для WelcomePage

import backgroundImage from './components/background.png';
import logoImage       from './components/logo.png';

const WelcomePage = () => {
  const navigate = useNavigate();
  const [animateLogo, setAnimateLogo] = useState(false);
  const [animateText, setAnimateText] = useState(false);
  const [greeting, setGreeting] = useState('');

  useEffect(() => {
    // Выбираем приветствие по времени суток
    const hour = new Date().getHours();
    let text = '';
    if (hour >= 6 && hour < 11) text = 'Доброе утро';
    else if (hour >= 11 && hour < 17) text = 'Добрый день';
    else if (hour >= 17 && hour < 24) text = 'Добрый вечер';
    else text = 'Доброй ночи';
    setGreeting(text);

    // Запускаем анимации логотипа и текста
    setTimeout(() => setAnimateLogo(true), 100);
    setTimeout(() => setAnimateText(true), 500);

    // Переход на главное меню через 3 секунды
    const timer = setTimeout(() => navigate('/main-menu'), 3000);
    return () => clearTimeout(timer);
  }, [navigate]);

  const swipeHandlers = useSwipeable({ preventDefault: true });

  return (
    <div
      className="welcome-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
      {...swipeHandlers}
    >
      {/* 10 подвижных полупрозрачных шариков (subtle-dot) */}
      <div className="subtle-dot dot-1" />
      <div className="subtle-dot dot-2" />
      <div className="subtle-dot dot-3" />
      <div className="subtle-dot dot-4" />
      <div className="subtle-dot dot-5" />
      <div className="subtle-dot dot-6" />
      <div className="subtle-dot dot-7" />
      <div className="subtle-dot dot-8" />
      <div className="subtle-dot dot-9" />
      <div className="subtle-dot dot-10" />

      {/* Полупрозрачный оверлей (если нужен) */}
      <div className="mainmenu-overlay" />

      {/* Анимированный логотип */}
      <div className={`logo-wrapper ${animateLogo ? 'animate-logo' : ''}`}>
        <img
          src={logoImage}
          alt="Логотип РГС Жизнь"
          className="logo-image"
        />
      </div>

      {/* Анимированное приветствие снизу (спустя 500 мс) */}
      <div className={`text-wrapper ${animateText ? 'animate-text' : ''}`}>
        <h1 className="welcome-title">{greeting}</h1>
      </div>
    </div>
  );
};

export default WelcomePage;
