// src/SNPPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import mainmenuIcon from './components/icons/mainmenu.png';
import logoImage     from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';

import './PollsPage.css';  // стили шапки и кнопок
import './SNPPage.css';    // ваши дополнительные стили

const SNPPage = () => {
  const navigate = useNavigate();

  // Шаги и состояния
  const [step, setStep] = useState(1);
  const [email, setEmail] = useState('');
  const [emailError, setEmailError] = useState('');
  const [birthDate, setBirthDate] = useState(null);
  const [gender, setGender] = useState(null);
  const [calcType, setCalcType] = useState(null);
  const [amount, setAmount] = useState('');
  const [amountError, setAmountError] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [resultData, setResultData] = useState(null);

  // Формат суммы «xxx.xxx»
  const formatSum = (raw) => {
    let digits = raw.replace(/\D/g, '');
    if (!digits) return '';
    let out = '';
    while (digits.length > 3) {
      out = '.' + digits.slice(-3) + out;
      digits = digits.slice(0, -3);
    }
    return digits + out;
  };

  // Возраст по дате
  const getAge = (date) => {
    if (!(date instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - date.getFullYear();
    if (
      today.getMonth() < date.getMonth() ||
      (today.getMonth() === date.getMonth() && today.getDate() < date.getDate())
    ) {
      age--;
    }
    return age;
  };

  // Проверка минимальной суммы
  const isAmountValid = () => {
    const numeric = parseInt(amount.replace(/\./g, ''), 10) || 0;
    return (
      (calcType === 'premium' && numeric >= 60000) ||
      (calcType === 'sum'     && numeric >= 450000)
    );
  };

  // Можно ли переходить дальше (шаг 2 → расчёт)
  const canGoNext = () => {
    return (
      birthDate instanceof Date &&
      getAge(birthDate) >= 18 &&
      gender &&
      calcType &&
      amount &&
      isAmountValid()
    );
  };

  // Шаг 1: проверка почты
  const handleEmailNext = () => {
    if (!email.toLowerCase().endsWith('@vtb.ru')) {
      setEmailError('Используйте корпоративную почту домена @vtb.ru');
      return;
    }
    setEmailError('');
    setStep(2);
  };

  // Обработчик ввода суммы
  const handleAmountChange = (e) => {
    const formatted = formatSum(e.target.value);
    setAmount(formatted);

    const numeric = parseInt(formatted.replace(/\./g, ''), 10) || 0;
    if (calcType === 'premium' && numeric < 60000) {
      setAmountError('Минимум 60 000 ₽');
    } else if (calcType === 'sum' && numeric < 450000) {
      setAmountError('Минимум 450 000 ₽');
    } else {
      setAmountError('');
    }
  };

  // Шаг 2 → финальный запрос
  const handleNext = async () => {
    if (!canGoNext()) return;

    const dateStr = birthDate.toLocaleDateString('ru-RU', {
      day:   '2-digit',
      month: '2-digit',
      year:  'numeric'
    });

    const payload = {
      email,
      birthdate: dateStr,
      age:        getAge(birthDate),
      gender,
      payment:    calcType === 'premium'
                    ? 'Страхового взноса'
                    : 'Страховой суммы',
      sum:        parseInt(amount.replace(/\./g, ''), 10)
    };

    try {
      setIsProcessing(true);
      const res = await fetch('/api/proxy/snp/save', {
        method:  'POST',
        headers: { 'Content-Type': 'application/json' },
        body:    JSON.stringify(payload)
      });
      const data = await res.json();
      setIsProcessing(false);

      if (res.ok)      setResultData(data);
      else             console.error('Ошибка сервера:', data);
    } catch (err) {
      setIsProcessing(false);
      console.error('Ошибка запроса:', err);
    }
  };

  const goHome = () => navigate('/MainMenu');

  return (
    <div
      className="snp-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* HEADER по PollsPage */}
      <div className="polls-header">
        <button className="back-btn" onClick={goHome}>
          <img src={mainmenuIcon} alt="Домой" />
        </button>
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип" />
        </div>
      </div>

      {/* === Шаг 1: ввод почты === */}
      {step === 1 && !isProcessing && !resultData && (
        <>
          <div className="email-step">
            <input
              type="email"
              placeholder="example@vtb.ru"
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
          </div>
          {emailError && <div className="error-text">{emailError}</div>}

          <div className="polls-buttons">
            <button
              className="polls-btn animate-btn btn1"
              onClick={handleEmailNext}
            >
              Продолжить
            </button>
          </div>
        </>
      )}

      {/* === Шаг 2: формула расчёта === */}
      {step === 2 && !isProcessing && !resultData && (
        <>
          <h1 className="polls-title animate-title">
            Расчёт по программе «Стратегия на пять. Гарант»
          </h1>

          {/* Дата рождения */}
          <div className="form-step data-step">
            <label>Дата рождения:</label>
            <DateWheelPicker
              onChange={val => {
                if (val instanceof Date)          setBirthDate(val);
                else if (val && val.day) {
                  setBirthDate(new Date(
                    val.year, val.month - 1, val.day
                  ));
                }
              }}
            />
          </div>

          {/* Пол */}
          <div className="gender-selector">
            <button
              className={`insurance-btn ${gender === 'male'   ? 'selected' : ''}`}
              onClick={() => setGender('male')}
            >
              Мужской
            </button>
            <button
              className={`insurance-btn ${gender === 'female' ? 'selected' : ''}`}
              onClick={() => setGender('female')}
            >
              Женский
            </button>
          </div>

          {/* Тип расчёта */}
          <div className="insurance-options">
            <button
              className={`insurance-btn ${calcType === 'premium' ? 'selected' : ''}`}
              onClick={() => {
                setCalcType('premium');
                setAmount('');
                setAmountError('');
              }}
            >
              Страхового взноса
            </button>
            <button
              className={`insurance-btn ${calcType === 'sum' ? 'selected' : ''}`}
              onClick={() => {
                setCalcType('sum');
                setAmount('');
                setAmountError('');
              }}
            >
              Страховой суммы
            </button>
          </div>

          {/* Ввод суммы */}
          {calcType && (
            <div className="form-step">
              <label>Сумма (руб.)</label>
              <input
                type="text"
                placeholder={calcType === 'premium'
                  ? 'Минимум 60 000' : 'Минимум 450 000'}
                value={amount}
                onChange={handleAmountChange}
              />
              {amountError && <div className="error-text">{amountError}</div>}
            </div>
          )}

          <div className="polls-buttons">
            <button
              className="polls-btn animate-btn btn2"
              onClick={handleNext}
              disabled={!canGoNext()}
            >
              Перейти к расчёту
            </button>
          </div>
        </>
      )}

      {/* Индикатор обработки */}
      {isProcessing && (
        <div className="processing-container">
          <p>Выполняется расчёт…</p>
          <div className="arrow-animation">→</div>
        </div>
      )}

      {/* Вывод результата */}
      {resultData && (
        <div className="result-container">
          <h2>Результаты расчёта</h2>
          <p><strong>Возраст:</strong> {resultData.age} лет</p>
          <p><strong>Пол:</strong> {resultData.gender}</p>
          <p><strong>Тип расчёта:</strong> {resultData.payment}</p>
          <p><strong>Сумма:</strong> {formatSum(resultData.sum.toString())} ₽</p>
          <p><strong>Премия:</strong> {formatSum((resultData.totalPremium||0).toString())} ₽</p>
        </div>
      )}
    </div>
  );
};

export default SNPPage;
