import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import mainmenuIcon    from './components/icons/mainmenu.png';
import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';
import './SNPPage.css';

export default function SNPPage() {
  const navigate = useNavigate();

  const [step, setStep] = useState('email');
  const [email, setEmail] = useState('');
  const [emailError, setEmailError] = useState('');

  const [birthDateParts, setBirthDateParts] = useState({ day: null, month: null, year: null });
  const [birthDate,      setBirthDate]      = useState(null);
  const [gender,         setGender]         = useState(null);
  const [calcType,       setCalcType]       = useState(null);
  const [amount,         setAmount]         = useState('');
  const [amountError,    setAmountError]    = useState('');
  const [isProcessing,   setIsProcessing]   = useState(false);
  const [resultData,     setResultData]     = useState(null);
  const [slideIndex,     setSlideIndex]     = useState(0);

  const [mgrSurname, setMgrSurname] = useState('');
  const [mgrName,    setMgrName]    = useState('');
  const [mgrCity,    setMgrCity]    = useState('');
  const [mgrError,   setMgrError]   = useState('');

  const term        = 5;
  const periodicity = 'ежегодно';
  const currency    = 'рублей';

  useEffect(() => {
    const { day, month, year } = birthDateParts;
    if (day && month && year) {
      const d = Number(day), m = Number(month), y = Number(year);
      const dt = new Date(y, m - 1, d);
      if (!isNaN(dt) && dt.getDate()===d && dt.getMonth()+1===m && dt.getFullYear()===y) {
        setBirthDate(dt);
      } else {
        setBirthDate(null);
      }
    } else {
      setBirthDate(null);
    }
  }, [birthDateParts]);

  const formatSum = raw => {
    let digits = raw.replace(/\D/g, '');
    if (!digits) return '';
    let out = '';
    while (digits.length > 3) {
      out = '.' + digits.slice(-3) + out;
      digits = digits.slice(0, -3);
    }
    return digits + out;
  };

  const handleAmountChange = e => {
    const fmt = formatSum(e.target.value);
    setAmount(fmt);
    const num = parseInt(fmt.replace(/\./g, ''), 10) || 0;
    if (calcType==='premium') {
      setAmountError(num<60000 ? 'Введите сумму от 60 000 ₽' : '');
    } else if (calcType==='sum') {
      setAmountError(num<450000 ? 'Введите сумму от 450 000 ₽' : '');
    }
  };

  const getAge = d => {
    if (!(d instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - d.getFullYear();
    const m = today.getMonth() - d.getMonth();
    if (m<0 || (m===0 && today.getDate()<d.getDate())) age--;
    return age;
  };

  const canGoNext = () =>
    birthDate instanceof Date &&
    !isNaN(birthDate.getTime()) &&
    gender &&
    calcType &&
    amount &&
    !amountError &&
    getAge(birthDate) >= 18;

  const handleEmailNext = () => {
    if (!email.includes('@vtb.ru')) {
      setEmailError('Домен вашей почты не является корпоративным. Попробуйте ещё раз.');
    } else {
      setEmailError('');
      setStep('form');
    }
  };

  const handleNext = async () => {
    if (!canGoNext()) return;
    const dateRu = birthDate.toLocaleDateString('ru-RU', {
      day:'2-digit', month:'2-digit', year:'numeric'
    });
    const age = getAge(birthDate);
    const payload = {
      birthdate: dateRu,
      age,
      gender,
      payment: calcType==='premium' ? 'Страхового взноса' : 'Страховой суммы',
      sum: parseInt(amount.replace(/\./g,''),10)
    };
    setIsProcessing(true);
    try {
      const res = await fetch('/api/snp/save',{
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body: JSON.stringify(payload)
      });
      const data = await res.json();
      if (res.ok) {
        setResultData(data);
        setSlideIndex(0);
        setStep('result');
      }
    } catch(e){
      console.error(e);
    }
    setIsProcessing(false);
  };

  const todayStr = new Date().toLocaleDateString('ru-RU',{
    day:'2-digit', month:'2-digit', year:'numeric'
  });

  const slides = resultData ? [
    <div key={0} className="detail-slide">
      <div className="slide-header">В ТЕЧЕНИЕ {term} ЛЕТ</div>
      <div className="slide-title">Вы вносите средства в программу</div>
      <div className="slide-amount">{formatSum(String(resultData.contribution))} ₽</div>
      <div className="slide-period">{periodicity}</div>
      <div className="slide-subtitle">Сумма ваших взносов</div>
      <div className="slide-amount">{formatSum(String(resultData.totalContribution))} ₽</div>
    </div>,
    <div key={1} className="detail-slide">
      <div className="slide-header">НАЧИСЛЯЕМ ДОПОЛНИТЕЛЬНО</div>
      <div className="slide-title">{`${Math.round(parseFloat(resultData.accrualPercent)*100)}% от каждого взноса`}</div>
      <div className="slide-amount">{formatSum(String(resultData.accrualAmount))} ₽</div>
      <div className="slide-period">{periodicity}</div>
      <div className="slide-subtitle">Общая сумма начислений</div>
      <div className="slide-amount">{formatSum(String(resultData.totalAccrual))} ₽</div>
    </div>,
    <div key={2} className="detail-slide">
      {/* Вот тут вернулся старый вариант */}
      <div className="slide-header">ВЫ ПОЛУЧАЕТЕ:</div>
      <div className="slide-icon">💰</div>
      <div className="slide-amount-big">{formatSum(String(resultData.finalAnnual))} ₽</div>
      <div className="slide-period">Ежегодные накопления</div>
      <div className="slide-amount">{formatSum(String(resultData.finalTotal))} ₽</div>
      <div className="slide-subtitle">Ваш капитал через {term} лет</div>
    </div>
  ] : [];

  const handleManagerSubmit = () => {
    if (!mgrSurname||!mgrName||!mgrCity) {
      setMgrError('Пожалуйста, заполните все поля.');
    } else {
      console.log({email,mgrSurname,mgrName,mgrCity});
      navigate('/Main-Menu');
    }
  };

  return (
    <div className="snp-container" style={{backgroundImage:`url(${backgroundImage})`}}>

      <div className="back-btn-container">
        <button className="back-btn" onClick={()=>navigate('/Main-Menu')}>
          <img src={mainmenuIcon} alt="Домой"/>
        </button>
      </div>

      <div className="logo-container">
        <img src={logoImage} alt="Logo" className="logo-image"/>
      </div>

      <h1 className="snp-title">Расчёт по программе "Стратегия на пять. Гарант"</h1>

      {step==='email' && (
        <div className="snp-form">
          <div className="form-group">
            <label className="form-label">Корпоративная почта ВТБ:</label>
            <input
              type="email"
              className="insurance-sum-input"
              value={email}
              placeholder="name@vtb.ru"
              onChange={e=>setEmail(e.target.value)}
            />
          </div>
          <button className="insurance-btn selected" onClick={handleEmailNext}>
            Перейти к расчёту
          </button>
          {emailError && <div className="email-error-message">{emailError}</div>}
        </div>
      )}

      {step==='form' && !isProcessing && !resultData && (
        <>
          <div className="snp-form">
            <div className="form-group">
              <label className="form-label">Дата рождения:</label>
              <DateWheelPicker
                value={birthDateParts}
                onChange={val=>{/*...ваш обработчик...*/}}
              />
            </div>
            <div className="form-group">
              <label className="form-label">Пол:</label>
              <div className="gender-selector">
                {['мужской','женский'].map(v=>(
                  <div
                    key={v}
                    className={`gender-option ${gender===v?'selected':''}`}
                    onClick={()=>setGender(v)}
                  >
                    <div className="gender-label">{v==='мужской'?'Мужской':'Женский'}</div>
                    <div className="gender-icon">{v==='мужской'?'👨':'👩'}</div>
                  </div>
                ))}
              </div>
            </div>
            <div className="form-group">
              <label className="form-label">Расчёт от…</label>
              <div className="insurance-options">
                <button
                  className={`insurance-btn ${calcType==='premium'?'selected':''}`}
                  onClick={()=>{setCalcType('premium');setAmount('');setAmountError('');}}
                >Страхового взноса</button>
                <button
                  className={`insurance-btn ${calcType==='sum'?'selected':''}`}
                  onClick={()=>{setCalcType('sum');setAmount('');setAmountError('');}}
                >Страховой суммы</button>
              </div>
            </div>
            {calcType && (
              <div className="form-group">
                <label className="form-label">Введите сумму в рублях</label>
                <input
                  type="text"
                  className="insurance-sum-input"
                  value={amount}
                  onChange={handleAmountChange}
                  placeholder={calcType==='premium'?'Минимум 60 000':'Минимум 450 000'}
                />
                {amountError && <div className="input-error-message">{amountError}</div>}
              </div>
            )}
          </div>
          <div className="top-btn-container">
            <button
              className="top-btn"
              onClick={handleNext}
              disabled={!canGoNext()}
            >
              <svg viewBox="0 0 24 24"><path d="M12 4l8 8-8 8"/></svg>
            </button>
          </div>
        </>
      )}

      {isProcessing && (
        <div className="processing-container">
          <p>Выполняется расчёт...</p>
          <div className="arrow-animation">→</div>
        </div>
      )}

      {step==='result' && !isProcessing && resultData && (
        <>
          <div className="result-container">
            <h2>Результаты расчёта</h2>
            <p><strong>Дата расчёта:</strong> {todayStr}</p>
            <p><strong>Пол:</strong> {resultData.gender}</p>
            <p><strong>Возраст застрахованного:</strong> {resultData.age} лет</p>
            <p><strong>Срок страхования:</strong> {term} лет</p>
            <p><strong>Периодичность взносов:</strong> {periodicity}</p>
            <p><strong>Валюта:</strong> {currency}</p>
          </div>

          <div className="detail-slider-container">
            <div className="slider-indicators">
              {[0,1,2].map(i=>(
                <div key={i} className={`slider-dot ${i===slideIndex?'active':''}`}/>
              ))}
            </div>
            {slides[slideIndex]}
            <div className="detail-nav">
              {slideIndex>0 && (
                <button className="nav-btn left" onClick={()=>setSlideIndex(si=>si-1)}>←</button>
              )}
              {slideIndex<slides.length-1 && (
                <button className="nav-btn right" onClick={()=>setSlideIndex(si=>si+1)}>→</button>
              )}
            </div>
          </div>

          <div className="snp-form">
            <button className="insurance-btn selected" onClick={()=>setStep('manager')}>
              Связаться с менеджером
            </button>
          </div>
        </>
      )}

      {step==='manager' && (
        <div className="snp-form">
          <div className="form-group">
            <label className="form-label">Фамилия:</label>
            <input
              type="text"
              className="insurance-sum-input"
              value={mgrSurname}
              onChange={e=>setMgrSurname(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label className="form-label">Имя:</label>
            <input
              type="text"
              className="insurance-sum-input"
              value={mgrName}
              onChange={e=>setMgrName(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label className="form-label">Город работы:</label>
            <input
              type="text"
              className="insurance-sum-input"
              value={mgrCity}
              onChange={e=>setMgrCity(e.target.value)}
            />
          </div>
          {mgrError && <div className="input-error-message">{mgrError}</div>}
          <button className="insurance-btn selected" onClick={handleManagerSubmit}>
            Отправить менеджеру
          </button>
        </div>
      )}
    </div>
  );
}
